module.exports = {
  root: true,
  extends: ["@webpack-contrib/eslint-config-webpack", "prettier"],
  overrides: [
    {
      files: "src/runtime/**/*",
      env: {
        browser: true,
      },
      rules: {
        "prefer-destructuring": "off",
        "no-param-reassign": "off",
        "no-continue": "off",
        "no-underscore-dangle": "off",
        "no-undefined": "off",
      },
    },
  ],
};

const MIN_BABEL_VERSION = 7;

module.exports = (api) => {
  api.assertVersion(MIN_BABEL_VERSION);
  api.cache(true);

  return {
    presets: [
      [
        "@babel/preset-env",
        {
          targets: {
            node: "18.12.0",
          },
        },
      ],
    ],
    overrides: [
      {
        test: "./src/runtime",
        presets: [
          [
            "@babel/preset-env",
            {
              targets: {
                node: "0.12",
              },
            },
          ],
        ],
      },
    ],
  };
};

