		if (button && button.textContent.includes('更多')) {
			await button.click();
		}
	}
	const labels = new Labels(doc, '[class*="summary"] > p, .summaryRight, .break > .pdfN, [class*="summary"] li.pdfN, .itembox .item');

	/* 因为两种抓取方式都需要补充extra,所以提前add */
