				newItem.university = labels.get('网络出版投稿人') || pubInfo.split(/，\s?/)[0];
				newItem.date = tryMatch(pubInfo, /\d{4}/) || ZU.strToISO(labels.get('Year'));
				newItem.ISSN = labels.get('ISSN');
				labels.get('导师', true).querySelectorAll('a').forEach((creator) => {
					creator = ZU.cleanAuthor(ZU.trimInternal(creator.textContent), 'contributor');
					if (/[\u4e00-\u9fff]/.test(creator.lastName)) {
						creator.lastName = creator.firstName + creator.lastName;
						creator.firstName = '';
						creator.fieldMode = 1;
					}
					newItem.creators.push(creator);
				});
				break;
			}
			case 'conferencePaper':
				newItem.date = labels.get('会议时间');
				newItem.proceedingsTitle = labels.get('会议录名称');
				newItem.conferenceName = labels.get('会议名称');
				newItem.place = labels.get('会议地点');
				break;
		}
		newItem.DOI = labels.get('DOI');
		newItem.url = url;
		procesAttachment(newItem, doc);
		newItem.extra = extra.toString();
		newItem.complete();
	}
}
class Labels {
	constructor(doc, selector) {
		this.data = [];
		this.emptyElm = doc.createElement('div');
		Array.from(doc.querySelectorAll(selector))
			// avoid nesting
			.filter(element => !element.querySelector(selector))
			// avoid empty
			.filter(element => !/^\s*$/.test(element.textContent))
			.forEach((element) => {
				const elmCopy = element.cloneNode(true);
				// avoid empty text
				while (/^\s*$/.test(elmCopy.firstChild.textContent)) {
					// Z.debug(elementCopy.firstChild.textContent);
					elmCopy.removeChild(elmCopy.firstChild);
					// Z.debug(elementCopy.firstChild.textContent);
				}
				if (elmCopy.childNodes.length > 1) {
					const key = elmCopy.removeChild(elmCopy.firstChild).textContent.replace(/\s/g, '');
					this.data.push([key, elmCopy]);
				}
				else {
					const text = ZU.trimInternal(elmCopy.textContent);
					const key = tryMatch(text, /^[[【]?.+?[】\]:：]/).replace(/\s/g, '');
					elmCopy.textContent = tryMatch(text, /^[[【]?.+?[】\]:：]\s*(.+)/, 1);
					this.data.push([key, elmCopy]);
				}
			});
	}

	get(label, element = false) {
		if (Array.isArray(label)) {
			const results = label
				.map(aLabel => this.get(aLabel, element));
			const keyVal = element
				? results.find(element => !/^\s*$/.test(element.textContent))
				: results.find(string => string);
			return keyVal
				? keyVal
				: element
					? this.emptyElm
					: '';
		}
		const pattern = new RegExp(label, 'i');
		const keyVal = this.data.find(arr => pattern.test(arr[0]));
		return keyVal
			? element
				? keyVal[1]
				: ZU.trimInternal(keyVal[1].textContent)
			: element
				? this.emptyElm
				: '';
	}
}

/**
 * Attempts to get the part of the pattern described from the character,
 * and returns an empty string if not match.
 * @param {String} string
 * @param {RegExp} pattern
 * @param {Number} index
 * @returns
 */
function tryMatch(string, pattern, index = 0) {
	if (!string) return '';
	let match = string.match(pattern);
	return (match && match[index])
		? match[index]
		: '';
}

const extra = {
	clsFields: [],
	elseFields: [],
	add: function (key, value, cls = false) {
		if (value && cls) {
			this.clsFields.push([key, value]);
		}
		else if (value) {
			this.elseFields.push([key, value]);
		}
	},
	toString: function (original) {
		return original
			? [
				...this.clsFields.map(entry => `${entry[0]}: ${entry[1]}`),
				original.replace(/^\n|\n$/g, ''),
				...this.elseFields.map(entry => `${entry[0]}: ${entry[1]}`)
			].join('\n')
			: [...this.clsFields, ...this.elseFields]
				.map(entry => `${entry[0]}: ${entry[1]}`)
				.join('\n');
	}
};

function procesAttachment(item, doc) {
	let cajLink = doc.querySelector('.cajDNew > a');
	let pdfLink = doc.querySelector('.pdfD> a');
	if (pdfLink) {
		item.attachments.push({
			url: pdfLink.href,
			title: 'Full Text PDF',
			mimeType: 'application/pdf'
		});
	}
	else if (cajLink) {
		item.attachments.push({
			url: pdfLink.href,
			title: 'Full Text CAJ',
			mimeType: 'application/caj'
		});
	}
}

