function detectWeb(doc, url) {
	if (url.includes('/ecph/words?')) {
		return 'encyclopediaArticle';
	}
	else if (getSearchResults(doc, true)) {
		return 'multiple';
	}
	return false;
}

function getSearchResults(doc, checkOnly) {
	var items = {};
	var found = false;
	// .name见于首页推荐
	// h2见于搜索页
	var rows = doc.querySelectorAll('a[href*="/ecph/words"].name, h2 > a[href*="/ecph/words"]');
	for (let row of rows) {
		let href = row.href;
		let title = ZU.trimInternal(row.textContent);
		if (!href || !title) continue;
		if (checkOnly) return true;
		found = true;
		items[href] = title;
	}
	return found ? items : false;
}

async function doWeb(doc, url) {
	if (detectWeb(doc, url) == 'multiple') {
		let items = await Zotero.selectItems(getSearchResults(doc, false));
		if (!items) return;
		for (let url of Object.keys(items)) {
			await scrape(await requestDocument(url));
		}
	}
	else {
		await scrape(doc, url);
	}
}

async function scrape(doc, url = doc.location.href) {
	var newItem = new Zotero.Item("encyclopediaArticle");
	newItem.extra = '';
	newItem.title = text(doc, '.title >  h2');
	newItem.abstractNote = text(doc, '.summary');
	newItem.encyclopediaTitle = '中国大百科全书';
	newItem.edition = '第三版·网络版';
	newItem.publisher = '中国大百科全书出版社';
	newItem.date = text(doc, '.time > span');
	newItem.url = url;
	newItem.url = url;
	newItem.libraryCatalog = '中国大百科全书';
	newItem.extra += addExtra('original-title', text(doc, '.enname').replace(/\//g, ''));
	// ".authorname .n-author > span"见于https://www.zgbk.com/ecph/words?SiteID=1&ID=456852&Type=bkzyb&SubID=99947
	doc.querySelectorAll('.author-noshadow .author-span > span, .authorname .n-author > span').forEach((element) => {
		let creator = element.innerText.replace(/(撰|修订)$/, '');
		creator = ZU.cleanAuthor(creator, 'author');
		if (/[\u4e00-\u9fa5]/.test(creator.lastName)) {
			creator.lastName = creator.firstName + creator.lastName;
			creator.firstName = '';
			creator.fieldMode = 1;
		}
		newItem.creators.push(creator);
	});
	newItem.language = 'zh-CN';
	newItem.attachments.push({
		title: 'Snapshot',
		document: doc
	});
	newItem.complete();
}

function addExtra(key, value) {
	return value
		? `${key}: ${value}\n`
		: '';
}

