function detectWeb(doc, _url) {
	if (doc.querySelector('input#fe_text1')) {
		return 'thesis';
	}
	else if (getSearchResults(doc, true)) {
		return 'multiple';
	}
	return false;
}

function getSearchResults(doc, checkOnly) {
	var items = {};
	var found = false;
	var rows = doc.querySelectorAll('a.slink');
	for (let row of rows) {
		let href = row.href;
		let title = ZU.trimInternal(row.textContent);
		if (!href || !title) continue;
		if (checkOnly) return true;
		found = true;
		items[href] = title;
	}
	return found ? items : false;
}

async function doWeb(doc, url) {
	if (detectWeb(doc, url) == 'multiple') {
		let items = await Zotero.selectItems(getSearchResults(doc, false));
		Z.debug(items);
		if (!items) return;
		for (let url of Object.keys(items)) {
			// 需要瀏覽器環境
			await scrape(await requestDocument(url));
		}
	}
	else {
		await scrape(doc);
	}
}

async function scrape(doc) {
	Z.debug(doc.body.innerText);
	let labels = new Labels(doc, '#aa:first-child > #format0_disparea > tbody > tr');
	let extra = new Extra();
	Z.debug(labels.data.map(arr => [arr[0], ZU.trimInternal(arr[1].textContent)]));
	let newIetm = new Z.Item('thesis');
	newIetm.title = labels.get('論文名稱');
	newIetm.abstractNote = text(doc, '#aa:nth-child(2) > #format0_disparea').replace(/\s{2,}/g, '\n');
	newIetm.thesisType = labels.get('學位類別') + '學位論文';
	newIetm.university = labels.get('校院名稱');
	newIetm.date = labels.get('論文出版年');
	newIetm.numPages = labels.get('論文頁數');
	newIetm.language = 'zh-TW';
	newIetm.url = attr(doc, 'input#fe_text1', 'value');
	newIetm.libraryCatalog = '臺灣博碩士論文知識加值系統';
	extra.set('major', labels.get('學類').slice(0, -1));
	let creators = [];
	creators.push({
		firstName: '',
		lastName: labels.get('研究生'),
		creatorType: 'author',
		fieldMode: 1,
		original: ZU.capitalizeName(labels.get('研究生\\(外文\\)'))
	});
	let supervisorsZh = labels.get('指導教授').split(/[;，；、]\s*/);
	let supervisorsEn = labels.get('指導教授\\(外文\\)').split(/[;，；、]\s*/);
	supervisorsZh.forEach((supervisor, index) => {
		creators.push({
			firstName: '',
			lastName: supervisor,
			creatorType: 'contributor',
			fieldMode: 1,
			original: ZU.capitalizeName(supervisorsEn[index] || '')
		});
	});
	if (creators.some(creator => creator.original)) {
		extra.set('creatorsExt', JSON.stringify(creators));
	}
	creators.forEach((creator) => {
		extra.push('original-author', creator.original, true);
		delete creator.original;
		newIetm.creators.push(creator);
	});
	newIetm.tags = `${labels.get('中文關鍵詞')}、${labels.get('外文關鍵詞')}`.split('、');
	newIetm.extra = extra.toString(labels.get('相關次數')
		.replace('被引用:', 'citation: ')
		.replace('點閱:', '\nview: ')
		.replace('評分:', '\nrating: ')
		.replace('下載:', '\ndownload')
		.replace('書目收藏:', '\nfavorite')
	);
	newIetm.complete();
}

class Labels {
	constructor(doc, selector) {
		this.data = [];
		this.emptyElm = doc.createElement('div');
		Array.from(doc.querySelectorAll(selector))
			// avoid nesting
			.filter(element => !element.querySelector(selector))
			// avoid empty
			.filter(element => !/^\s*$/.test(element.textContent))
			.forEach((element) => {
				const elmCopy = element.cloneNode(true);
				// avoid empty text
				while (/^\s*$/.test(elmCopy.firstChild.textContent)) {
					// Z.debug(elementCopy.firstChild.textContent);
					elmCopy.removeChild(elmCopy.firstChild);
					// Z.debug(elementCopy.firstChild.textContent);
				}
				if (elmCopy.childNodes.length > 1) {
					const key = elmCopy.removeChild(elmCopy.firstChild).textContent.replace(/\s/g, '');
					this.data.push([key, elmCopy]);
				}
				else {
					const text = ZU.trimInternal(elmCopy.textContent);
					const key = tryMatch(text, /^[[【]?.+?[】\]:：]/).replace(/\s/g, '');
					elmCopy.textContent = tryMatch(text, /^[[【]?.+?[】\]:：]\s*(.+)/, 1);
					this.data.push([key, elmCopy]);
				}
			});
	}

	get(label, element = false) {
		if (Array.isArray(label)) {
			const results = label
				.map(aLabel => this.get(aLabel, element));
			const keyVal = element
				? results.find(element => !/^\s*$/.test(element.textContent))
				: results.find(string => string);
			return keyVal
				? keyVal
				: element
					? this.emptyElm
					: '';
		}
		const pattern = new RegExp(label, 'i');
		const keyVal = this.data.find(arr => pattern.test(arr[0]));
		return keyVal
			? element
				? keyVal[1]
				: ZU.trimInternal(keyVal[1].textContent)
			: element
				? this.emptyElm
				: '';
	}
}

class Extra {
	constructor() {
		this.fields = [];
	}

	push(key, val, csl = false) {
		this.fields.push({ key: key, val: val, csl: csl });
	}

	set(key, val, csl = false) {
		let target = this.fields.find(obj => new RegExp(`^${key}$`, 'i').test(obj.key));
		if (target) {
			target.val = val;
		}
		else {
			this.push(key, val, csl);
		}
	}

	get(key) {
		let result = this.fields.find(obj => new RegExp(`^${key}$`, 'i').test(obj.key));
		return result
			? result.val
			: '';
	}

	toString(history = '') {
		this.fields = this.fields.filter(obj => obj.val);
		return [
			this.fields.filter(obj => obj.csl).map(obj => `${obj.key}: ${obj.val}`).join('\n'),
			history,
			this.fields.filter(obj => !obj.csl).map(obj => `${obj.key}: ${obj.val}`).join('\n')
		].filter(obj => obj).join('\n');
	}
}


/**
 * Attempts to get the part of the pattern described from the character,
 * and returns an empty string if not match.
 * @param {String} string
 * @param {RegExp} pattern
 * @param {Number} index
 * @returns
 */
function tryMatch(string, pattern, index = 0) {
	if (!string) return '';
	let match = string.match(pattern);
	return (match && match[index])
		? match[index]
		: '';
}

