async function scrapeSingle(doc, url = doc.location.href) {
	let labels = new Cells(doc, 'tr > td');
	let newItem = new Z.Item('book');
	let extra = new Extra();
	addFileds(newItem, extra, labels, 'labels');
	newItem.url = url;
	if (new URL(url.replace('#', '')).searchParams.get('type').includes('ele')) {
		extra.set('medium', 'CD', true);
	}
	processCreators(newItem, extra, labels.get(['作者', '著作权人', '其他著作者']));
	newItem.extra = extra.toString();
	newItem.attachments.push({
		url: url,
		title: 'Snapshot',
		mimeType: 'text/html'
	});
	newItem.tags = labels.get('主题词').split(/\s?[;,，；－]\s?/g);
	newItem.complete();
}

async function scrapeMulti(postData) {
	postData = JSON.parse(postData);
	let usersessionid = localStorage.getItem('user-token');
	let postResult = await requestJSON(
		'https://pdc.capub.cn/api/index/bookDetail',
		{
			method: 'POST',
			headers: { Usersessionid: usersessionid },
			body: `id=${postData.id}&searchType=${postData.type}`
		}
	);
	// Z.debug(postResult);
	let ciphertext = aescbc.base64.toBytes(postResult.result);
	let key = aescbc.utf8.toBytes('zg35ws76swnxz679');
	let iv = aescbc.utf8.toBytes('z66qa18l0w9o521k');
	let cbc = new aescbc.CBC(key, iv);
	let plainText = aescbc.utf8.fromBytes(aescbc.pkcs7.strip(cbc.decrypt(ciphertext)));
	const jsonData = {
		innerData: JSON.parse(plainText),
		get: function (keys) {
			for (let key of keys) {
				let result = this.innerData[key];
				if (result && (result != '**')) {
					return result;
				}
			}
			return '';
		}
	};
	Z.debug(jsonData.innerData);
	let newItem = new Z.Item('book');
	let extra = new Extra();
	addFileds(newItem, extra, jsonData, 'keys');

	let subBookName = jsonData.get(['subbookname']);
	if (subBookName) {
		newItem.shortTitle = newItem.title;
		newItem.title = `${newItem.title}（${subBookName}）`;
	}
	let url = `https://pdc.capub.cn/search.html#/detail?id=${postData.id}&from=1&type=${postData.type}`;
	newItem.url = url;
	processCreators(newItem, extra, jsonData.get(['firstauthor', 'copyrightor', 'authorother']));
	if (postData.type.includes('ele')) {
		extra.set('medium', 'CD', true);
	}
	newItem.attachments.push({
		url: url,
		title: 'Snapshot',
		mimeType: 'text/html'
	});
	newItem.tags = jsonData.get('keyword').split(/\s?[;,，；－]\s?/g);
	newItem.extra = extra.toString();
	newItem.complete();
}

class Cells {
	constructor(doc, selector) {
		this.emptyElm = doc.createElement('div');
		this.data = [];
		const cells = Array.from(doc.querySelectorAll(selector)).filter(element => !element.querySelector(selector));
		let i = 0;
		while (cells[i + 1]) {
			this.data.push([cells[i].textContent.replace(/\s*/g, ''), cells[i + 1]]);
			i += 2;
		}
	}

	get(label, element = false) {
		if (Array.isArray(label)) {
			let result = label
				.map(aLabel => this.get(aLabel, element));
			result = element
				? result.find(element => element.childNodes.length)
				: result.find(element => element);
			return result
				? result
				: element
					? this.emptyElm
					: '';
		}
		const pattern = new RegExp(label);
		const keyValPair = this.data.find(element => pattern.test(element[0]));
		if (element) return keyValPair ? keyValPair[1] : this.emptyElm;
		return keyValPair
			? ZU.trimInternal(keyValPair[1].innerText)
			: '';
	}
}

class Extra {
	constructor() {
		this.fields = [];
	}

	push(key, val, csl = false) {
		this.fields.push({ key: key, val: val, csl: csl });
	}

	set(key, val, csl = false) {
		let target = this.fields.find(obj => new RegExp(`^${key}$`, 'i').test(obj.key));
		if (target) {
			target.val = val;
		}
		else {
			this.push(key, val, csl);
		}
	}

	get(key) {
		let result = this.fields.find(obj => new RegExp(`^${key}$`, 'i').test(obj.key));
		return result
			? result.val
			: undefined;
	}

	toString(history = '') {
		this.fields = this.fields.filter(obj => obj.val);
		return [
			this.fields.filter(obj => obj.csl).map(obj => `${obj.key}: ${obj.val}`).join('\n'),
			history,
			this.fields.filter(obj => !obj.csl).map(obj => `${obj.key}: ${obj.val}`).join('\n')
		].filter(obj => obj).join('\n');
	}
}

function tryMatch(string, pattern, index = 0) {
	let match = string.match(pattern);
	if (match && match[index]) {
		return match[index];
	}
	return '';
}

function addFileds(item, extra, manager, arg) {
	for (let field in fieldMap) {
		let recipe = fieldMap[field];
		let result = manager.get(recipe[arg]);
		if (recipe.callback) {
			result = recipe.callback(result);
		}
		Z.debug(`for field ${field}, I get \n${result}`);
		if (ZU.fieldIsValidForType(field, 'book')) {
			item[field] = result;
		}
		else {
			extra.set(field, result, true);
		}
	}
}

// below codes are modified from https://github.com/ricmoo/aes-js, Thank macoo for his efforts.
// I have ported the code required for this project from his work.
function coerceArray(arg, copy) {
	// ArrayBuffer view
	if (arg.buffer && arg.name === 'Uint8Array') {
		if (copy) {
			if (arg.slice) {
				arg = arg.slice();
			}
			else {
				arg = Array.prototype.slice.call(arg);
			}
		}
		return arg;
	}

	// It's an array; check it is a valid representation of a byte
	if (Array.isArray(arg)) {
		if (!checkInts(arg)) {
			throw new Error('Array contains invalid value: ' + arg);
		}

		return new Uint8Array(arg);
	}

	// Something else, but behaves like an array (maybe a Buffer? Arguments?)
	if (checkInt(arg.length) && checkInts(arg)) {
		return new Uint8Array(arg);
	}

	throw new Error('unsupported array-like object');
}

function createArray(length) {
	return new Uint8Array(length);
}

function copyArray(sourceArray, targetArray, targetStart, sourceStart, sourceEnd) {
	if (sourceStart !== null || sourceEnd !== null) {
		if (sourceArray.slice) {
			sourceArray = sourceArray.slice(sourceStart, sourceEnd);
		}
		else {
			sourceArray = Array.prototype.slice.call(sourceArray, sourceStart, sourceEnd);
		}
	}
	targetArray.set(sourceArray, targetStart);
}

var Utf8 = (function () {
function toBytes(text) {
	var result = [], i = 0;
	text = encodeURI(text);
	while (i < text.length) {
		var c = text.charCodeAt(i++);

		// if it is a % sign, encode the following 2 bytes as a hex value
		if (c === 37) {
			result.push(parseInt(text.substr(i, 2), 16));
			i += 2;

			// otherwise, just the actual byte
		}
		else {
			result.push(c);
		}
	}

	return coerceArray(result);
}

function fromBytes(bytes) {
	var result = [], i = 0;

	while (i < bytes.length) {
		var c = bytes[i];

		if (c < 128) {
			result.push(String.fromCharCode(c));
			i++;
		}
		else if (c > 191 && c < 224) {
			result.push(String.fromCharCode(((c & 0x1f) << 6) | (bytes[i + 1] & 0x3f)));
			i += 2;
		}
		else {
			result.push(String.fromCharCode(((c & 0x0f) << 12) | ((bytes[i + 1] & 0x3f) << 6) | (bytes[i + 2] & 0x3f)));
			i += 3;
		}
	}

	return result.join('');
}

return {
	toBytes: toBytes,
	fromBytes: fromBytes,
};
})();

