		case 'journalArticle':
			newItem.publicationTitle = labels.get('刊名', true).innerText.trim().split(/\s{2}/)[0];
			// newItem.volume = 卷次;
			newItem.issue = tryMatch(labels.get('期'), /0*([1-9]\d*)/, 1);
