		}
		case 'patent': {
			let labels = new Cells(doc, 'table.patent-table > tbody > tr > td');
			Z.debug(labels.data.map(arr => [arr[0], ZU.trimInternal(arr[1].textContent)]));
			item.patentNumber = labels.get('公布号');
			item.applicationNumber = labels.get('申请\\(专利\\)号');
