function detectWeb(doc, url) {
	// avoid https://www.gov.cn/zhengce/jiedu/
	if (/\/zhengce\/.*content_\d+\.htm/.test(url)) {
		return doc.querySelector('[class*="pctoubukuang"]')
			? 'statute'
			: 'webpage';
	}
	else if (/\/gongbao\/.*content_\d+\.htm/.test(url)) {
		return 'journalArticle';
	}
	else if (getSearchResults(doc, true)) {
		return 'multiple';
	}
	return false;
}

function getSearchResults(doc, checkOnly) {
	var items = {};
	var found = false;
	var rows = Array.from(doc.querySelectorAll('a')).filter(element => /\/(zhengce|gongbao)\/.*content_\d+\.htm/.test(element.href));
	for (let row of rows) {
		let href = row.href;
		let title = ZU.trimInternal(row.textContent);
		if (!href || !title) continue;
		if (checkOnly) return true;
		found = true;
		items[href] = title;
	}
	return found ? items : false;
}

async function doWeb(doc, url) {
	if (detectWeb(doc, url) == 'multiple') {
		let items = await Zotero.selectItems(getSearchResults(doc, false));
		if (!items) return;
		for (let url of Object.keys(items)) {
			await scrape(await requestDocument(url));
		}
	}
	else {
		await scrape(doc, url);
	}
}

async function scrape(doc, url = doc.location.href) {
	var newItem = new Z.Item(detectWeb(doc, url));

	switch (newItem.itemType) {
		case 'statute': {
			// 两个selector分别见
			// https://www.gov.cn/zhengce/zhengceku/2022-08/16/content_5705882.htm
			// https://www.gov.cn/zhengce/zhengceku/2019-06/28/content_5404170.htm
			const labels = new Cells(doc, 'table table:only-child td, [class*="pctoubukuang"] > table td');
			Z.debug(labels.data.map(arr => [arr[0], ZU.trim(arr[1].innerText)]));
			newItem.nameOfAct = labels.get('标题');
			// newItem.code = 法典;
			// newItem.codeNumber = 法典编号;
			newItem.publicLawNumber = labels.get('发文字号');
			newItem.dateEnacted = labels.get('成文日期').replace(/\D/g, '-').replace(/-$/, '');
			labels.get('主题分类').split('\\').forEach(tag => newItem.tags.push(tag));
			labels.get('发文机关').split(/\s/).forEach((creator) => {
				creator = ZU.cleanAuthor(creator, 'author');
				creator.fieldMode = 1;
				newItem.creators.push(creator);
			});
			break;
		}
		case 'webpage': {
			newItem.title = text(doc, 'h1#ti');
			newItem.websiteTitle = '中国政府网';
			newItem.date = ZU.strToISO(text(doc, '.pages-date'));
			let creator = innerText(doc, '.pages-date > span').slice(3);
			if (/.*部网站/.test(creator)) {
				creator = '中华人民共和国' + creator.replace(/网站$/, '');
			}
			newItem.creators.push({
				firstName: '',
				lastName: creator,
				creatorType: 'author',
				fieldMode: 1
			});
			break;
		}
		case 'journalArticle': {
			newItem.title = ZU.trimInternal(text(doc, '.share-title'));
			newItem.abstractNote = attr(doc, 'meta[name="description"]', 'content');
			newItem.publicationTitle = '中华人民共和国国务院公报';
			newItem.ISSN = '1004-3438';
			newItem.issue = tryMatch(attr(doc, 'meta[name="lanmu"]', 'content'), /(\d+)号/, 1);
			newItem.date = ZU.strToISO(attr(doc, 'meta[name="firstpublishedtime"]'), 'content');
			Array.from(doc.querySelectorAll('[label="右对齐"]'))
				.map(element => element.textContent.replace(/\s/g, ''))
				.filter(string => /^[\u4e00-\u9fff]+$/.test(string))
				.forEach((creator) => {
					creator = ZU.cleanAuthor(creator, 'author');
					creator.fieldMode = 1;
					newItem.creators.push(creator);
				});
			newItem.tags = attr(doc, 'meta[name="keywords"]', 'content').split(';');
			break;
		}
	}
	newItem.language = 'zh-CN';
	newItem.url = url;
	newItem.attachments.push({
		title: 'Snapshot',
		document: doc
	});
	newItem.complete();
}

class Cells {
	constructor(doc, selector) {
		this.emptyElm = doc.createElement('div');
		this.data = [];
		const cells = Array.from(doc.querySelectorAll(selector)).filter(element => !element.querySelector(selector));
		let i = 0;
		while (cells[i + 1]) {
			this.data.push([cells[i].textContent.replace(/\s*/g, ''), cells[i + 1]]);
			i += 2;
		}
	}

	get(label, element = false) {
		if (Array.isArray(label)) {
			let result = label
				.map(aLabel => this.get(aLabel, element));
			result = element
				? result.find(element => element.childNodes.length)
				: result.find(element => element);
			return result
				? result
				: element
					? this.emptyElm
					: '';
		}
		const pattern = new RegExp(label);
		const keyValPair = this.data.find(element => pattern.test(element[0]));
		if (element) return keyValPair ? keyValPair[1] : this.emptyElm;
		return keyValPair
			? ZU.trimInternal(keyValPair[1].innerText)
			: '';
	}
}


function tryMatch(string, pattern, index = 0) {
	let match = string.match(pattern);
	return match && match[index]
		? match[index]
		: '';
}

