{
  "extends": ["prettier", "eslint:recommended", "plugin:markdown/recommended-legacy"],
  "parserOptions": {
    "ecmaVersion": "latest"
  },
  "plugins": ["markdown", "async-await"],
  "env": {
    "node": true,
    "es6": true
  },
  "rules": {
    "template-curly-spacing": ["error", "never"],
    "prefer-template": "error",
    "no-useless-call": "error",
    "no-lonely-if": "error",
    "indent": [
      "error",
      2,
      {
        "SwitchCase": 1
      }
    ],
    "no-else-return": [
      "error",
      {
        "allowElseIf": false
      }
    ],
    "eqeqeq": "error",
    "no-invalid-this": "error",
    "consistent-this": "error",
    "prefer-arrow-callback": "error",
    "prefer-const": "error",
    "arrow-body-style": ["error", "as-needed"],
    "no-var": "error",
    "no-use-before-define": "error",
    "strict": ["error", "global"]
  },
  "overrides": [
    {
      "files": ["**/*.ts"],
      "parser": "@typescript-eslint/parser",
      "extends": ["plugin:@typescript-eslint/recommended"],
      "plugins": ["@typescript-eslint"],
      "rules": {
        "@typescript-eslint/no-empty-interface": "off",
        "@typescript-eslint/no-explicit-any": "off",
        "arrow-parens": "off",
        "no-restricted-syntax": [
          "error",
          {
            "selector": "ImportDeclaration[source.value=/^\\./][source.value!=/\\.(js)$/]",
            "message": "Local imports must have the explicit extension"
          }
        ]
      }
    },
    {
      "files": ["**/*.md/*js", "**/*.md/*ts"],
      "rules": {
        "no-undef": "off",
        "no-unused-vars": "off",
        "@typescript-eslint/no-unused-vars": "off",
        "no-console": "off",
        "no-unused-labels": "off",
        "strict": "off",
        "prefer-arrow-callback": "off"
      },
      "parserOptions": {
        "sourceType": "module"
      }
    },
    {
      "files": ["**/**/*.test.*"],
      "rules": {
        "arrow-parens": ["error", "always"]
      }
    },
    {
      "files": ["**/*.mjs"],
      "parserOptions": {
        "sourceType": "module"
      }
    }
  ]
}

{
  "printWidth": 80,
  "singleQuote": true
}

'use strict';

const SqlString = require('sqlstring');

const ConnectionConfig = require('./lib/connection_config.js');
const parserCache = require('./lib/parsers/parser_cache.js');

const Connection = require('./lib/connection.js');

exports.createConnection = require('./lib/create_connection.js');
exports.connect = exports.createConnection;
exports.Connection = Connection;
exports.ConnectionConfig = ConnectionConfig;

const Pool = require('./lib/pool.js');
const PoolCluster = require('./lib/pool_cluster.js');
const createPool = require('./lib/create_pool.js');
const createPoolCluster = require('./lib/create_pool_cluster.js');

exports.createPool = createPool;

exports.createPoolCluster = createPoolCluster;

exports.createQuery = Connection.createQuery;

exports.Pool = Pool;

exports.PoolCluster = PoolCluster;

exports.createServer = function (handler) {
  const Server = require('./lib/server.js');
  const s = new Server();
  if (handler) {
    s.on('connection', handler);
  }
  return s;
};

exports.PoolConnection = require('./lib/pool_connection.js');
exports.authPlugins = require('./lib/auth_plugins');
exports.escape = SqlString.escape;
exports.escapeId = SqlString.escapeId;
exports.format = SqlString.format;
exports.raw = SqlString.raw;

exports.__defineGetter__(
  'createConnectionPromise',
  () => require('./promise.js').createConnection,
);

exports.__defineGetter__(
  'createPoolPromise',
  () => require('./promise.js').createPool,
);

exports.__defineGetter__(
  'createPoolClusterPromise',
  () => require('./promise.js').createPoolCluster,
);

exports.__defineGetter__('Types', () => require('./lib/constants/types.js'));

exports.__defineGetter__('Charsets', () =>
  require('./lib/constants/charsets.js'),
);

exports.__defineGetter__('CharsetToEncoding', () =>
  require('./lib/constants/charset_encodings.js'),
);

exports.setMaxParserCache = function (max) {
  parserCache.setMaxCache(max);
};

exports.clearParserCache = function () {
  parserCache.clearCache();
};

