export default {
  input: "src/main.js",
  output: [
    {
      file: "js/min.js",
      format: "iife",
      sourcemap: true,
      plugins: [terser()],
    },
  ],
};

---
permalink: /sitemap.xml
eleventyExcludeFromCollections: true
siteWideUpdate: 2020-07-12
---
<?xml version="1.0" encoding="utf-8"?>
<urlset xmlns="http://www.sitemaps.org/schemas/sitemap/0.9">
{%- for page in collections.all %}
  {% set absoluteUrl %}{{ page.url | url | absoluteUrl(metadata.url) }}{% endset %}
  {% set pageDate = page.date | sitemapDateTimeString %}
  {% set lastModifiedDate = page.inputPath | lastModifiedDate  | sitemapDateTimeString %}
  {% set siteWide = siteWideUpdate| sitemapDateTimeString %}
  {% set lastmod = siteWide %}
  {% if pageDate > lastmod %}
    {% set lastmod = pageDate %}
  {% endif %}
  {% if lastModifiedDate > lastmod %}
    {% set lastmod = lastModifiedDate %}
  {% endif %}
  <url>
    <loc>{{ absoluteUrl }}</loc>
    <lastmod>{{ lastmod }}</lastmod>
  </url>
{%- endfor %}
</urlset>

---
permalink: /tags/
layout: layouts/home.njk
---
<h1>Tags</h1>

{% for tag in collections.tagList %}
  {% set tagUrl %}/tags/{{ tag | slugify }}/{% endset %}
  <a href="{{ tagUrl | url }}" class="post-tag">{{ tag }}</a>
{% endfor %}

{
  "headers": [
    {
      "source": "/(.*)",
      "headers": [
        {
          "key": "Cache-Control",
          "value": "public,max-age=300"
        },
        {
          "key": "X-Content-Type-Options",
          "value": "nosniff"
        },
        {
          "key": "X-Frame-Options",
          "value": "SAMEORIGIN"
        },
        {
          "key": "X-XSS-Protection",
          "value": "1; mode=block"
        },
        {
          "key": "Access-Control-Allow-Origin",
          "value": "n/a"
        }
      ]
    },
    {
      "source": "/new-on-the-web/(.*).html",
      "headers": [
        {
          "key": "X-Frame-Options",
          "value": "ALLOW"
        }
      ]
    },
    {
      "source": "/img/(.*)",
      "headers": [
        {
          "key": "Cache-Control",
          "value": "public,max-age=31536000,immutable"
        }
      ]
    },
    {
      "source": "/js/(.*)",
      "headers": [
        {
          "key": "Cache-Control",
          "value": "public,max-age=31536000,immutable"
        }
      ]
    },
    {
      "source": "/fonts/(.*)",
      "headers": [
        {
          "key": "Cache-Control",
          "value": "public,max-age=31536000,immutable"
        },
        {
          "key": "Access-Control-Allow-Origin",
          "value": "*"
        }
      ]
    },
    {
      "source": "/favicon.svg",
      "headers": [
        {
          "key": "Cache-Control",
          "value": "public,max-age=3600"
        }
      ]
    }
  ]
}
