/* eslint-env node */

// This JS file exists to reset 'env' and 'globals',
// which ESLint doesn't have a built-in way to do otherwise.
// Because of this, we have to bypass semistandard.
const standard = require('eslint-config-standard');

const config = Object.assign(standard, {
  env: {},
  globals: {},
  rules: Object.assign(standard.rules, {
    // semistandard
    semi: ['error', 'always'],
    'no-extra-semi': 'error',
    // disablements
    'n/no-callback-literal': 'off',
    'no-lone-blocks': 'off',
    'object-shorthand': 'off',
    'prefer-const': 'off',
    'prefer-promise-reject-errors': 'off',
    // extra
    'operator-linebreak': ['error', 'before', { overrides: { '=': 'after', '+=': 'after' } }],
    eqeqeq: ['error'],
    'no-unused-vars': ['error', { args: 'all', argsIgnorePattern: '^_' }]
  })
});

module.exports = config;

{
  "root": true,
  "extends": ".eslintrc.base.js",
  "plugins": [
    "compat",
    "html",
    "json-es",
    "markdown",
    "qunit"
  ],
  "reportUnusedDisableDirectives": true,
  "ignorePatterns": [
    "__codeorigin/**",
    "coverage/**",
    "demos/*/qunit/**",
    "demos/*/coverage/**",
    "demos/*/tmp/**",
    "demos/*/package-lock.json",
    "docs/.jekyll-cache/**",
    "docs/_site/**",
    "lib/**",
    "qunit/**",
    "test/cli/fixtures/sourcemap/*.min.js",
    "temp/**"
  ],
  "overrides": [
    // Universal source code
    // Written for compat with ES5 runtimes,
    // ES6+ syntax is transpiled via Rollup. ES6+ APIs may not be used.
    {
      "files": ["src/**"],
      "extends": ".eslintrc.base.js",
      "parserOptions": {
        "ecmaVersion": 6,
        "sourceType": "module"
      },
      "globals": {
        "clearTimeout": "off",
        "setTimeout": "off"
      },
      "rules": {
        "no-console": "error",
        "compat/compat": [
          "error",
          "android >= 4.3, firefox >= 45, ie >= 11, safari >= 9.1, ios_saf >= 7, chrome >= 58"
        ]
      }
    },
    // Universal test suites
    // Strictly ES5 syntax for ES5 runtimes.
    {
      "files": ["demos/**", "test/**"],
      "extends": [".eslintrc.base.js", "plugin:qunit/recommended"],
      "parserOptions": {
        "ecmaVersion": 5,
        "sourceType": "script"
      },
      "globals": {
        "QUnit": "readonly"
      },
      "rules": {
        "compat/compat": [
          "error",
          "android >= 4.3, firefox >= 45, ie >= 11, safari >= 9.1, chrome >= 58"
        ],
        "max-len": "off",
        "no-throw-literal": "off",
        "no-var": "off",
        "prefer-regex-literals": "off",
        "qunit/literal-compare-order": "off",
        "qunit/no-arrow-tests": "off",
        "qunit/no-assert-equal": "off",
        "qunit/no-assert-equal-boolean": "off",
        "qunit/no-assert-logical-expression": "off",
        "qunit/no-only": "off",
        "qunit/require-expect": "off",
        "qunit/resolve-async": "off"
      }
    },
    // Node.js-specific source code
    {
      "files": [
        "bin/**",
        "build/**/*.js",
        "src/cli/**",
        "Gruntfile.js",
        "rollup.config.js"
      ],
      "excludedFiles": [
        "build/coverage-bridge.js"
      ],
      "extends": [".eslintrc.base.js", "plugin:n/recommended"],
      "parserOptions": {
        "ecmaVersion": 2020,
        "sourceType": "script"
      },
      "env": {
        "es2020": true,
        "node": true
      },
      "rules": {
        "strict": "error",
        "compat/compat": "off",
        "no-console": "off",
        "no-process-exit": "off",
        "n/no-process-exit": "off",
        "n/no-missing-require": "off",
        "n/no-unpublished-require": "off"
      }
    },
    // Misc overrides
    {
      "files": ["test/cli/**"],
      "env": {
        "es2020": true,
        "node": true
      },
      "parserOptions": {
        "ecmaVersion": 2020
      },
      "rules": {
        "compat/compat": "off"
      }
    },
    {
      "files": ["test/mozjs.js", "test/mozjs.mjs"],
      "parserOptions": {
        "ecmaVersion": 6
      },
      "rules": {
        "compat/compat": "off"
      }
    },
    {
      "files": ["test/main/*-es6.js"],
      "env": {
        "es6": true
      },
      "parserOptions": {
        "ecmaVersion": 6
      },
      "rules": {
        "compat/compat": "off"
      }
    },
    {
      "files": ["test/main/*-es2018.js", "test/main/*-esm.mjs"],
      "env": {
        "es2018": true
      },
      "parserOptions": {
        "ecmaVersion": 2018
      },
      "rules": {
        "compat/compat": "off"
      }
    },
    {
      "files": ["test/benchmark/**"],
      "env": {
        "es2017": true
      },
      "parserOptions": {
        "ecmaVersion": 2018
      },
      "rules": {
        "compat/compat": "off"
      }
    },
    {
      "files": ["test/dynamic-import/**"],
      "env": {
        "es2017": true
      },
      "parserOptions": {
        "ecmaVersion": 2020
      },
      "rules": {
        "compat/compat": "off"
      }
    },
    {
      "files": ["test/node/**"],
      "env": {
        "es2017": true,
        "node": true
      },
      "parserOptions": {
        "ecmaVersion": 2018
      },
      "rules": {
        "compat/compat": "off"
      }
    },
    {
      "files": ["demos/**/*.js", "demos/**/*.mjs"],
      "env": {
        "browser": true,
        "es2017": true,
        "node": true
      },
      "parserOptions": {
        "ecmaVersion": 2022
      },
      "rules": {
        "compat/compat": "off"
      }
    },
    {
      "files": ["demos/**/*.html", "test/**/*.html"],
      "env": {
        "browser": true
      }
    },
    {
      "files": ["**/*.mjs"],
      "parserOptions": {
        "sourceType": "module"
      }
    },
    {
      "files": [
        "docs/resources/**/*.html",
        "docs/resources/**/*.js"
      ],
      "parserOptions": {
        "ecmaVersion": 2018
      },
      "env": {
        "es2017": true,
        "browser": true
      },
      "globals": {
        "QUnit": "readonly"
      }
    },
    {
      "files": ["**/*.md"],
      "processor": "markdown/markdown"
    },
    {
      "files": ["**/*.md/**"],
      "parserOptions": {
        "ecmaFeatures": {
          "impliedStrict": true
        }
      },
      "rules": {
        // https://github.com/eslint/eslint/issues/15732
        "array-bracket-spacing": "off",
        "camelcase": "off",
        "n/handle-callback-err": "off",
        "no-labels": "off",
        "no-undef": "off",
        "no-unused-expressions": "off",
        "no-unused-vars": "off",
        "no-var": "off",
        "object-shorthand": "off",
        "strict": "off"
      }
    },
    {
      "files": ["**/*.json"],
      "extends": [
        "plugin:json-es/recommended"
      ],
      "parser": "eslint-plugin-json-es",
      "rules": {
        "indent": ["error", 2]
      }
    }
  ]
}

