import remarkValidateLinks from "remark-validate-links";
import remarkPresetLintConsistent from "remark-preset-lint-consistent";
import remarkPresetLintRecommended from "remark-preset-lint-recommended";
import remarkPresetLintMarkdownStyleGuide from "remark-preset-lint-markdown-style-guide";
import remarkLintListItemIndent from "remark-lint-list-item-indent";
import remarkLintListItemSpacing from "remark-lint-list-item-spacing";
import remarkLintNoFileNameMixedCase from "remark-lint-no-file-name-mixed-case";
import remarkLintNoFileNameIrregularCharacters from "remark-lint-no-file-name-irregular-characters";


export default {
  plugins: [
    remarkValidateLinks,
    remarkPresetLintConsistent,
    remarkPresetLintRecommended,
    remarkPresetLintMarkdownStyleGuide,
    [remarkLintListItemIndent, "one"],
    [remarkLintListItemSpacing, { checkBlanks: true }],
    [remarkLintNoFileNameMixedCase, false],
    [remarkLintNoFileNameIrregularCharacters, false]
  ]
};

import remarkGfm from "remark-gfm";
import lintPreset from "./.remarkrc-lint.js";


export default {
  plugins: [
    remarkGfm,
    lintPreset
  ]
};

import js from "@eslint/js";
import globals from "globals";
import stylistic from "@stylistic/eslint-plugin";
import importPlugin from "eslint-plugin-import";


export default [
  js.configs.recommended,
  importPlugin.flatConfigs.recommended,
  stylistic.configs.customize({
    arrowParens: true,
    braceStyle: "1tbs",
    commaDangle: "never",
    flat: true,
    jsx: false,
    quotes: "double",
    semi: true
  }),
  {
    languageOptions: {
      ecmaVersion: "latest",
      globals: {
        ...globals.node
      }
    },
    settings: {
      "import/resolver": {
        node: {}
      }
    },
    rules: {
      "no-unused-vars": ["error", { caughtErrorsIgnorePattern: "^_" }],
      "no-empty-function": "off",
      "no-console": ["error"],

      // Imports
      "import/extensions": ["error", "ignorePackages"],
      "import/newline-after-import": ["error", { count: 2, exactCount: false, considerComments: true }],

      // Stylistic
      "@stylistic/yield-star-spacing": ["error", "after"],
      "@stylistic/multiline-ternary": "off",
      "@stylistic/no-multiple-empty-lines": ["error", { max: 2, maxEOF: 0, maxBOF: 0 }] // Allow max=2 for imports
    }
  }
];

