{
	"name": "hello",
	"version": "1.0.1",
	"description": "A free group video conference app for the web. No sign ups. No downloads.",
	"main": "server.js",
	"scripts": {
		"start": "node ./scripts/pre-start.js && node server.js",
		"test": "echo \"Error: no test specified\" && exit 1"
	},
	"author": "",
	"license": "ISC",
	"dependencies": {
		"ejs": "^3.1.10",
		"express": "^4.19.2",
		"socket.io": "^4.7.5",
		"vue": "^3.4.31",
		"webrtc-adapter": "^9.0.1"
	},
	"devDependencies": {
		"@eslint/eslintrc": "^3.1.0",
		"@eslint/js": "^9.14.0",
		"eslint-config-prettier": "^9.1.0",
		"globals": "^15.12.0",
		"prettier": "3.4.2"
	},
	"prettier": {
		"printWidth": 120,
		"singleQuote": false,
		"trailingComma": "es5",
		"tabWidth": 2,
		"useTabs": true,
		"overrides": [
			{
				"files": [
					"*.ejs"
				],
				"options": {
					"parser": "html"
				}
			}
		]
	}
}

const socketIO = require("socket.io");
const express = require("express");
const path = require("path");
const http = require("http");
const app = express();

const config = require("./config");
const signallingServer = require("./signalling-server");

// Get PORT from env variable else assign 3000 for development
const PORT = config.PORT || 3000;
const server = http.createServer(app);

app.set("view engine", "ejs");

app.use(express.static(path.join(__dirname, "node_modules/vue/dist/")));
app.use(express.static(path.join(__dirname, "assets")));
app.use(express.static(path.join(__dirname, "www"), { maxAge: 0 }));

const io = socketIO(server);
io.sockets.on("connection", signallingServer);

app.get("/", (req, res) => res.render("index", { page: "index", title: "A free video chat for the web." }));

app.get("/faq", (req, res) => res.render("faq", { page: "faq", title: "Frequently asked questions" }));
app.get(["/privacy", "/legal", "/terms"], (req, res) =>
	res.render("privacy", { page: "privacy", title: "Privacy policy" })
);
app.get("/404", (req, res) => res.render("404", { page: "404", title: "Page not found" }));

app.get("/:channel", (req, res) => {
	const channel = req.params.channel;
	const channelRegex = /^([a-zA-Z0-9-]){1,100}$/;
	if (!channelRegex.test(channel)) return res.render("invalid", { page: "invalid-channel", title: "Invalid channel" });

	res.render("channel", { page: "channel", title: channel });
});

app.use("/*", (req, res) => res.render("404", { page: "404", title: "Page not found" }));

server.listen(PORT, null, () => {
	console.log("Hello server started");
	console.log({ port: PORT, node_version: process.versions.node });
});

/**
 * A simple signalling server implementation using socket.io.
 * This socket connection is used a signalling server as WebRTC does not support discovery of other peers.
 * User's audio, video or chat messages does not use this socket.
 */

const channels = {};
const sockets = {};
const peers = {};

const signallingServer = (socket) => {
	const clientAddress = socket.handshake.address;

	socket.channels = {};
	sockets[socket.id] = socket;

	console.log("[" + socket.id + "] connection accepted");
	socket.on("disconnect", () => {
		for (const channel in socket.channels) {
			part(channel);
		}
		console.log("[" + socket.id + "] disconnected");
		delete sockets[socket.id];
	});

	socket.on("join", (config) => {
		console.log("[" + socket.id + "] joined. Channel: ", config.channel);
		const channel = config.channel;

		// Already Joined
		if (channel in socket.channels) return;

		if (!(channel in channels)) channels[channel] = {};

		if (!(channel in peers)) peers[channel] = {};

		peers[channel][socket.id] = { userData: config.userData };

		for (const id in channels[channel]) {
			channels[channel][id].emit("addPeer", {
				peer_id: socket.id,
				should_create_offer: false,
				channel: peers[channel],
			});
			socket.emit("addPeer", { peer_id: id, should_create_offer: true, channel: peers[channel] });
		}

		channels[channel][socket.id] = socket;
		socket.channels[channel] = channel;
	});

	socket.on("updateUserData", async (config) => {
		const channel = clientAddress + config.channel;
		const key = config.key;
		const value = config.value;
		for (let id in peers[channel]) {
			if (id == socket.id) {
				peers[channel][id]["userData"][key] = value;
			}
		}
	});

	const part = (channel) => {
		// Socket not in channel
		if (!(channel in socket.channels)) return;

		delete socket.channels[channel];
		delete channels[channel][socket.id];

		delete peers[channel][socket.id];
		if (Object.keys(peers[channel]).length == 0) {
			// last peer disconnected from the channel
			delete peers[channel];
		}

		for (const id in channels[channel]) {
			channels[channel][id].emit("removePeer", { peer_id: socket.id });
			socket.emit("removePeer", { peer_id: id });
		}
	};

	socket.on("relayICECandidate", (config) => {
		let peer_id = config.peer_id;
		let ice_candidate = config.ice_candidate;

		if (peer_id in sockets) {
			sockets[peer_id].emit("iceCandidate", { peer_id: socket.id, ice_candidate: ice_candidate });
		}
	});

	socket.on("relaySessionDescription", (config) => {
		let peer_id = config.peer_id;
		let session_description = config.session_description;

		if (peer_id in sockets) {
			sockets[peer_id].emit("sessionDescription", {
				peer_id: socket.id,
				session_description: session_description,
			});
		}
	});
};

module.exports = signallingServer;
