{
  "name": "jest-image-snapshot",
  "version": "6.4.0",
  "description": "Jest matcher for image comparisons. Most commonly used for visual regression testing.",
  "main": "src/index.js",
  "engines": {
    "node": "^14.15.0 || ^16.10.0 || >=18.0.0"
  },
  "scripts": {
    "lint": "eslint ./ --ignore-path .gitignore --ext .js",
    "test": "jest --ci=false",
    "test:lockfile": "lockfile-lint -p package-lock.json -t npm -a npm -o https: -c -i",
    "test:git-history": "commitlint --from origin/main --to HEAD",
    "posttest": "npm run lint && npm run test:git-history && npm run test:lockfile"
  },
  "keywords": [
    "test",
    "amex",
    "visual testing",
    "css",
    "jest",
    "browser testing"
  ],
  "jest": {
    "preset": "amex-jest-preset",
    "collectCoverageFrom": [
      "src/*.js",
      "!src/diff-process.js",
      "!**/node_modules/**",
      "!test-results/**"
    ],
    "testMatch": [
      "<rootDir>/__tests__/**/*.spec.js"
    ],
    "coveragePathIgnorePatterns": [
      "/node_modules/",
      "<rootDir>/examples"
    ]
  },
  "repository": {
    "type": "git",
    "url": "https://github.com/americanexpress/jest-image-snapshot.git"
  },
  "author": "Andres Escobar <andres.escobar@aexp.com> (https://github.com/anescobar1991)",
  "license": "Apache-2.0",
  "devDependencies": {
    "@commitlint/cli": "^17.6.5",
    "@commitlint/config-conventional": "^17.7.0",
    "@semantic-release/changelog": "^5.0.0",
    "@semantic-release/git": "^9.0.0",
    "amex-jest-preset": "^6.1.0",
    "eslint": "^6.8.0",
    "eslint-config-amex": "^7.0.0",
    "husky": "^4.2.1",
    "image-size": "^0.8.3",
    "jest": "^29.7.0",
    "jest-snapshot": "^29.0.0",
    "lockfile-lint": "^4.14.0",
    "mock-spawn": "^0.2.6",
    "rimraf": "^5.0.10",
    "semantic-release": "^17.0.4"
  },
  "dependencies": {
    "chalk": "^4.0.0",
    "get-stdin": "^5.0.1",
    "glur": "^1.1.2",
    "lodash": "^4.17.4",
    "pixelmatch": "^5.1.0",
    "pngjs": "^3.4.0",
    "ssim.js": "^3.1.1"
  },
  "peerDependencies": {
    "jest": ">=20 <=29"
  },
  "peerDependenciesMeta": {
    "jest": {
      "optional": true
    }
  },
  "husky": {
    "hooks": {
      "pre-commit": "npm test",
      "commit-msg": "commitlint -E HUSKY_GIT_PARAMS"
    }
  },
  "release": {
    "branches": [
      "+([0-9])?(.{+([0-9]),x}).x",
      "main",
      "next",
      "next-major",
      {
        "name": "beta",
        "prerelease": true
      },
      {
        "name": "alpha",
        "prerelease": true
      }
    ],
    "plugins": [
      "@semantic-release/commit-analyzer",
      "@semantic-release/release-notes-generator",
      "@semantic-release/changelog",
      "@semantic-release/npm",
      "@semantic-release/git",
      "@semantic-release/github"
    ]
  },
  "overrides": {
    "eslint-config-amex": {
      "eslint": "$eslint"
    }
  }
}
