{
  "name": "twind-monorepo",
  "version": "1.0.0",
  "description": "twind monorepo",
  "private": true,
  "type": "module",
  "homepage": "https://twind.style",
  "bugs": "https://github.com/tw-in-js/twind/issues",
  "repository": "github:tw-in-js/twind",
  "keywords": [
    "twind",
    "tw-in-js",
    "tailwind",
    "tailwindcss",
    "tailwind-in-js"
  ],
  "license": "MIT",
  "author": "Sascha Tandel (https://github.com/sastan)",
  "funding": [
    {
      "type": "Open Collective",
      "url": "https://opencollective.com/twind"
    },
    {
      "type": "Github Sponsor",
      "url": "https://github.com/sponsors/tw-in-js"
    },
    {
      "type": "Ko-fi",
      "url": "https://ko-fi.com/twind"
    }
  ],
  "engines": {
    "node": ">=14.15.0",
    "pnpm": "^7.0.0"
  },
  "// https://nodejs.org/dist/latest-v16.x/docs/api/corepack.html": "",
  "packageManager": "pnpm@7.16.0",
  "// for npm/yarn within codesandbox — same as in pnpm-workspace.yml": "",
  "workspaces": [
    "examples/*",
    "packages/*",
    "sites/*"
  ],
  "// start — use 'BROWSER=none' to prevent vite to open a browser if within codesandbox or stackblitz": "",
  "scripts": {
    "start": "BROWSER=none pnpm test:ui",
    "build": "pnpm --filter='./packages/*' --if-present build",
    "check": "TIMING=1 eslint --ext .js,.jsx,.cjs,.mjs,.ts,.tsx,.svelte --ignore-path .gitignore .",
    "check:fix": "pnpm check --fix",
    "format": "pnpm prettier . '!pnpm-lock.yaml'",
    "prettier": "prettier --write --plugin-search-dir=. --ignore-path .gitignore",
    "test": "vitest",
    "test:ui": "vitest --ui",
    "test:coverage": "vitest run --coverage",
    "changeset": "changeset",
    "changeset:version": "changeset version && pnpm install --lockfile-only --no-frozen-lockfile --prefer-offline && pnpm prettier **/CHANGELOG.md",
    "changeset:publish": "pnpm build && changeset publish",
    "prepare": "node ./.husky/prepare.cjs"
  },
  "// style-vendorizer is included here to allow examples to find it": "",
  "devDependencies": {
    "@changesets/cli": "^2.24.4",
    "@changesets/get-github-info": "^0.5.1",
    "@size-limit/esbuild": "^8.1.0",
    "@size-limit/file": "^8.1.0",
    "@types/node": "^14.18.33",
    "@typescript-eslint/eslint-plugin": "^5.42.1",
    "@typescript-eslint/parser": "^5.42.1",
    "@vitest/coverage-c8": "^0.25.8",
    "@vitest/ui": "^0.25.8",
    "distilt": "^0.19.2",
    "dotenv": "^10.0.0",
    "eslint": "^8.27.0",
    "eslint-config-prettier": "^8.5.0",
    "eslint-plugin-prettier": "^4.2.1",
    "eslint-plugin-svelte3": "^3.4.1",
    "happy-dom": "^2.55.0",
    "husky": "^7.0.4",
    "is-ci": "^3.0.1",
    "jiti": "^1.16.0",
    "lint-staged": "^12.5.0",
    "prettier": "^2.7.1",
    "prettier-plugin-svelte": "^2.8.0",
    "size-limit": "^8.1.0",
    "style-vendorizer": "^2.2.3",
    "svelte": "^3.54.0",
    "tslib": "^2.4.1",
    "typescript": "~4.8.4",
    "vite": "^4.0.1",
    "vitest": "^0.25.8",
    "wrangler": "^2.2.1"
  },
  "pnpm": {
    "patchedDependencies": {
      "@size-limit/esbuild@8.1.0": "patches/@size-limit__esbuild@8.1.0.patch",
      "monaco-editor@0.34.1": "patches/monaco-editor@0.34.1.patch",
      "@jspm/generator@1.0.0-beta.38": "patches/@jspm__generator@1.0.0-beta.38.patch",
      "@jsenv/logger@4.1.1": "patches/@jsenv__logger@4.1.1.patch",
      "@jsenv/importmap@1.2.1": "patches/@jsenv__importmap@1.2.1.patch",
      "prettier@2.8.1": "patches/prettier@2.8.1.patch"
    },
    "updateConfig": {
      "ignoreDependencies": [
        "@size-limit/esbuild",
        "monaco-editor",
        "@jspm/generator",
        "@jsenv/logger",
        "@jsenv/importmap"
      ]
    }
  },
  "prettier": {
    "printWidth": 100,
    "semi": false,
    "singleQuote": true,
    "trailingComma": "all",
    "overrides": [
      {
        "files": "*.test.js",
        "options": {
          "semi": true
        }
      },
      {
        "files": [
          "sites/twind.run/src/lib/templates/**"
        ],
        "options": {
          "singleQuote": false
        }
      }
    ]
  },
  "eslintConfig": {
    "root": true,
    "parserOptions": {
      "ecmaVersion": 2022,
      "sourceType": "module"
    },
    "env": {
      "es6": true,
      "shared-node-browser": true
    },
    "ignorePatterns": [
      "dist",
      "node_modules",
      "sites/**"
    ],
    "extends": [
      "eslint:recommended",
      "plugin:prettier/recommended"
    ],
    "overrides": [
      {
        "files": [
          "**/*.cjs"
        ],
        "env": {
          "node": true
        }
      },
      {
        "files": [
          "**/*.jsx"
        ],
        "parser": "@typescript-eslint/parser",
        "extends": [
          "eslint:recommended"
        ]
      },
      {
        "files": [
          "*.svelte"
        ],
        "processor": "svelte3/svelte3",
        "plugins": [
          "svelte3"
        ]
      },
      {
        "files": [
          "**/*.ts",
          "**/*.tsx"
        ],
        "parser": "@typescript-eslint/parser",
        "parserOptions": {
          "project": [
            "./examples/**/tsconfig.json",
            "./packages/**/tsconfig.json",
            "./sites/**/tsconfig.json"
          ]
        },
        "plugins": [
          "@typescript-eslint"
        ],
        "extends": [
          "eslint:recommended",
          "plugin:@typescript-eslint/recommended",
          "plugin:@typescript-eslint/recommended-requiring-type-checking"
        ],
        "rules": {
          "@typescript-eslint/no-unsafe-assignment": "off",
          "@typescript-eslint/no-unsafe-argument": "off",
          "@typescript-eslint/no-unsafe-return": "off",
          "@typescript-eslint/no-explicit-any": "off",
          "@typescript-eslint/restrict-plus-operands": "off",
          "@typescript-eslint/no-extra-semi": "off",
          "@typescript-eslint/no-unsafe-call": "off",
          "@typescript-eslint/restrict-template-expressions": "off"
        }
      }
    ]
  },
  "lint-staged": {
    "*.{js,jsx,cjs,mjs,ts,tsx,svelte}": [
      "eslint --cache --fix",
      "vitest related --run --silent"
    ],
    "!(pnpm-lock.yaml)*": "prettier --plugin-search-dir=. --ignore-unknown --write"
  }
}

{
  "$schema": "http://json.schemastore.org/tsconfig",
  "compilerOptions": {
    "baseUrl": "./",
    "target": "ESNext",
    "module": "NodeNext",
    "moduleResolution": "Node",
    "lib": ["DOM", "DOM.Iterable", "ESNext"],
    // Search under node_modules for non-relative imports.
    "moduleDetection": "auto",
    // ensure that nobody can accidentally use this config for a build
    "noEmit": true,
    "declaration": true,
    "declarationMap": true,
    "noEmitOnError": true,
    "noErrorTruncation": true,
    // Enforce using `import type` instead of `import` for Types
    "importsNotUsedAsValues": "error",
    "allowJs": true,
    // Generate inline sourcemaps
    "sourceMap": false,
    "inlineSourceMap": true,
    "inlineSources": true,
    // Enable strictest settings like strictNullChecks & noImplicitAny.
    "strict": true,
    // Disallow features that require cross-file information for emit.
    "isolatedModules": true,
    "allowSyntheticDefaultImports": true,
    // Import non-ES modules as default imports.
    "esModuleInterop": true,
    // Allow to import json files
    "resolveJsonModule": true,
    "skipLibCheck": true,
    "forceConsistentCasingInFileNames": true,
    "importHelpers": true
  }
}

import isCI from 'is-ci'

export default {
  test: {
    coverage: {
      reporter: isCI ? 'lcovonly' : 'html',
    },
    api: { port: 3002 },
  },
}
