// 参考 https://eslint.org/docs/user-guide/configuring
module.exports = {
    root: true,
    env: {
        node: true,
    },
    extends: ["plugin:vue/essential", "eslint:recommended", "prettier"],
    rules: {
        "no-console": "off",
        "no-debugger": "off",
        "no-unused-vars": "warn",
        "no-useless-escape": "off",
        // plugin:vue/recommended 规则
        "vue/attributes-order": "error",
        "vue/html-quotes": ["error", "double"],
        // plugin:vue/strongly-recommended 规则
        "vue/attribute-hyphenation": ["error", "always"],
        "vue/html-end-tags": "error",
        "vue/html-indent": "off",
        "vue/require-default-prop": "error",
        "vue/require-prop-types": "error",
        "vue/jsx-uses-vars": "error",
        "consistent-this": ["error", "that"], // this别名
        "vue/order-in-components": [
            "error",
            {
                // methods 顺序和 官网推荐稍有不同
                order: [
                    "el",
                    "name",
                    "parent",
                    "functional",
                    ["delimiters", "comments"],
                    ["components", "directives", "filters"],
                    "extends",
                    "mixins",
                    "inheritAttrs",
                    "model",
                    ["props", "propsData"],
                    "data",
                    "computed",
                    "watch",
                    "methods",
                    "LIFECYCLE_HOOKS",
                    ["template", "render"],
                    "renderError",
                ],
            },
        ],
    },
    parserOptions: {
        parser: "babel-eslint",
    },
    globals: {},
};

module.exports = {
    comments: false,
    presets: [
        [
            "@babel/env",
            {
                targets: {
                    ie: "11",
                    edge: "17",
                    firefox: "60",
                    chrome: "67",
                    safari: "11.1",
                },
                useBuiltIns: "usage",
                corejs: "3.6.5",
            },
        ],
    ],
    plugins: ["transform-vue-jsx"],
    env: {
        test: {
            // presets: [["env", { targets: { node: "current" } }]],
            // plugins: ["istanbul"]
        },
    },
};

// COVERAGE
const COVERAGE = process.env.COVERAGE;

module.exports = {
    setupFiles: ["./tests/unit/setup.js"],
    moduleFileExtensions: ["js", "jsx", "json", "vue", "md", "jpg"],
    transform: {
        ".*\\.(vue|md)$": "<rootDir>/node_modules/vue-jest",
        "^.+\\.(js|jsx)$": "<rootDir>/node_modules/babel-jest",
    },
    // 支持源代码中相同的 `@` -> `packages` 别名
    moduleNameMapper: {
        "^@/(.*)$": "<rootDir>/packages/$1",
        "^ve-table/(.*)$": "<rootDir>/$1", // map webpack externals comps
    },
    snapshotSerializers: ["<rootDir>/node_modules/jest-serializer-vue"],
    coverageDirectory: "tests/unit/coverage",
    collectCoverage: COVERAGE === "true",
    collectCoverageFrom: [
        "packages/ve-*/**/*.{jsx,js,vue}",
        "!packages/ve-*/src/util/*",
        "!packages/ve-checkbox-group/**",
        "!packages/ve-table/src/selection/*",
        "!**/node_modules/**",
    ],
};
