{
    "presets": ["es2015", "stage-0"]
}

{
    "extends": ".eslintrc",
    "plugins" : [],
    "parser": "babel-eslint",
}

module.exports = {
    env: {
        es6: true,
        browser: true,
        jasmine: true,
        amd: true,
    },
    parserOptions: {
        sourceType: 'module',
        "ecmaFeatures": {
            "experimentalObjectRestSpread": true
        },
    },
    globals: {
        sinon: true,
    },
    rules: {
        'block-scoped-var': 1,
        'comma-style': [2, 'last'],
        'complexity': 1,
        'consistent-this': [0, 'self'],
        'default-case': 1,
        'dot-notation': 0,
        'guard-for-in': 1,
        'keyword-spacing': 1,
        'newline-after-var': 1,
        'no-alert': 2,
        'no-console': 2,
        'no-debugger': 2,
        'no-div-regex': 1,
        'no-eq-null': 1,
        'no-floating-decimal': 1,
        'no-multiple-empty-lines': [2, {'max': 2}],
        'no-nested-ternary': 1,
        'no-param-reassign': 0,
        'no-self-compare': 1,
        'no-throw-literal': 1,
        'no-void': 1,
        'no-unused-vars': [1, {'varsIgnorePattern': '[d3Transition]'}],
        'no-underscore-dangle': 0,
        'quotes': [2, 'single'],
        'one-var': [1, {
            'var': 'always',
            'const': 'never',
        }],
        'radix': 1,
        'vars-on-top' : 1,
        'wrap-iife': [2, 'inside'],
    },
};

module.exports = function(grunt) {
    'use strict';

    require('load-grunt-tasks')(grunt, { pattern: [
        'grunt-*'
    ]});

    grunt.loadTasks('src/tasks');
};

{
    "files": [
        "README.md"
    ],
    "imageSize": 150,
    "contributorsPerLine": 4,
    "badgeTemplate": "[![All Contributors](https://img.shields.io/badge/all_contributors-<%= contributors.length %>-orange.svg?style=flat-square)](#contributors)",
    "contributorTemplate": "<%= avatarBlock %><br /><%= contributions %>",
    "contributors": []
}

let webpackConfig = require('./webpack.config');

webpackConfig.devtool = 'inline-source-map';

// Karma configuration
module.exports = function(config) {
    'use strict';

    config.set({

        // base path that will be used to resolve all patterns (eg. files, exclude)
        basePath: '',

        // frameworks to use
        // available frameworks: https://npmjs.org/browse/keyword/karma-adapter
        frameworks: ['jasmine-jquery', 'jasmine'],

        // list of files / patterns to load in the browser
        files: [
            'tests_index.js',
            {
                pattern: 'test/fixtures/*.html',
                watched: true,
                served: true,
                included: false
            },
            './node_modules/phantomjs-polyfill-find/find-polyfill.js',
            './node_modules/babel-polyfill/dist/polyfill.js',
        ],


        // list of files to exclude
        exclude: [
            'node_modules/**/*spec*',
            'node_modules/**/*Spec*'
        ],

        // preprocess matching files before serving them to the browser
        // available preprocessors: https://npmjs.org/browse/keyword/karma-preprocessor
        preprocessors: {
            'tests_index.js': ['webpack', 'sourcemap', 'coverage'],
        },

        // Coverage reporter options, check more in:
        // https://github.com/karma-runner/karma-coverage
        coverageReporter: {
            type: 'text',
            reporters: [
                {type: 'text'},
            ],
            check: {
                global: {
                    statements: 50,
                    branches: 50,
                    functions: 50,
                    lines: 50,
                    excludes: [
                        'tests_index.js',
                        'src/charts/helpers/export.js',
                        'src/charts/helpers/date.js',
                        'src/charts/helpers/filter.js'
                    ]
                }
            }
        },

        webpack: webpackConfig('test'),

        webpackMiddleware: {
            noInfo: true
        },

        plugins: [
            require('karma-webpack'),
            require('karma-jasmine'),
            require('karma-jasmine-jquery'),
            require('karma-coverage'),
            require('karma-chrome-launcher'),
            require('karma-phantomjs-launcher'),
            require('karma-sourcemap-loader')
        ],

        // Setup of babel settings
        // Check more in: https://github.com/babel/karma-babel-preprocessor
        babelPreprocessor: {
            options: {
                presets: ['es2015']
            }
        },

        // test results reporter to use
        // possible values: 'dots', 'progress'
        // available reporters: https://npmjs.org/browse/keyword/karma-reporter
        reporters: ['dots', 'coverage'],


        // web server port
        port: 9876,


        // enable / disable colors in the output (reporters and logs)
        colors: true,


        // level of logging
        // possible values: config.LOG_DISABLE || config.LOG_ERROR || config.LOG_WARN || config.LOG_INFO || config.LOG_DEBUG
        logLevel: config.LOG_INFO,


        // enable / disable watching file and executing tests whenever any file changes
        autoWatch: true,


        // start these browsers
        // available browser launchers: https://npmjs.org/browse/keyword/karma-launcher
        // possible values: 'PhantomJS', 'Chrome'
        browsers: ['Chrome'],


        // Continuous Integration mode
        // if true, Karma captures browsers, runs the tests and exits
        singleRun: false
    });
};

const testsContext = require.context('./test/specs', true, /spec\.js$/);
const srcContext = require.context('./src/charts', true, /\.js$/);
const jsFixturesContext = require.context('./test/fixtures', true, /\.js$/);

testsContext.keys().forEach(testsContext);
srcContext.keys().forEach(srcContext);
jsFixturesContext.keys().forEach(jsFixturesContext);

const path = require('path');
const merge = require('webpack-merge');

const parts = require('./webpack.parts');
const constants = require('./webpack.constants');

const demosConfig = merge([
    {
        mode: 'development',
        devtool: 'cheap-eval-source-map',
        entry: constants.DEMOS,
        output: {
            path: path.resolve(__dirname, './demos/build/'),
            publicPath: '/assets/',
            filename: '[name].js'
        },
        externals: {
            britecharts: 'britecharts'
        },
        devServer: {
            // this is to allow the docs system to access otherwise inaccessible scripts
            proxy: {
                '/britecharts/scripts/common.js': {
                    target: 'http://localhost:8001/',
                    pathRewrite: {'^/britecharts/scripts/' : '/assets/'}
                },
                '/britecharts/scripts/demo-*.js': {
                    target: 'http://localhost:8001/',
                    pathRewrite: {'^/britecharts/scripts/' : '/assets/'}
                },
                '/britecharts/scripts/*.js': {
                    target: 'http://localhost:8001/',
                    pathRewrite: {'^/britecharts/scripts/' : 'scripts/'}
                },
                '/britecharts/': {
                    target: 'http://localhost:8001/',
                    pathRewrite: {'^/britecharts/' : ''}
                }
            },
        },
        optimization: {
            minimize: false,
            namedModules: true,
        },
    },
    parts.babelLoader(),
    parts.devServer(8001),
]);

const testConfig = merge([
    {
        mode: 'development',
        resolve: {
            modules: [
                path.resolve(__dirname, './src/charts'),
                path.resolve(__dirname, './test/fixtures'),
                'node_modules',
            ],
        }
    },
    parts.babelLoader(),
    parts.aliasD3ToVendorPath(),
    parts.babelIstambulLoader(),
]);

const sandboxConfig = merge([
    {
        mode: 'development',
        devtool: 'cheap-eval-source-map',
        entry: {
            sandbox: path.resolve(__dirname, './sandbox/sandbox.js'),
        },
        output: {
            path: path.resolve(__dirname, './sandbox/build'),
            publicPath: '/assets/',
            filename: '[name].js',
        },
    },
    parts.babelLoader(),
    parts.sassLoader(),
    parts.devServer(8002),
]);

const prodBundleConfig = merge([
    {
        mode: 'production',
        devtool: 'source-map',
        entry: {
            britecharts: constants.PATHS.bundleIndex
        },
        output: {
            path: path.resolve(__dirname, 'dist/bundled'),
            filename: 'britecharts.min.js',
            library: ['britecharts'],
            libraryTarget: 'umd'
        },
    },
    parts.babelLoader(),
    parts.aliasD3ToVendorPath(),
    // parts.bundleTreeChart(9999),
    parts.noParseD3Vendor(),
    parts.externals(),
]);

const prodChartsConfig = merge([
    {
        mode: 'production',
        devtool: 'source-map',
        entry: constants.CHARTS,
        output: {
            path: path.resolve(__dirname, './dist/umd'),
            filename: '[name].min.js',
            library: ['britecharts', '[name]'],
            libraryTarget: 'umd'
        },
    },
    parts.babelLoader(),
    parts.aliasD3ToVendorPath(),
    // parts.bundleTreeChart(),
    parts.noParseD3Vendor(),
    parts.externals(),
]);

module.exports = (env) => {
    // eslint-disable-next-line no-console
    console.log('%%%%%%%% env', env);

    if (env === 'demos') {
        return demosConfig;
    }

    if (env === 'test') {
        return testConfig;
    }

    if (env === 'sandbox') {
        return sandboxConfig;
    }

    if (env === 'production') {
        return [
            prodBundleConfig,
            prodChartsConfig
        ];
    }
};

const webpack = require('webpack');
const BundleAnalyzerPlugin = require('webpack-bundle-analyzer').BundleAnalyzerPlugin;
const constants = require('./webpack.constants');

exports.babelLoader = () => ({
    module: {
        rules: [
            {
                test: /\.js$/,
                exclude: /node_modules/,
                use: ['babel-loader'],
            },
        ],
    },
});

exports.babelIstambulLoader = () => ({
    module: {
        rules: [
            {
                test: /\.js?$/,
                include: /src/,
                exclude: /(node_modules|__tests__|tests_index.js)/,
                use: [{
                    loader: 'istanbul-instrumenter-loader',
                    query: {
                        esModules: true
                    }
                }],
            }
        ]
    }
});

exports.bundleTreeChart = (analyzerPort = 8888) => ({
    plugins: [
        new BundleAnalyzerPlugin({
            analyzerPort,
        }),
    ],
});

exports.sassLoader = () => ({
    module: {
        rules: [
            {
                test:/\.scss$/,
                use: [
                    'style-loader',
                    'css-loader',
                    'sass-loader',
                ],
                exclude: /node_modules/,
            }
        ]
    }
});

exports.noParseD3Vendor = () => ({
    module: {
        noParse: [
            new RegExp(constants.PATHS.vendor + '/d3/d3.js')
        ]
    }
});

exports.externals = () => ({
    externals: {
        d3: 'd3'
    },
});

exports.aliasD3ToVendorPath = () => ({
    resolve: {
        alias: {
            d3: constants.PATHS.vendor + '/d3',
        }
    }
});

exports.devServer = (port) => ({
    devServer: {
        host: '0.0.0.0',
        port,
    }
});
