hostname = %APPEND% www.google.cn, www.g.cn

@import url(https://fonts.googleapis.com/css?family=Lato:400,700,400italic,700italic&subset=latin);
.summary_root {
    min-height: 100vh;
    width: 100% !important;
    margin: 70px 0px 0px !important;
    /*margin: 0px !important;*/
    padding: 0px !important;
    text-align: center;
    /*margin-top: 50px;*/
}

.summary_root > div:first-child > div {
    display: inline-block;
    vertical-align: middle;
    margin-right: 30px;
    margin-bottom: 50px;
}

.summary_root > div:first-child > div:first-child {
    margin-right: 30px;
}

.summary_trophy {
    height: 100px;
    /*margin-bottom: 30px;*/
    /*margin-top: 50px;*/
}

.summary_title {
    font-size: 50px;
    font-weight: bold;
    line-height: 1;
    color: #fff;
    margin-bottom: 20px;
}

.summary_description {
    font-size: 18px;
    color: #ddd;
    line-height: 1;
    /*margin-bottom: 50px;*/
}

.summary_block {
    margin-bottom: 50px;
}

.summary_sorting {
    text-align: left;
    color: #ddd;
    line-height: 1;
    font-size: 16px;
}

@media only screen and (max-width: 992px) {
    /*.summary_container {*/
    /*    width: 100%;*/
    /*    padding: 15px;*/
    /*}*/
    /*.summary_more {*/
    /*    top: 15px;*/
    /*    right: 15px;*/
    /*}*/
}

@media only screen and (max-width: 768px) {
    .summary_root {
        min-height: 90vh;
        margin: 50px 0px 0px !important;
    }

    .summary_root > div:first-child > div {
        display: block;
        margin-right: 0px;
        margin-bottom: 0px;
    }

    .summary_root > div:first-child > div:first-child {
        margin-right: 0px;
    }


    .summary_trophy {
        height: 100px;
        margin-bottom: 30px;
        margin-top: 0px;
    }


    .summary_title {
        font-size: 40px;
        font-weight: bold;
        line-height: 1;
        color: #fff;
        margin-bottom: 15px;
    }

    .summary_description {
        font-size: 15px;
        color: #ddd;
        line-height: 1;
        margin-bottom: 50px;
    }

    .summary_sorting {
        font-size: 14px;
    }

}

.mainlayout_root {
