    /*border: 20px solid #eee;*/

    /* 設定body高度為100% 拉到視窗可視的大小 */
    /*height: 100%;*/
}

.mainlayout_wrapper {
    /* 設定高度最小為100%, 如果內容區塊很多, 可以長大 */
    /*min-height: 100%;*/
