    /*position: relative;*/
    /*padding: 20px;*/
}

.mainlayout_container {
    border-radius: 0px;
    border: none;
    margin: 0px;
    /*background: rgb(241, 241, 241);*/
    /*background: #f3f3f3;*/
    background: #141414;
    box-shadow: none;
    padding: 0px;
    /*transition: all .2s ease;*/
    /*position: absolute;*/
    /*top: 64px;*/
    /*padding-top: 64px;*/
    /*left: 240px;*/
    /*padding-left: 240px;*/
    /*bottom: 0px;*/
    /*right: 0px;*/
    /*overflow-y: scroll;*/
}

.mainlayout_container_collapse {
    border-radius: 0px;
    border: none;
    margin: 0px;
    /*background: rgb(241, 241, 241);*/
    /*background: #f3f3f3;*/
    background: #141414;
    box-shadow: none;
    padding: 0px;
    padding-left: 255px;
    /*transition: all .2s ease;*/
}

.mainlayout_content {
    /*margin-top: 20px;*/
    /*max-width: 1200px;*/
    margin: auto;
}

.mainlayout_segment {
    /*padding-top: 15px;*/
}

@media only screen and (max-width: 900px) {
    /*@media only screen and (max-width: 768px) {*/
    .mainlayout_root {
        min-height: 100vh;
        /*border: 10px solid #eee;*/
    }

    .mainlayout_segment {
        padding-top: 0px;
    }

    .mainlayout_container {
        /*padding-bottom: 40px;*/
        /*min-height: 90vh;*/
        /*background: #fff;*/
        /*min-height: auto;*/
        /*padding: 0px 5px 10px;*/
        padding: 0px 0px 10px;
        /*top: 155px;*/
    }

    .mainlayout_container_collapse {
        /*background: #fff;*/
        /*min-height: auto;*/
        padding: 0px 0px 10px;
        padding-top: 70px;
        /*margin-left: 240px;*/
        /*transform: translateX(220px);*/
    }
}

.footer_root {
    /*background: #f3f3f3;*/
    width: 100%;
    padding: 50px 20px;
    /*max-width: 1200px;*/
    max-width: 1000px;
    margin: auto;
    text-align: center;
    color: #888;
    font-size: 12px;
    /*height: 40px;*/
    /*box-sizing: border-box;*/
    /*!* 設定footer絕對位置在底部 *!*/
    /*position: absolute;*/
    /*bottom: 0;*/
    /*!* 展開footer寬度 *!*/
    /*width: 100%;*/
    /*border-top: 1px solid #eee;*/
}

.footer_icon {
    width: 30px;
    margin: 0px 20px;
}

.footer_search {
    margin-bottom: 20px;
}

.footer_search input {
    background: #fff !important;
}

.footer_search button {
    background: #ddd !important;
    color: #888 !important;
}

.footer_desktop_only {
    display: block;
}

@media only screen and (max-width: 768px) {
    .footer_root {
        /*border-top: 1px solid #eee;*/
        color: #aaa;
        padding: 30px 20px 50px;
        margin-top: 50px;
        max-width: 100%;
        font-size: 12px;
    }

    .footer_search {
        width: 100%;
    }

    .footer_desktop_only {
        display: none;
    }
}

.desktop_header_root {
    position: fixed;
    top: 0px;
    left: 0px;
    z-index: 10999;
    width: 100%;
    padding: 15px 20px;
    background: rgba(20, 20, 20, 0.67);
}

.desktop_header_top {
    height: 70px;
}

.desktop_header_root > div {
    display: inline-block;
    vertical-align: middle;

}

.desktop_header_root > div:first-child > * {
    vertical-align: middle;
    display: inline-block;
}

.desktop_header_root > div:nth-child(2) {
    text-align: right;
    position: absolute;
    top: 15px;
    right: 20px;
    line-height: 39px;
}

.desktop_header_root > div:nth-child(2) > * {
    vertical-align: middle;
}

.desktop_header_logo {
    max-height: 35px;
    margin-right: 15px;
    cursor: pointer;
}

.desktop_header_item {
    color: #fff;
    font-size: 17px;
    padding: 10px 15px;
}

.desktop_header_item > img {
    width: 10px;
    height: 20px;
    position: absolute;
    right: 0px;
}

.desktop_header_item:hover {
    color: #fff;
}

.desktop_header_item:active {
    color: #fff;
}

.desktop_header_item:focus {
    color: #fff;
}

.desktop_header_language {
    height: 30px;
    margin-right: 20px;
    cursor: pointer;
}

.desktop_header_language:last-child {
    margin-right: 0px;
}


.desktop_header_button {
    font-size: 14px !important;
    border-radius: 0px !important;
    margin-right: 15px !important;
    color: white !important;
}

.desktop_header_button:first-child {
    background: #E50914 !important;
    border: 1px solid #E50914 !important;
}

.desktop_header_button:nth-child(2) {
    background: none !important;
    border: 1px solid #fff !important;
}


@media only screen and (max-width: 1350px) {
    .desktop_header_root {
        padding: 15px;
    }

    .desktop_header_root > div:nth-child(2) {
        top: 15px;
    }

    .desktop_header_item {
        /*font-size: 17px;*/
        padding: 10px;
    }

    .desktop_header_language {
        height: 30px;
        margin-right: 20px;
    }
}

@media only screen and (max-width: 1200px) {

    .desktop_header_logo {
        max-height: 30px;
        margin-right: 10px;
    }

    .desktop_header_item {
        font-size: 16px;
    }

    .desktop_header_language {
        height: 25px;
        margin-right: 20px;
    }
}

@media only screen and (max-width: 1150px) {
    .desktop_header_button {
        font-size: 13px !important;
        margin-right: 15px !important;
    }

    .desktop_header_item {
        font-size: 15px;
        padding: 10px;
    }
}

@media only screen and (max-width: 1100px) {

    .desktop_header_item {
        font-size: 14px;
    }

    .desktop_header_language {
        margin-right: 15px;
    }
}

@media only screen and (max-width: 1050px) {
    .desktop_header_button {
        font-size: 12px !important;
        margin-right: 10px !important;
        padding: 10px !important;
    }

    .desktop_header_item {
        font-size: 14px;
        /*padding: 10px;*/
    }
}

@media only screen and (max-width: 1000px) {

    .desktop_header_top {
        height: 60px;
    }

    .desktop_header_root {
        padding: 10px;
    }

    .desktop_header_root > div:nth-child(2) {
        top: 10px;
    }


    .desktop_header_logo {
        max-height: 25px;
        margin-right: 5px;
    }

    /*.desktop_header_item {*/
    /*    font-size: 14px;*/
    /*    padding: 10px;*/
    /*}*/
    .desktop_header_language {
        height: 25px;
        margin-right: 15px;
    }

    .desktop_header_language:last-child {
        margin-right: 0px;
    }

    .desktop_header_button {
        padding: 10px 7px !important;
    }
}

@media only screen and (max-width: 950px) {

    .desktop_header_item {
        /*font-size: 14px;*/
        padding: 10px;
    }

    .desktop_header_language {
        height: 20px;
        /*margin-right: 15px;*/
    }
}

.custom_dropdown_hover {
    position: relative;
}

.custom_dropdown_item {
    cursor: pointer;
}

/*.custom_dropdown_item:hover + .custom_dropdown_overlay {*/
.custom_dropdown_hover:hover .custom_dropdown_overlay {
    /* background: yellow; */
    /* display: block; */
    /*-webkit-transform: translateY(0) translateX(-50%) scaleY(1);*/
    /*-ms-transform: translateY(0) translateX(-50%) scaleY(1);*/
    /*-o-transform: translateY(0) translateX(-50%) scaleY(1);*/
    /*transform: translateY(0) translateX(-50%) scaleY(1);*/
    opacity: 1;
    /* z-index: 999; */
    /* top: 100%; */
    visibility: visible;
    transform: translateY(0);
    /*right: 0px;*/
    /*left: auto;*/
}



.custom_dropdown_overlay {
    right: 0px;
    left: auto;
    background: #141414ee;
    color: #fff;
    transition: all .3s ease-in-out;
    overflow: hidden;
    position: absolute;
    opacity: 0;
    z-index: 1;
    width: max-content;
    border: 2px solid #333;
    transform: translateY(-10px);
    text-align: left;
    visibility: hidden;
    /*padding: 5px 20px;*/
    line-height: 3;
    font-size: 15px;

    left: 0px;
    top: 100%;
    margin-top: 10px;

    /*opacity: 1;*/
    /*visibility: visible;*/
    /*transform: translateY(0);*/
}

.custom_dropdown_overlay > a {
    border-bottom: 2px solid #333;
    padding: 5px 30px 0px;
    cursor: pointer;
    /*transition: all .2s ease;*/
    transition: all .1s linear;
    display: block;
}

.custom_dropdown_overlay > a:hover {
    background: #333;
}

.header_root {
    width: 100%;
    z-index: 10999;
    margin: auto;
    background: linear-gradient(to right, #000, transparent);
    top: 0;
    text-align: center;
    min-height: 70px;
    margin-bottom: 0px;
    border-bottom: none;
    background: #141414ee;
    position: relative;
}


.header_drawer_icon {
    color: #fff;
    position: absolute;
    cursor: pointer;
    top: 50%;
    transform: translateY(-50%);
    left: 15px;
}


.header_logo {
    /*max-height: 90px;*/
    position: absolute;
    cursor: pointer;
    max-height: 50px;
    top: 50%;
    transform: translate(-50%, -50%);
    left: 50%;
}

.header_language {
    position: absolute;
    right: 20px;
    /*left: 20px;*/
    top: 20px;
}


.header_language_flag {
    cursor: pointer;
    display: block;
    object-fit: cover;
    border-radius: 100px;
    transition: all 0.1s ease-in-out;
    box-shadow: 0 1px 2px 0 rgba(60, 64, 67, 0.302), 0 1px 3px 1px rgba(60, 64, 67, 0.149);
    width: 30px;
    height: 30px;
}

.overlay_root {
    /*background: red;*/
    position: fixed;
    top: 0px;
    left: 0px;
    height: 100%;
    width: 100%;
    z-index: 11000;
}

.overlay_background {
    /*background: rgba(255, 255, 255, 0.67);*/
    background: rgba(0, 0, 0, 0.67);
    position: fixed;
    top: 0px;
    left: 0px;
    height: 100%;
    width: 100%;
    padding: 10px;
}

.overlay_container {
    background: #141414;
    max-height: 100%;
    width: 100%;
    max-width: 700px;
    margin: auto;
    padding: 10px;
    border-radius: 3px;
    box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.04), 0 4px 8px 0 rgba(0, 0, 0, 0.2);
    overflow-y: scroll;
    -webkit-overflow-scrolling: touch; /* Lets it scroll lazy */
}

.overlay_preview_block {
    /*width: 33.3%;*/
    width: 50%;
    display: inline-block;
    margin-bottom: 0px;
    padding: 5px;
}

@media only screen and (max-width: 1200px) {
    .overlay_preview_block {
        width: 50%;
    }
}

@media only screen and (max-width: 992px) {
    .overlay_container {
        width: 100%;
        max-width: 1000px;
    }
}

.actress_grid_info_block {
    height: 50px;
    margin-top: 5px;
    margin-bottom: 0px;
    text-align: center;
}

.actress_grid_title_block {
    /*color: #4e5d74;*/
    /*color: #222;*/
    /*font-size: 14px;*/
    color: #fff;
    font-weight: bold;
    font-size: 14px;

    margin-bottom: 5px;
    letter-spacing: 0px;
    max-height: 40px;
    line-height: 20px;
    overflow: hidden;
}

.actress_grid_date_block {
    color: rgb(170, 170, 170);
    font-size: 13px;
    letter-spacing: 0px;
    font-family: sans-serif;
    line-height: 15px;
}

@media only screen and (max-width: 768px) {
    .actress_grid_info_block {
        /*height: 15px;*/
    }

    .actress_grid_title_block {
        font-size: 12px;
        max-height: 30px;
        line-height: 15px;
    }

    .actress_grid_date_block {
        font-size: 12px;
        line-height: 15px;
    }
}


.bookmark_container {
    position: absolute;
    top: 0px;
    left: 0px;
    width: 100%;
    height: 100%;
}

.bookmark_container > div {
    width: 100%;
    height: 100%;
    position: relative;
}

/*.bookmark_container > div > div {*/
/*    !*position: absolute;*!*/
/*    !*top: 50%;*!*/
/*    !*transform: translateY(-50%);*!*/
/*    text-align: center;*/
/*    width: 100%;*/
/*}*/

.bookmark_container > div > div {
    text-align: left;
    width: 450px;
    margin: auto;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translateX(-50%) translateY(-50%);
    box-shadow: 0 2px 25px 10px rgba(0, 0, 0, 0.3);
    /*border: 1px solid rgba(255, 255, 255, 0.12);*/
    line-height: normal;
    background-color: rgba(0, 0, 0, .75);
    border-radius: 10px;
    overflow: hidden;
}

.bookmark_background_cover {
    width: 100%;
    object-fit: cover;
    object-position: center top;
    height: 800px;
}

.bookmark_input_container {
    padding: 40px 50px 100px;
}

.bookmark_input {
    width: 100%;
}

.bookmark_input > input {
    /*border-radius: 0px !important;*/
    font-size: 15px !important;
    background: #333 !important;
    border-radius: 5px !important;
    border: none !important;
    color: #fff !important;
    line-height: 35px !important;
}

.bookmark_title_container {
    border-bottom: 1px solid rgb(85, 85, 85);
}

.bookmark_title {
    display: inline-block;
    font-size: 20px;
    font-weight: bold;
    width: 50%;
    color: #888;
    text-align: center;
    padding: 15px 0px;
    cursor: pointer;
}

.bookmark_title:first-child {
    border-right: 1px solid #555;
}

.bookmark_description {
    margin: auto;
    font-size: 17px;
    line-height: 30px;
    color: rgb(238, 238, 238);
    margin-bottom: 20px;
    white-space: pre-wrap;
}

.bookmark_error_message {
    font-size: 13px;
    color: #ff4887;
    margin-top: 5px;
    margin-bottom: 15px;
}


.bookmark_action_button {
    width: 100%;
    color: #fff !important;
    background: #e50914 !important;
    font-size: 17px !important;
    line-height: 50px !important;
    height: auto;
    padding: 0px !important;
}

.bookmark_hint {
    color: #bbb;
    font-size: 13px;
    margin-top: 20px;
}

@media only screen and (max-width: 768px) {
    .bookmark_container > div > div {
        text-align: left;
        width: 95%;
        margin: auto;
        position: absolute;
        top: 20px;
        /*top: 40%;*/
        /*left: 50%;*/
        transform: translateX(-50%);
        box-shadow: none;
        /*border: none;*/
        /*max-width: 100%;*/
        background: none;
    }


    .bookmark_background_cover {
        height: 100vh;
        min-height: 600px;
        visibility: hidden;
    }

    .bookmark_input_container {
        padding: 40px 30px 50px;
        max-width: 450px;
        margin: auto;
    }

    .bookmark_title {
        padding: 15px 0px;
        font-size: 17px;
    }

    .bookmark_title:first-child {
        border-right: none;
    }

    .bookmark_title_container {
        border-bottom: 2px solid #444;
    }

    .bookmark_description {
        font-size: 15px;
        line-height: 25px;
        margin-bottom: 20px;
    }
}

.bookmark_header_title {
    color: #eee;
    font-size: 20px;
    font-weight: bold;
    margin-top: 15px;
    margin-bottom: 30px;
}

.bookmark_header_main_title {
    color: #fff;
    font-size: 30px;
    margin-bottom: 20px;
    margin-top: 20px;
    font-weight: bold;
    line-height: 1;
}


.bookmark_logout_container {
    position: absolute;
    top: 15px;
    right: 15px;
    text-align: right;
}

.bookmark_logout_container > a {
    /*position: absolute !important;*/
    /*top: 15px;*/
    /*right: 15px;*/
    background: #e50914;
    color: #fff;
    padding: 10px 20px;
    cursor: pointer;
    margin-right: 15px;
    font-weight: bold;
}

.bookmark_logout_container > a:hover {
    color: #fff !important;
}

.bookmark_logout_container > a:last-child {
    margin-right: 0px;
    background: #aaa;
}

.bookmark_list_placeholder {
    text-align: center;
    padding: 50px 0px;
    max-width: 90%;
    margin: auto;
    color: #aaa;
    font-size: 17px;
    margin-top: 20px;
}

@media only screen and (max-width: 768px) {

    .bookmark_header_title {
        /*margin-top: 14px;*/
        padding-left: 10px;
        margin-bottom: 20px;
    }

    .bookmark_header_main_title {
        margin-top: 20px;
        display: none;
    }


    .bookmark_logout {
        top: 0px;
        right: 5px;
        padding: 7px 15px;
    }

    .bookmark_logout_container {
        text-align: left;
        position: relative;
        top: unset;
        bottom: 0px;
        z-index: 998;
        width: 100%;
        right: 0px;
        margin-bottom: 20px;
    }


    .bookmark_logout_container > a {
        width: auto;
        padding: 10px 20px;
        cursor: pointer;
        /*margin-right: 0px;*/
        display: inline-block;
        text-align: center;
        /*font-size: 15px;*/
    }


}

.share_bookmark_background {
    background: rgba(0, 0, 0, 0.67);
    position: fixed;
    top: 0px;
    left: 0px;
    height: 100%;
    width: 100%;
    z-index: 998;
}

.share_bookmark_overlay {
    /*background: red;*/
    /*background: rgba(0,0,0, 0.67);*/
    position: fixed;
    top: 0px;
    left: 0px;
    height: 100%;
    width: 100%;
    z-index: 999;
}

.slide_up_scroll {
    -webkit-overflow-scrolling: touch;
}

.share_bookmark_container {
    position: relative;
    height: 100%;
    width: 100%;
}

.share_bookmark_container > div:first-child {
    position: absolute;
    height: 100%;
    width: 100%;
    top: 0px;
    left: 0px;
}

.share_bookmark_content {
    position: absolute;
    top: 50%;
    left: 50%;
    max-width: 90%;
    width: 500px;
    transform: translateX(-50%) translateY(-50%);
    background: #222;
    /*font-size: 14px;*/
    box-shadow: 0 2px 25px 10px rgba(0, 0, 0, 0.3);
    border: 1px solid rgba(255, 255, 255, 0.12);
    padding: 40px 40px 30px;
    color: #fff;
    font-size: 17px;
    line-height: 25px;
}

.share_bookmark_input > input {
    line-height: 30px !important;
}

.share_bookmark_button {
    background: #E50914 !important;
    color: white !important;
    font-size: 15px !important;
    width: 100% !important;
    line-height: 25px !important;
}

/*.share_bookmark_content > div {*/
/*    margin-bottom: 20px;*/
/*}*/

