.videocoverheader_root {
}

.videocoverheader_content {
    width: 50%;
    height: 100%;
    /*min-width: 500;*/
    position: relative;
}

.videocoverheader_code {
    font-size: 20px;
    color: #eee;
    margin-bottom: 20px;
}

.videocoverheader_title {
    font-size: 30px;
    color: #fff;
    margin-bottom: 20px;
    line-height: 40px;
    max-height: 80px;
    overflow: hidden;
}

.videocoverheader_description {
    font-size: 18px;
    color: rgb(204, 204, 204);
    line-height: 25px;
    margin-bottom: 20px;
}

.videocoverheader_extra_content {
    font-size: 17px;
    color: #ccc;
    line-height: 30px;
    max-width: 90%;
}

.videocoverheader_views {
    font-size: 16px;
    color: #eee;
}

.videocoverheader_button {
    margin-top: 20px !important;
    background: #e50914 !important;
    color: white !important;
    font-size: 17px !important;
    border-radius: 0px !important;
}

.videocoverheader_separator {
    border-bottom: 1px solid #eee;
    margin-top: 15px;
    margin-bottom: 15px;
}

.videocoverheader_field_name {
    font-size: 15px;
    color: #aaa;
    line-height: 30px;
    margin-right: 5px;
    display: inline-block;
}

.videocoverheader_field_values {
    font-size: 15px;
    color: #eee !important;
    line-height: 30px;
    display: inline-block;
}

.videocoverheader_preview_block {
    width: 33.3%;
    display: inline-block;
    margin-bottom: 5px;
    padding: 5px;
}

.videocoverheader_overlay_shadow {
    position: absolute;
    /*background: linear-gradient(to bottom, transparent, transparent, #141414);*/
    background: linear-gradient(to bottom, #141414ff, #141414aa, #141414);
    top: 0px;
    left: 0px;
    width: 100%;
    height: 101%;
}

.videocoverheader_overlay {
    position: absolute;
    background: linear-gradient(to right, rgb(20, 20, 20), rgba(20, 20, 20, 0.4), transparent);
    top: 0px;
    left: 0px;
    width: 100%;
    height: 100%;
}

.videocoverheader_container {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    padding-left: 100px;
}

@media only screen and (max-width: 1400px) {
    .videocoverheader_overlay {
        background: linear-gradient(to right, rgb(20, 20, 20), rgba(20, 20, 20, 0.5), transparent);
    }

    .videocoverheader_container {
        padding-left: 75px;
    }
}

@media only screen and (max-width: 1200px) {
    .videocoverheader_preview_block {
        width: 50%;
    }

    .videocoverheader_container {
        padding-left: 50px;
    }
}

@media only screen and (max-width: 768px) {
    .videocoverheader_title {
        font-size: 17px;
        color: rgb(255, 255, 255);
        margin-bottom: 15px;
        line-height: 25px;
        max-height: 50px;
        overflow: hidden;
        text-overflow: ellipsis;
        font-weight: bold;
    }

    .videocoverheader_description {
        display: none;
    }

    .videocoverheader_code {
        display: none;
    }

    .videocoverheader_overlay_shadow {
        /*position: absolute;*/
        /*background: linear-gradient(to bottom, transparent, #141414);*/
        /*!*background: none;*!*/
        /*height: 50%;*/

        position: absolute;
        background: linear-gradient(to bottom, transparent, #141414, #141414);
        height: 101%;
    }

    .videocoverheader_container {
        position: relative;
        padding-left: 20px;
        background: #141414;
        transform: translateY(-20%);
    }

    .videocoverheader_overlay {
        position: absolute;
        background: none;

        position: absolute;
        top: 0px;
        left: 0px;
    }

    .videocoverheader_content {
        width: 100%;
        height: 100%;
        /*min-width: 500;*/
        /*position: relative;*/
    }

    .videocoverheader_extra_content > div:nth-child(3) {
        /*display: none;*/
    }

    .videocoverheader_extra_content {
        font-size: 14px;
        color: #ccc;
        line-height: 30px;
    }

    .videocoverheader_button {
        display: none !important;
        margin-top: 15px !important;
        font-size: 14px;
    }

    .videocoverheader_separator {
        margin-top: 10px;
        margin-bottom: 10px;
    }

    .videocoverheader_views {
        font-size: 14px;
    }

    .videocoverheader_field_name {
        font-size: 14px;
        line-height: 27px;
    }

    .videocoverheader_field_values {
        font-size: 14px;
        line-height: 27px;
        color: #eee;
        /*font-weight: bold;*/
    }

    .videocoverheader_root {
        order: 2;
        margin-bottom: 20px;
        border-bottom: 1px solid #eee;
        padding: 0px 10px 20px;
    }
}

