.landing_player_root {
    position: relative;
    /*padding: 50px 0px 0px;*/
    /*background: linear-gradient(to bottom, #000, #222);*/
    background: none;
}

.landing_player_title {
    font-size: 50px;
    font-weight: bold;
    color: rgb(255, 255, 255);
    text-align: center;
    line-height: 1;
    margin-bottom: 20px;
}

.landing_player_caption {
    font-size: 25px;
    color: rgb(250, 250, 250);
    text-align: center;
    line-height: 1;
    margin-bottom: 50px;
}

.landing_player_container {
    margin: auto;
    width: 70%;
    box-shadow: rgb(0, 0, 0) 0px 0px 10px;
}

.landing_player_play_container {
    position: absolute;
    bottom: 0px;
    left: 0px;
    width: 100%;
    text-align: left;
    /*padding: 20px;*/
    background: linear-gradient(to bottom, transparent, #141414);
}

.landing_player_play_container > div:first-child {
    padding: 20px;
}

.landing_player_action_button {
    position: absolute;
    right: 20px;
    bottom: 12px;
    background: #e50914 !important;
    color: #fff !important;
    border-radius: 0px !important;
    padding: 15px 25px;
    font-weight: bold;
    font-size: 20px;
    cursor: pointer;
}

.landing_player_action_button_remove {
    position: absolute;
    right: 20px;
    bottom: 12px;
    background: #aaa !important;
    color: #fff !important;
    border-radius: 0px !important;
    padding: 15px 25px;
    font-weight: bold;
    font-size: 20px;
    cursor: pointer;
}

.landing_player_bottom_bar {
    position: relative;
    text-align: left;
    background: linear-gradient(to right, #333 5%, #181818 95%);
    height: 75px;
    padding: 0px 20px;
    width: 100%;
}

.landing_player_bottom_bar > div:first-child {
    color: #fff;
    font-size: 20px;
    line-height: 75px;
    display: inline-block;
}

.landing_player_bottom_bar img {
    width: 30px;
    display: inline-block;
    margin-right: 15px;
    vertical-align: middle;
}

.landing_player_c {
}

.dplayer_wrapper {
}

.dplayer_wrapper video {
    padding-bottom: 50px;
}

.dplayer_wrapper .dplayer-controller {
    opacity: 1 !important;
    transform: none !important;
}

@media only screen and (max-width: 2200px) {
    .landing_player_bottom_bar > div:first-child {
        font-size: 17px;
        line-height: 70px;
    }

    .landing_player_bottom_bar {
        height: 70px;
    }

    .landing_player_action_button {
        right: 20px;
        bottom: 10px;
        padding: 15px 25px;
        font-size: 17px;
    }

    .landing_player_action_button_remove {
        right: 20px;
        bottom: 10px;
        padding: 15px 25px;
        font-size: 17px;
    }
}

@media only screen and (max-width: 1800px) {
    .landing_player_bottom_bar > div:first-child {
        font-size: 15px;
        line-height: 60px;
    }

    .landing_player_bottom_bar {
        height: 60px;
    }

    .landing_player_action_button {
        right: 20px;
        bottom: 10px;
        padding: 11px 21px;
        font-size: 15px;
    }

    .landing_player_action_button_remove {
        right: 20px;
        bottom: 10px;
        padding: 11px 21px;
        font-size: 15px;
    }
}

@media only screen and (max-width: 900px) {
    .landing_player_container {
        margin: auto;
        width: 100%;
        box-shadow: rgb(0, 0, 0) 0px 0px 10px;
    }
}

@media only screen and (max-width: 768px) {
    .landing_player_root {
        /*padding: 60px 0px 0px;*/
    }

    .landing_player_title {
        padding-top: 30px;
        font-size: 25px;
        margin-bottom: 10px;
        display: none;
    }

    .landing_player_caption {
        font-size: 15px;
        line-height: 1.3;
        margin-bottom: 20px;
        padding: 0px 10px;
        display: none;
    }

    .landing_player_container {
        margin: auto;
        width: 100%;
        box-shadow: rgb(0, 0, 0) 0px 0px 10px;
    }

    .landing_player_play_container > div:first-child {
        padding: 10px;
    }

    .landing_player_bottom_bar {
        height: 50px;
        padding: 0px 10px;
    }

    .landing_player_bottom_bar > div:first-child {
        font-size: 13px;
        line-height: 50px;
    }

    .landing_player_bottom_bar img {
        width: 25px;
        margin-right: 7px;
        line-height: 50px;
        padding-bottom: 3px;
    }

    .landing_player_action_button {
        /*display: none;*/
        right: 0px;
        bottom: 7px;
        font-size: 13px !important;
        padding: 8px 15px !important;
    }

    .landing_player_action_button_remove {
        /*display: none;*/
        right: 0px;
        bottom: 7px;
        font-size: 13px !important;
        padding: 8px 15px !important;
    }
}

