body {
    font-family: Quicksand, 'Helvetica Neue', Helvetica, Arial, sans-serif, 微軟正黑體 !important;
    overflow-x: hidden;
}

::-webkit-scrollbar {
    display: none;
}

.hoverPointer:hover {
    cursor: pointer;
}

.swiper-button-next.swiper-button-disabled,
.swiper-button-prev.swiper-button-disabled {
    opacity: 0 !important;
    pointer-events: auto !important;
}

.swiper-button-next {
    outline: none;
    background-image: url(data:image/svg+xml;utf8;base64,) !important;
    background-repeat: no-repeat;
    transition: all 0.2s ease-in-out;
    margin-top: 0px !important;
    background-color: rgba(0, 0, 0, 0.7);

    height: 50% !important;
    width: 40px !important;
    background-size: 26px;
    background-position: center center;

    padding: 0;
    top: 50% !important;
    transform: translateY(-50%) !important;
    right: 0px !important;
    box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.04), 0 4px 8px 0 rgba(0, 0, 0, 0.2);
}

.swiper-button-next::after {
    content: '' !important;
}

.swiper-button-prev::after {
    content: '' !important;
}

.swiper-button-prev {
    outline: none;
    background-image: url(data:image/svg+xml;utf8;base64,) !important;
    transition: all 0.2s ease-in-out;
    margin-top: 0px !important;
    background-repeat: no-repeat;

    background-color: rgba(0, 0, 0, 0.7);

    height: 50% !important;
    width: 40px !important;
    background-size: 26px;
    background-position: center center;

    padding: 0;
    top: 50% !important;
    transform: translateY(-50%) !important;
    left: 0px !important;
    box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.04), 0 4px 8px 0 rgba(0, 0, 0, 0.2);
}

.actor-swiper-button-next.swiper-button-disabled,
.actor-swiper-button-prev.swiper-button-disabled {
    opacity: 1;
    pointer-events: auto;
}

.actor-swiper-button-next {
    position: absolute;
    z-index: 10;
    cursor: pointer;
    outline: none;
    background-image: url(data:image/svg+xml;utf8;base64,) !important;
    transition: all 0.2s ease-in-out;
    margin-top: 0px !important;
    background-color: rgba(0, 0, 0, 0.7);

    height: 50% !important;
    width: 40px !important;
    background-size: 15px;
    background-position: 14px;
    background-repeat: no-repeat;

    padding: 0;
    top: 40% !important;
    transform: translateY(-50%) !important;
    right: 0px !important;
    box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.04), 0 4px 8px 0 rgba(0, 0, 0, 0.2);
}

.actor-swiper-button-prev {
    position: absolute;
    z-index: 10;
    cursor: pointer;
    outline: none;
    background-image: url(data:image/svg+xml;utf8;base64,) !important;
    transition: all 0.2s ease-in-out;
    margin-top: 0px !important;

    background-color: rgba(0, 0, 0, 0.7);

    height: 50% !important;
    width: 40px !important;
    background-size: 15px;
    background-position: 12px;

    background-repeat: no-repeat;
    padding: 0;
    top: 40% !important;
    transform: translateY(-50%) !important;
    left: 0px !important;
    box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.04), 0 4px 8px 0 rgba(0, 0, 0, 0.2);
}

