.video_product_root {
    margin-top: 10px;
    margin-bottom: 20px;
}

.video_product_container {
    overflow-x: scroll;
    white-space: nowrap;
    -webkit-overflow-scrolling: touch; /* Lets it scroll lazy */
}

.video_product_cell {
    display: inline-block;
    background: #000;
    /*background: #304048;*/
    /*padding: 8px 0px 8px 10px;*/
    margin-top: 5px;
    margin-right: 10px;
    /*border-radius: 5px;*/
