    display: inline-block;
    width: 20%;
}

.magnet_header_title:first-child {
    text-align: left;
    width: 60%;
}

.magnet_item {
}

.magnet_item:hover {
    background: #333;
}

.magnet_item_title {
    color: #ccc;
    font-size: 14px;
    /*line-height: 35px;*/
    padding: 7px 10px;
    text-align: center;
    display: inline-block;
    width: 20%;
