{
    "name": "g-assist",
    "productName": "Google Assistant",
    "description": "A cross-platform unofficial desktop client for Google Assistant powered by the Google Assistant SDK",
    "version": "1.1.0",
    "private": true,
    "author": "Melvin Abraham <melvinredvirus@gmail.com>",
    "homepage": "https://github.com/Melvin-Abraham/Google-Assistant-Unofficial-Desktop-Client",
    "repository": {
        "url": "https://github.com/Melvin-Abraham/Google-Assistant-Unofficial-Desktop-Client"
    },
    "bugs": {
        "url": "https://github.com/Melvin-Abraham/Google-Assistant-Unofficial-Desktop-Client/issues"
    },
    "license": "Apache-2.0",
    "keywords": [
        "google-assistant",
        "google-assistant-sdk",
        "electron-app",
        "virtual-assistant",
        "desktop-app",
        "electron",
        "assistant",
        "cross-platform"
    ],
    "main": "app.js",
    "build": {
        "appId": "com.redvirus.g-assist",
        "files": [
            "app/**/*",
            "node_modules/**/*",
            "package.json",
            "app.js"
        ],
        "directories": {
            "buildResources": "app/res/misc/*"
        },
        "publish": [
            {
                "provider": "github",
                "owner": "Melvin-Abraham",
                "repo": "Google-Assistant-Unofficial-Desktop-Client"
            }
        ],
        "win": {
            "target": [
                {
                    "target": "nsis",
                    "arch": [
                        "x64",
                        "ia32"
                    ]
                }
            ],
            "icon": "./app/res/icons/favicon.ico"
        },
        "nsis": {
            "oneClick": "false",
            "artifactName": "${productName}-Setup-${version}.${ext}",
