const tocPlugin = require("eleventy-plugin-toc");
const markdownIt = require("markdown-it");
const CleanCSS = require("clean-css");

module.exports = (eleventyConfig) => {
  eleventyConfig.addPassthroughCopy("docs/assets");
  eleventyConfig.addFilter(
    "cssmin",
    (code) => new CleanCSS({}).minify(code).styles,
  );
  // This filter is used for deriving the chapter names
  // from data in chapters.json
  eleventyConfig.addFilter("heading", (str) =>
    str.replace(/[_;\\/:*?\"<>|&']/g, " "),
  );

  // Filter for linking to section headers
  // and displaying chapters in a page
  const markdownItAnchor = require("markdown-it-anchor");
  const mdIt = markdownIt({
    html: true,
    linkify: true,
  }).use(markdownItAnchor, {
    permalink: true,
    permalinkBefore: false,
    permalinkClass: "direct-link",
    permalinkSymbol: "#",
    level: [1, 2],
  });
  eleventyConfig.addFilter("markdown", (code) => {
    if (code) {
      return mdIt.render(code);
    }
  });
  eleventyConfig.setLibrary("md", mdIt);
  eleventyConfig.addPlugin(tocPlugin);

  return {
    dir: {
      input: "docs",
      output: "docs/_site",
    },
  };
};

{
  "env": {
    "es6": true,
    "node": true,
    "browser": true,
    "mocha": true,
    "jest/globals": true
  },
  "ignorePatterns": [
    ".local-chromium",
    "node_modules/",
    "test/functional-tests/reports/",
    "test/docs-tests/gauge/reports/",
    "docs"
  ],
  "extends": [
    "eslint:recommended",
    "prettier"
  ],
  "parser": "@typescript-eslint/parser",
  "parserOptions": {
    "sourceType": "module",
    "ecmaVersion": 9
  },
  "plugins": [
    "jest",
    "prettier",
    "@typescript-eslint",
    "no-instanceof"
  ],
  "rules": {
    "prettier/prettier": [
      "error",
      {
        "endOfLine": "auto"
      }
    ],
    "no-control-regex": 0,
    "no-instanceof/no-instanceof": [
      "error"
    ],
    "quotes": [
      "error",
      "single",
      "avoid-escape"
    ],
    "no-constant-condition": [
      "error",
      {
        "checkLoops": false
      }
    ],
    "no-empty": [
      "error",
      {
        "allowEmptyCatch": true
      }
    ],
    "semi": [
      "error",
      "always"
    ],
    "no-console": 0,
    "curly": 2,
    "require-atomic-updates": 0
  }
}

