const yargs = require('yargs');

const argv = yargs
  .option('type', {
    alias: 't',
    describe: 'provide the type of run (standard, legacy, mobile)',
    demandOption: true,
    default: 'standard',
    type: 'string'
  })
  .help()
  .alias('help', 'h').argv;

let runType = '';

switch (argv.type) {
  case 'legacy':
