{
  "$schema": "https://raw.githubusercontent.com/electron-userland/electron-builder/master/packages/app-builder-lib/scheme.json",
  "productName": "Escrcpy",
  "appId": "org.viarotel.escrcpy",
  "directories": {
    "output": "dist-release",
    "buildResources": "electron/resources/build"
  },
  "files": ["dist", "dist-electron"],
  "win": {
    "icon": "logo.ico",
    "target": [
      {
        "target": "nsis",
        "arch": ["x64", "arm64"]
      },
      {
        "target": "zip",
        "arch": ["x64", "arm64"]
      },
      {
        "target": "portable",
        "arch": ["x64", "arm64"]
      },
      {
        "target": "appx",
        "arch": ["x64", "arm64"]
      }
    ],
    "artifactName": "${productName}-${version}-win-${arch}.${ext}",
    "extraResources": {
      "from": "electron/resources/extra",
      "to": "extra",
      "filter": ["common", "win", "win-${arch}"]
    }
  },
  "nsis": {
    "artifactName": "${productName}-${version}-win-setup-${arch}.${ext}",
    "shortcutName": "${productName}",
    "uninstallDisplayName": "${productName}",
    "oneClick": false,
    "perMachine": false,
    "allowToChangeInstallationDirectory": true,
    "deleteAppDataOnUninstall": false
  },
  "portable": {
    "artifactName": "${productName}-${version}-win-portable-${arch}.${ext}",
    "requestExecutionLevel": "user"
  },
  "appx": {
    "identityName": "viarotel.Escrcpy",
    "publisher": "CN=E67CC10B-A1BE-413C-BC3B-6E1137E0742D",
    "publisherDisplayName": "viarotel",
    "languages": ["zh-CN", "en-US", "zh-TW"]
  },
  "mac": {
    "icon": "logo.icns",
    "target": [
      {
        "target": "dmg",
        "arch": ["x64", "arm64"]
      },
      {
        "target": "zip",
        "arch": ["x64", "arm64"]
      }
    ],
    "artifactName": "${productName}-${version}-mac-${arch}.${ext}",
    "extraResources": {
      "from": "electron/resources/extra",
      "to": "extra",
      "filter": ["common", "mac", "mac-${arch}"]
    },
    "x64ArchFiles": "Contents/Resources/extra/**/*",
    "entitlementsInherit": "entitlements.mac.plist",
    "extendInfo": {
      "NSDocumentsFolderUsageDescription": "Application requests access to the user's Documents folder.",
      "NSDownloadsFolderUsageDescription": "Application requests access to the user's Downloads folder."
    },
    "type": "development",
    "notarize": false,
    "darkModeSupport": true,
    "hardenedRuntime": false
  },
  "linux": {
    "icon": "logo.icns",
    "maintainer": "viarotel@qq.com",
    "category": "Utility",
    "target": [
      {
        "target": "AppImage",
        "arch": ["x64", "arm64"]
      },
      {
        "target": "deb",
        "arch": ["x64", "arm64"]
      }
    ],
    "artifactName": "${productName}-${version}-linux-${arch}.${ext}",
    "extraResources": {
      "from": "electron/resources/extra",
      "to": "extra",
      "filter": ["common", "linux", "linux-${arch}"]
    }
  },
  "asar": true,
  "npmRebuild": true,
  "publish": {
    "provider": "github",
    "owner": "viarotel-org",
    "repo": "escrcpy",
    "updaterCacheDirName": "escrcpy-updater"
  }
}

{
  "compilerOptions": {
    "baseUrl": ".",
    "paths": {
      "$/*": ["src/*"],
      "$root/*": ["*"],
      "$electron/*": ["electron/*"],
      "$renderer/*": ["src/*"],
      "$control/*": ["control/*"]
    }
  },
  "exclude": ["node_modules", "dist", "dist-electron", "dist-release"],
  "include": ["src", "electron", "control"]
}

import nested from 'postcss-nested'
import postcssScss from 'postcss-scss'

export default {
  parser: postcssScss,
  plugins: [nested],
}

import transformerDirectives from '@unocss/transformer-directives'
import { presetShades } from '@viarotel-org/unocss-preset-shades'
import { defineConfig, presetWind } from 'unocss'
import { primaryColor } from './src/configs/index.js'

const presetMain = presetWind()

const presets = [presetMain, presetShades(primaryColor)]

export default defineConfig({
  // @ts-expect-error
  presets,
  transformers: [transformerDirectives()],
  theme: {
    colors: {
      // @ts-expect-error
      gray: presetMain?.theme?.colors?.neutral,
    },
    boxShadow: {
      'el': 'var(--el-box-shadow)',
      'el-light': 'var(--el-box-shadow-light)',
      'el-lighter': 'var(--el-box-shadow-lighter)',
      'el-dark': 'var(--el-box-shadow-dark)',
    },
  },
  shortcuts: {
    'inset-0': 'top-0 bottom-0 left-0 right-0',
    'inset-center':
      'top-1/2 left-1/2 transform -translate-y-1/2 -translate-x-1/2',
    'top-center': 'top-1/2 transform -translate-y-1/2',
    'left-center': 'left-1/2 transform -translate-x-1/2',
  },
})

import { resolve } from 'node:path'
import useI18n from '@intlify/unplugin-vue-i18n/vite'
import useVueRouter from 'unplugin-vue-router/vite'
import useVue from '@vitejs/plugin-vue'
import useUnoCSS from 'unocss/vite'
import { defineConfig, mergeConfig } from 'vite'

import { notBundle } from 'vite-plugin-electron/plugin'
import useElectron from 'vite-plugin-electron/simple'
import useRenderer from 'vite-plugin-electron-renderer'
import useSvg from 'vite-svg-loader'

import postcssConfig from './postcss.config.js'

import useAutoImports from './src/plugins/auto.js'

const alias = {
  $: resolve('src'),
  $root: resolve(),
  $renderer: resolve('src'),
  $electron: resolve('electron'),
  $control: resolve('control'),
}

function mergeCommon(config, { command = '' } = {}) {
  return mergeConfig(
    {
      resolve: {
        alias,
      },
      plugins: [...(command === 'serve' ? [notBundle()] : [])],
    },
    config,
  )
}

export default function (args) {
  return mergeCommon(
    defineConfig({
      build: {
        rollupOptions: {
          input: {
            main: resolve('index.html'),
            control: resolve('control/index.html'),
          },
        },
      },
      plugins: [
        useUnoCSS(),
        useSvg(),
        useVueRouter({
          exclude: ['src/pages/**/components'],
        }),
        useVue(),
        useI18n({
          include: [resolve('src/locales/languages/**')],
        }),
        useElectron({
          main: {
            entry: 'electron/main.js',
            vite: mergeCommon({}, args),
          },
          preload: {
            input: 'electron/preload.js',
            vite: mergeCommon({}, args),
          },
        }),
        useRenderer(),
        ...useAutoImports(),
      ],
      css: {
        postcss: postcssConfig,
      },
    }),
  )
}
