{
  "name": "react-native-typography",
  "version": "1.4.1",
  "description":
    "Pixel‚Äìperfect, native‚Äìlooking typographic styles for React Native",
  "main": "dist/index.js",
  "types": "dist/index.d.ts",
  "module": "src/index.js",
  "sideEffects": false,
  "files": ["src/", "dist/"],
  "directories": {
    "example": "example"
  },
  "scripts": {
    "build":
      "rm -rf dist && babel src --out-dir dist && cp ./src/index.d.ts ./dist",
    "format": "prettier --write '**/*.js'",
    "precommit": "lint-staged",
    "test": "echo \\\"Error: no test specified\\\" && exit 1"
  },
  "lint-staged": {
    "*.{js,json,css,md}": ["prettier --write", "git add"]
  },
  "repository": {
    "type": "git",
    "url": "git+https://github.com/hectahertz/react-native-typography.git"
  },
  "keywords": [
    "react-native",
    "react",
    "typography",
    "style",
    "styles",
    "typeface",
    "font"
  ],
  "author": "hectahertz <hectahertz@gmail.com>",
  "license": "MIT",
  "bugs": {
    "url": "https://github.com/hectahertz/react-native-typography/issues"
  },
  "homepage": "https://github.com/hectahertz/react-native-typography#readme",
  "dependencies": {},
  "peerDependencies": {
    "react": "*",
    "react-native": "*"
  },
  "devDependencies": {
    "babel-cli": "^6.26.0",
    "babel-preset-react-native": "^4.0.0",
    "husky": "^0.14.3",
