{
  "extends": "airbnb-base",
  "rules": {
    "no-empty": ["error", { "allowEmptyCatch": true }],
    "no-underscore-dangle": ["error", { "allow": ["_claim_names", "_claim_sources", "_matchedRouteName", "__dirname"] }],
    "no-await-in-loop": ["off"],
    "import/order": ["error", { "groups": ["builtin", "external", "internal", "parent", "sibling", "index"], "newlines-between": "always" }],
    "symbol-description": ["off"],
    "import/extensions": ["error", "ignorePackages"],
    "no-restricted-syntax": [
      "error",
      {
        "selector": "LabeledStatement",
        "message": "Labels are a form of GOTO; using them makes code confusing and hard to maintain and understand."
      },
      {
        "selector": "WithStatement",
        "message": "`with` is disallowed in strict mode because it makes code impossible to predict and optimize."
      }
    ],
    "max-len": [
      "error",
      100,
      2,
      {
        "ignoreUrls": true,
        "ignoreComments": true,
        "ignoreRegExpLiterals": true,
        "ignoreStrings": true,
        "ignoreTemplateLiterals": true
      }
    ]
  },
  "parserOptions": {
    "ecmaVersion": "latest"
  }
}

{
  "scripts": {
    "postchangelog": "sed -i '' -e 's/### \\[/## [/g' CHANGELOG.md"
  },
  "types": [
    {
      "type": "feat",
      "section": "Features",
      "hidden": false
    },
    {
      "type": "fix",
      "section": "Fixes",
      "hidden": false
    },
    {
      "type": "chore",
      "hidden": true
    },
    {
      "type": "docs",
      "section": "Documentation",
      "hidden": false
    },
    {
      "type": "style",
      "hidden": true
    },
    {
      "type": "refactor",
      "section": "Refactor",
      "hidden": false
    },
    {
      "type": "perf",
      "section": "Performance",
      "hidden": false
    },
    {
      "type": "test",
      "hidden": true
    }
  ]
}

