color: #666;
}

.flexContainer {
  width: 100%;
  height: 100vh;
}

.flexContainer > * {
  height: 100%;
  padding-top: 32px;
}

.rowParent, .columnParent{
  display: -webkit-box;
  display: -ms-flexbox;
  display: -webkit-flex;
  display: flex;
  -webkit-box-direction: normal;
  -webkit-box-orient: horizontal;
  -webkit-flex-direction: row;
  -ms-flex-direction: row;
  flex-direction: row;
  -webkit-flex-wrap: nowrap;
  -ms-flex-wrap: nowrap;
  flex-wrap: nowrap;
  -webkit-box-pack: start;
  -webkit-justify-content: flex-start;
  -ms-flex-pack: start;
  justify-content: flex-start;
  -webkit-align-content: stretch;
  -ms-flex-line-pack: stretch;
  align-content: stretch;
  -webkit-box-align: stretch;
  -webkit-align-items: stretch;
  -ms-flex-align: stretch;
  align-items: stretch;
}

.columnParent{
  -webkit-box-orient: vertical;
  -webkit-flex-direction: column;
  -ms-flex-direction: column;
  flex-direction: column;
}

.flexChild{
  -webkit-box-flex: 1;
  -webkit-flex: 1;
  -ms-flex: 1;
  flex: 1;
  -webkit-align-self: auto;
  -ms-flex-item-align: auto;
  align-self: auto;
}

.editorBox, .stackRow {
  flex: 3;
  position: relative;
}

.editorBox {
  background: #FDF6E3;
}

.codeColumn {
  flex: 0.5;
  margin-right: 20px;
  border-right: 2px #ddd solid;
}

.editor {
  min-height: 100%;
  width: 100%;
  font-family: monospace;
  font-size: 13px;
  line-height: 1.75;
  display: inline-block;
  white-space: pre;
  padding-left: 45px;
  color: #333;
  font-family: Monaco, Menlo, 'Ubuntu Mono', Consolas, source-code-pro, monospace;
  font-size: 12px;
  overflow-y: hidden;
  position: relative;
}

.editor:before {
  content: attr(data-lines);
  /*content: "1\a 2\A 3\A 4\A 5\A 6\A 7\A 8\A 9\A 10\A 11\A 12\A 13\A 14\A 15\A 16\A 17\A";*/
  position: absolute;
  top: 0;
  left: 0;
  width: 41px;
  padding-left: 7.5px;
  text-align: center;
  background: #FBF1D3;
  height: 100%;
  white-space: pre;
}

.ace-editor-wrapper {
  flex: 1;
}

.ace_editor {
  line-height: 1.75!important;
}

.html-scratchpad {
  padding: 10px;
  background: #eee;
  overflow: scroll;
  font-family: Helvetica Neue, Helvetica, Arial, sans-serif;
}

.webapi {
  padding: 5px;
  border: 2px #ddd solid;
  margin: 5px;
}

.webapi-code {
  font-family: monospace;
  height: 100%;
  vertical-align: middle;
  margin-right: 5px;
}


.webapi-code:before {
  content: '';
  display: inline-block;
  vertical-align: middle;
  height: 100%;
}

.webapi-timer > * {
  display: inline-block;
  vertical-align: middle;
  height: 44px;
}


@keyframes rota {
  0%   { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

@keyframes fill {
  0%        { opacity: 0; }
  50%, 100% { opacity: 1; }
}

@keyframes mask {
  0%        { opacity: 1; }
  50%, 100% { opacity: 0; }
}

@keyframes check {
  0%        { opacity: 0; }
  98%       { opacity: 0; }
  100%      { opacity: 1; }
}

@keyframes background {
  0% { background: rgba(148,255,148,0); }
  95% { background: rgba(148,255,148,0); }
  100% { background: rgba(148,255,148,1); }
}

.stopwatch-wrapper {
  width: 44px;
  height: 44px;
  position: relative;
  background: rgba(148,255,148,1);
  border-radius: 250px;
  animation: background 10s linear;
  overflow: hidden;
}

.stopwatch-wrapper:before {
  position: absolute;
  content: "";
  width: 50%;
  height: 28%;
  top: 35%;
  left: 25.5%;
  border-left: 7px white solid;
  border-bottom: 7px white solid;
  transform: rotate(-45deg);
  z-index: 500;
}

.stopwatch-pie {
  border-radius: 22px;
  width: 50%;
  height: 100%;
  position: absolute;
  border: 8px solid rgb(148,255,148);
}

.stopwatch-spinner {
  border-top-right-radius: 0;
  border-bottom-right-radius: 0;
  z-index: 200;
  border-right: none;
  animation: rota 10s linear;
  transform-origin: 100% 50%;
}

.stopwatch-filler {
  border-top-left-radius: 0;
  border-bottom-left-radius: 0;
  z-index: 100;
  border-left: none;
  animation: fill 10s steps(1, end);
  left: 50%;
  opacity: 1;
}

.stopwatch-mask {
  width: 50%;
  height: 100%;
  position: absolute;
  z-index: 300;
  opacity: 0;
  background: white;
  animation: mask 10s steps(1, end);
  border-radius: 250px 0 0 250px;
}

.stopwatch-spinner, .stopwatch-filler, .stopwatch-mask, .stopwatch-wrapper {
  animation-duration: 3s;
}


.callback-queue {
  margin-bottom: 20px;
  margin-right: 20px;
  overflow-y: scroll;
}
.callback-queue.render-queue {
  margin-bottom: 5px;
}

.callback {
  font-family: monospace;
  padding: 10px;
  display: inline-block;
  margin-right: 10px;
  border: 2px #ddd solid;
  width: 200px;
  overflow: scroll;
}

.callback-active {
  background: rgb(220,224,255);
}

.stackBox {
  position: relative;
  margin-right: 20px;
}

.stack-wrapper {
  width: 244px;
  padding: 20px;
  margin-bottom: 86px;
  margin-top: 20px;
}

.stack-wrapper, .callback-queue, .webapis {
  border: 2px #ddd dashed;
  position: relative;
  padding: 25px 10px 10px 10px;
}

.stack-wrapper:before,
.callback-queue:before,
.webapis:before {
  font-family: Helvetica Neue, Arial, sans-serif;
  position: absolute;
  top: 0;
  left: 0;
  padding: 5px;
  color: #999;
}

.webapis {
  margin-top: 20px;
  margin-bottom: 86px;
  margin-right: 20px;
}

.webapis:before {
  content: "Web Apis";
}

.stack-wrapper:before {
  content: "Call Stack";
}

.callback-queue:before {
  content: "Callback Queue";
}

.callback-queue.render-queue:before {
  content: "Render Queue";
}

.stack {
  position: absolute;
  bottom: 0px;
  left: 0;
  padding-left: 10px;
}
.stackBox > .spinner-wrapper {
  position: absolute;
  bottom: 0;
  left: 0;
  margin-bottom: 5px;
}

.stack-item {
  font-family: monospace;
  padding: 10px;
  margin: 5px;
  border: 2px #ddd solid;
  width: 220px;
  margin: 10px auto;
}


.spinner-wrapper {
  height: 76px;
  width: 76px;
  position: relative;
  padding: 9px;
  transform: rotate(0deg);
  margin-left: 82px;
}

.spinner-wrapper-transition {
  transform: rotate(180deg);
  transition: transform 0.5s linear;
}

.spinner-circle {
  height: 100%;
  width: 100%;
  border-radius: 30px;
  border: 6px coral solid;
}

.spinner-arrow {
  width: 0px;
  height: 0px;
  border-style: solid;
  border-width: 0 16px 16px 16px;
  border-color: transparent transparent white transparent;
  position: absolute;
}

.spinner-arrow:after {
  content: "";
  width: 0px;
  height: 0px;
  border-style: solid;
  border-width: 0 12px 12px 12px;
  border-color: transparent transparent coral transparent;
  position: absolute;
}

.spinner-arrow-left:after {
  left: -12px;
  top: 4px;
}

.spinner-arrow-left {
  left: -2.5px;
  top: calc(50% - 9px);
}

.spinner-arrow-right {
  right: -2px;
  top: calc(50% - 6px);
  border-width: 16px 16px 0px 16px;
  border-color: white transparent transparent transparent;
}

.spinner-arrow-right:after {
  border-width: 12px 12px 0px 12px;
  border-color: coral transparent transparent transparent;

  left: -12px;
  bottom: 4px;
}

.code-node {
}

.code-node:empty {
  display: none;
}

.code-node.running {
  background: rgba(236, 117, 74, 0.25);
} 


/*********************************/
.tr-webapis-enter {
  background: rgb(255,255,194);
  transition: background 0.2s linear;
}

.tr-webapis-enter.tr-webapis-enter-active {
  background: white;
}

.tr-webapis-leave {
  background: white;
  transition: background 0.01s linear;
}

.tr-webapis-leave.tr-webapis-leave-active {
  background: white;
}

.tr-webapi-spawn {
  background: white;
  transition: background 0.1s linear;
}

.tr-webapi-spawn.tr-webapi-spawn-active {
  background: rgb(255, 175, 146);
}

/*********************************/
