}

export function liveAltTab(meta_window, space, { _display, _screen, binding }) {
    let tabPopup = new LiveAltTab(binding.is_reversed(), false);
    tabPopup.show(binding.is_reversed(), binding.get_name(), binding.get_mask());
}

export function liveAltTabScratch(meta_window, space, { _display, _screen, binding }) {
    let tabPopup = new LiveAltTab(binding.is_reversed(), true);
    tabPopup.show(binding.is_reversed(), binding.get_name(), binding.get_mask());
}

export const LiveAltTab = GObject.registerClass(
    class LiveAltTab extends AltTab.WindowSwitcherPopup {
        _init(reverse, scratchOnly) {
            this.reverse = reverse;
            this.scratchOnly = scratchOnly;
            this.space = Tiling.spaces.selectedSpace;
            this.monitor = Tiling.spaces.selectedSpace.monitor;
            super._init();
        }

        _getWindowList(reverse) {
            let tabList = global.display.get_tab_list(
                Meta.TabList.NORMAL_ALL,
                switcherSettings.get_boolean('current-workspace-only')
                    ? global.workspace_manager.get_active_workspace() : null)
                .filter(w => !Scratch.isScratchWindow(w));

            let scratch = Scratch.getScratchWindows();

            if (this.scratchOnly) {
                return reverse ? scratch.reverse() : scratch;
            }
            else if (Scratch.isScratchWindow(global.display.focus_window)) {
                // Access scratch windows in mru order with shift-super-tab
                return scratch.concat(reverse ? tabList.reverse() : tabList);
            } else {
                return tabList.concat(reverse ? scratch.reverse() : scratch);
            }
        }

        _initialSelection(backward, actionName) {
            this.space.startAnimate();
            let workArea = Main.layoutManager.getWorkAreaForMonitor(this.monitor.index);
            let fog = new Clutter.Actor({
                x: workArea.x, y: workArea.y,
                width: workArea.width, height: workArea.height,
                opacity: 0, background_color: Utils.color_from_string("black")[1],
            });

            // this.blur = new Clutter.BlurEffect();
            // this.space.cloneContainer.add_effect(this.blur);
            this.space.setSelectionInactive();

            Main.uiGroup.insert_child_above(fog, global.window_group);
            Easer.addEase(fog, {
                time: Settings.prefs.animation_time,
                opacity: 100,
            });
            this.fog = fog;

            super._initialSelection(backward, actionName);
        }

        _keyPressHandler(keysym, mutterActionId) {
            if (keysym === Clutter.KEY_Escape)
                return Clutter.EVENT_PROPAGATE;
            // After the first super-tab the mutterActionId we get is apparently
            // SWITCH_APPLICATIONS so we need to case on those too.
            switch (mutterActionId) {
            case Meta.KeyBindingAction.SWITCH_APPLICATIONS:
                mutterActionId = Meta.KeyBindingAction.SWITCH_WINDOWS;
                break;
            case Meta.KeyBindingAction.SWITCH_APPLICATIONS_BACKWARD:
                mutterActionId = Meta.KeyBindingAction.SWITCH_WINDOWS_BACKWARD;
                break;
            case Keybindings.idOf('live-alt-tab'):
                mutterActionId = Meta.KeyBindingAction.SWITCH_WINDOWS;
                break;
            case Keybindings.idOf('live-alt-tab-backward'):
                mutterActionId = Meta.KeyBindingAction.SWITCH_WINDOWS_BACKWARD;
                break;
            case Keybindings.idOf('live-alt-tab-scratch'):
                mutterActionId = Meta.KeyBindingAction.SWITCH_WINDOWS;
                break;
            case Keybindings.idOf('live-alt-tab-scratch-backward'):
                mutterActionId = Meta.KeyBindingAction.SWITCH_WINDOWS_BACKWARD;
                break;
            }
            // let action = Keybindings.byId(mutterActionId);
            // if (action && action.options.activeInNavigator) {
            //     let space = Tiling.spaces.selectedSpace;
            //     let metaWindow = space.selectedWindow;
            //     action.handler(metaWindow, space);
            //     return true;
            // }
            return super._keyPressHandler(keysym, mutterActionId);
        }

        _select(num) {
            let to = this._switcherList.windows[num];

            this.clone && this.clone.destroy();
            this.clone = null;

            let actor = to.get_compositor_private();
            actor.remove_clip();
            let frame = to.get_frame_rect();
            let clone = new Clutter.Clone({ source: actor });
            clone.position = actor.position;

            let space = Tiling.spaces.spaceOfWindow(to);
            if (space.indexOf(to) !== -1) {
                clone.x = Tiling.ensuredX(to, space) + space.monitor.x;
                clone.x -= frame.x - actor.x;
            }

            this.clone = clone;
            Main.uiGroup.insert_child_above(clone, this.fog);

            // Tiling.ensureViewport(to, space);
            this._selectedIndex = num;
            this._switcherList.highlight(num);
        }

        _finish() {
            this.was_accepted = true;
            super._finish();
        }

        _itemEnteredHandler() {
            // The item-enter (mouse hover) event is triggered even after a item is
            // accepted. This can cause _select to run on the item below the pointer
            // ensuring the wrong window.
            if (!this.was_accepted) {
                // eslint-disable-next-line prefer-rest-params
                super._itemEnteredHandler.apply(this, arguments);
            }
        }

        _onDestroy() {
            super._onDestroy();
            console.debug('#preview', 'onDestroy', this.was_accepted);
            Easer.addEase(this.fog, {
                time: Settings.prefs.animation_time,
                opacity: 0,
                onStopped: () => {
                    this.fog.destroy();
                    this.fog = null;
                    // this.space.cloneContainer.remove_effect(this.blur);
                    this.clone && this.clone.destroy();
                    this.clone = null;
                    this.space.moveDone();
                },
            });
            let index = this.was_accepted ? this._selectedIndex : 0;
            let to = this._switcherList.windows[index];
            Tiling.focus_handler(to);
            let actor = to.get_compositor_private();
            if (this.was_accepted) {
                actor.x = this.clone.x;
                actor.y = this.clone.y;
            }
            actor.set_scale(1, 1);
        }
    });

import Clutter from 'gi://Clutter';
import St from 'gi://St';
import Pango from 'gi://Pango';

import * as Main from 'resource:///org/gnome/shell/ui/main.js';

import { Settings, Utils, Lib } from './imports.js';
import { Easer } from './utils.js';

export function calcOffset(metaWindow) {
    let buffer = metaWindow.get_buffer_rect();
    let frame = metaWindow.get_frame_rect();
    let x_offset = frame.x - buffer.x;
    let y_offset = frame.y - buffer.y;
    return [x_offset, y_offset];
}

export class Minimap extends Array {
    constructor(space, monitor) {
        super();
        this.space = space;
        // initial fade
        space.getWindows()
            .forEach(w => {
                w.clone?.shade?.show();
                if (w === space.selectedWindow) {
                    return;
                }

                Easer.addEase(w.clone?.shade, {
                    time: Settings.prefs.animation_time,
                    opacity: Settings.prefs.minimap_shade_opacity,
                });
            });

        this.monitor = monitor;
        let actor = new St.Widget({
            name: 'minimap',
            style_class: 'paperwm-minimap switcher-list',
        });
        this.actor = actor;
        actor.height = space.height * 0.20;

        let highlight = new St.Widget({
            name: 'minimap-selection',
            style_class: 'paperwm-minimap-selection item-box',
        });
        highlight.add_style_pseudo_class('selected');
        this.highlight = highlight;
        let label = new St.Label({ style_class: 'paperwm-minimap-label' });
        label.clutter_text.ellipsize = Pango.EllipsizeMode.END;
        this.label = label;

        let clip = new St.Widget({ name: 'container-clip' });
        this.clip = clip;
        let container = new St.Widget({ name: 'minimap-container' });
        this.container = container;

        actor.add_child(highlight);
        actor.add_child(label);
        actor.add_child(clip);
        clip.add_child(container);
        clip.set_position(12 + Settings.prefs.window_gap, 12 + Math.round(1.5 * Settings.prefs.window_gap));
        highlight.y = clip.y - 10;
        Main.uiGroup.add_child(this.actor);
        this.actor.opacity = 0;
        this.createClones();

        this.signals = new Utils.Signals();
        this.signals.connect(space, 'select', this.select.bind(this));
        this.signals.connect(space, 'window-added', this.addWindow.bind(this));
        this.signals.connect(space, 'window-removed', this.removeWindow.bind(this));
        this.signals.connect(space, 'layout', this.layout.bind(this));
        this.signals.connect(space, 'swapped', this.swapped.bind(this));
        this.signals.connect(space, 'full-layout', this.reset.bind(this));

        this.layout();
    }

    static get [Symbol.species]() { return Array; }

    reset() {
        this.splice(0, this.length).forEach(c => c.forEach(x => x.destroy()));
        this.createClones();
        this.layout();
    }

    addWindow(space, metaWindow, index, row) {
        let clone = this.createClone(metaWindow);
        if (row !== undefined && this[index]) {
            let column = this[index];
            column.splice(row, 0, clone);
        } else {
            row = row || 0;
            this.splice(index, 0, [clone]);
        }
        this.layout();
    }

    removeWindow(space, metaWindow, index, row) {
        let clone = this[index][row];
        let column = this[index];
        column.splice(row, 1);
        if (column.length === 0)
            this.splice(index, 1);
        // this.container.remove_child(clone);
        Utils.actor_remove_child(this.container, clone);
        this.layout();
    }

    swapped(space, index, targetIndex, row, targetRow) {
        let column = this[index];
        Lib.swap(this, index, targetIndex);
        Lib.swap(column, row, targetRow);
        this.layout();
    }

    show(animate) {
        if (this.destroyed)
            return;

        // if minimap_scale preference is 0, then don't show
        if (Settings.prefs.minimap_scale <= 0) {
            return;
        }

        this.layout();
        let time = animate ? Settings.prefs.animation_time : 0;
        this.actor.show();
        Easer.addEase(this.actor,
            { opacity: 255, time, mode: Clutter.AnimationMode.EASE_OUT_EXPO });
    }

    hide(animate) {
        if (this.destroyed)
            return;
        let time = animate ? Settings.prefs.animation_time : 0;
        Easer.addEase(this.actor,
            {
                opacity: 0, time, mode: Clutter.AnimationMode.EASE_OUT_EXPO,
                onComplete: () => this.actor.hide(),
            });
    }

    createClones() {
        for (let column of this.space) {
            this.push(column.map(this.createClone.bind(this)));
        }
    }

    createClone(mw) {
        const windowActor = mw.get_compositor_private();
        const clone = new Clutter.Clone({ source: windowActor });
        const container = new Clutter.Actor({
            // layout_manager: new WindowCloneLayout(this),
            name: "window-clone-container",
        });
        clone.meta_window = mw;
        container.clone = clone;
        container.meta_window = mw;
        container.add_child(clone);
        this.container.add_child(container);
        return container;
    }

    _allocateClone(container) {
        let clone = container.clone;
        let meta_window = clone.meta_window;
        let buffer = meta_window.get_buffer_rect();
        let frame = meta_window.get_frame_rect();
        let scale = Settings.prefs.minimap_scale;
        clone.set_size(buffer.width * scale, buffer.height * scale - Settings.prefs.window_gap);
        clone.set_position((buffer.x - frame.x) * scale, (buffer.y - frame.y) * scale);
        container.set_size(frame.width * scale, frame.height * scale);
    }

    layout() {
        if (this.destroyed)
            return;
        let gap = Settings.prefs.window_gap;
        let x = 0;
        for (let column of this) {
            let y = 0, w = 0;
            for (let c of column) {
                c.set_position(x, y);
                this._allocateClone(c);
                w = Math.max(w, c.width);
                y += c.height;
            }
            x += w + gap;
        }

        this.clip.width = Math.min(this.container.width,
            this.monitor.width - this.clip.x * 2 - 24);
        this.actor.width = this.clip.width + this.clip.x * 2;
        this.clip.set_clip(0, 0, this.clip.width, this.clip.height);
        this.label.set_style(`max-width: ${this.clip.width}px;`);
        this.actor.set_position(
            this.monitor.x + Math.floor((this.monitor.width - this.actor.width) / 2),
            this.monitor.y + Math.floor((this.monitor.height - this.actor.height) / 2));
        this.select();
    }

    select() {
        let position = this.space.positionOf();
        let highlight = this.highlight;
        if (!position) {
            this.highlight.hide();
            return;
        }
        let [index, row] = position;
        if (!(index in this && row in this[index]))
            return;
        highlight.show();
        let clip = this.clip;
        let container = this.container;
        let label = this.label;
        let selected = this[index][row];
        if (!selected)
            return;

        this.space.getWindows().forEach(w => {
            const shade = w.clone.shade;
            // if selected
            if (w === selected.meta_window) {
                shade.opacity = 0;
                return;
            }

            // others
            Easer.addEase(shade, {
                time: Settings.prefs.animation_time,
                opacity: Settings.prefs.minimap_shade_opacity,
            });
        });

        label.text = selected.meta_window.title;

        if (selected.x + selected.width + container.x > clip.width) {
            // Align right edge of selected with the clip
            container.x = clip.width - (selected.x + selected.width);
            container.x -= 500; // margin
        }
        if (selected.x + container.x < 0) {
            // Align left edge of selected with the clip
            container.x = -selected.x;
            container.x += 500; // margin
        }

        if (container.x + container.width < clip.width)
            container.x = clip.width - container.width;

        if (container.x > 0)
            container.x = 0;

        let gap = Settings.prefs.window_gap;
        highlight.x = Math.round(
            clip.x + container.x + selected.x - gap / 2);
        highlight.y = Math.round(
            clip.y + selected.y - Settings.prefs.window_gap);
        highlight.set_size(Math.round(selected.width + gap),
            Math.round(Math.min(selected.height, this.clip.height + gap) + gap));

        let x = highlight.x + (highlight.width - label.width) / 2;
        if (x + label.width > clip.x + clip.width)
            x = clip.x + clip.width - label.width + 5;
        if (x < 0)
            x = clip.x - 5;

        label.set_position(Math.round(x), this.clip.y + this.clip.height + 8);
        this.actor.height = this.clip.y + this.clip.height + 40;
    }

    destroy() {
        if (this.destroyed)
            return;
        this.space.getWindows()
            .forEach(w => {
                Easer.addEase(w.clone?.shade, {
                    time: Settings.prefs.animation_time,
                    opacity: 0,
                    onComplete: () => w.clone?.shade.hide(),
                });
            });
        this.destroyed = true;
        this.signals.destroy();
        this.signals = null;
        this.splice(0, this.length);
        this.actor.destroy();
        this.actor = null;
    }
}

import Clutter from 'gi://Clutter';
import GLib from 'gi://GLib';
import Meta from 'gi://Meta';
import St from 'gi://St';

import * as Main from 'resource:///org/gnome/shell/ui/main.js';

import {
    Utils, Tiling, Keybindings, Topbar,
    Scratch, Minimap, Settings
} from './imports.js';

/**
  Navigation and previewing functionality.

  This is a somewhat messy tangle of functionality relying on
  `SwitcherPopup.SwitcherPopup` when we really should just take full control.
 */

const { signals: Signals } = imports;
const display = global.display;

export let navigating; // exported
let grab, dispatcher, signals;
export function enable() {
    navigating = false;

    /**
     * Stop navigation before before/after overview. Avoids a corner-case issue
     * in multimonitors where workspaces can get snapped to another monitor.
     */
    signals = new Utils.Signals();
    signals.connect(Main.overview, 'showing', () => {
        finishNavigation();
    });
    signals.connect(Main.overview, 'hidden', () => {
        finishNavigation();
    });
}

export function disable() {
    navigating = false;
    grab = null;
    dispatcher = null;
    signals.destroy();
    signals = null;
    index = null;
}

export function primaryModifier(mask) {
    if (mask === 0)
        return 0;

    let primary = 1;
    while (mask > 1) {
        mask >>= 1;
        primary <<= 1;
    }
    return primary;
}

/**
   Handle catching keyevents and dispatching actions

   Adapted from SwitcherPopup, without any visual handling.
 */
class ActionDispatcher {
    /** @type {import('@gi-types/clutter10').GrabState} */
    mode;

    constructor() {
        console.debug("#dispatch", "created");
        this.signals = new Utils.Signals();
        this.actor = Tiling.spaces.spaceContainer;
        this.actor.reactive = true;
        this.navigator = getNavigator();

        if (grab) {
            console.debug("#dispatch", "already in grab");
            return;
        }

        // grab = stage.grab(this.actor)
        grab = Main.pushModal(this.actor);
        // We expect at least a keyboard grab here
        if ((grab.get_seat_state() & Clutter.GrabState.KEYBOARD) === 0) {
            console.error("Failed to grab modal");
            throw new Error('Could not grab modal');
        }

        this.signals.connect(this.actor, 'key-press-event', this._keyPressEvent.bind(this));
        this.signals.connect(this.actor, 'key-release-event', this._keyReleaseEvent.bind(this));

        this.keyPressCallbacks = [];
        this.keyReleaseCallbacks = [];

        this._noModsTimeoutId = null;
        this._doActionTimeout = null;
    }

    /**
     * Adds a signal to this dispatcher.  Will be destroyed when this
     * dispatcher is destroyed.
     */
    addKeypressCallback(handler) {
        this.keyPressCallbacks.push(handler);
        return this;
    }

    /**
     * Adds a signal to this dispatcher.  Will be destroyed when this
     * dispatcher is destroyed.
     */
    addKeyReleaseCallback(handler) {
        this.keyReleaseCallbacks.push(handler);
        return this;
    }

    show(_backward, binding, mask) {
        this._modifierMask = primaryModifier(mask);
        this.navigator = getNavigator();
        Topbar.fixTopBar();
        let actionId = Keybindings.idOf(binding);
        if (actionId === Meta.KeyBindingAction.NONE) {
            try {
                // Check for built-in actions
                actionId = Meta.prefs_get_keybinding_action(binding);
            } catch (e) {
                console.debug("Couldn't resolve action name");
                return false;
            }
        }

        this._doAction(actionId);

        // There's a race condition; if the user released Alt before
        // we got the grab, then we won't be notified. (See
        // https://bugzilla.gnome.org/show_bug.cgi?id=596695 for
        // details.) So we check now. (straight from SwitcherPopup)
        if (this._modifierMask) {
            let [, , mods] = global.get_pointer();
            if (!(mods & this._modifierMask)) {
                this._finish(global.get_current_time());
                return false;
            }
        } else {
            this._resetNoModsTimeout();
        }

        return true;
    }

    _resetNoModsTimeout() {
        Utils.timeout_remove(this._noModsTimeoutId);
        this._noModsTimeoutId = GLib.timeout_add(
            GLib.PRIORITY_DEFAULT,
            0, () => {
                this._finish(global.get_current_time());
                this._noModsTimeoutId = null;
                return false; // stops timeout recurrence
            });
    }

    _keyPressEvent(_actor, event) {
        if (!this._modifierMask) {
            this._modifierMask = primaryModifier(event.get_state());
        }
        let keysym = event.get_key_symbol();
        let action = global.display.get_keybinding_action(
            event.get_key_code(),
            event.get_state());

        // run callbacks and if any return true, stop bubbling
        if (this.keyPressCallbacks.some(callback => {
            return callback(this._modifierMask, keysym, event);
        })) {
            return Clutter.EVENT_STOP;
        }

        // Popping the modal on keypress doesn't work properly, as the release
        // event will leak to the active window. To work around this we initate
        // visual destruction on key-press and signal to the release handler
        // that we should destroy the dispactcher too
        // https://github.com/paperwm/PaperWM/issues/70
        if (keysym === Clutter.KEY_Escape) {
            this._destroy = true;
            getNavigator().accept();
            getNavigator().destroy();
            return Clutter.EVENT_STOP;
        }

        this._doAction(action);

        return Clutter.EVENT_STOP;
    }

    _keyReleaseEvent(_actor, event) {
        if (this._destroy) {
            dismissDispatcher(Clutter.GrabState.KEYBOARD);
        }

        if (this._modifierMask) {
            let [, , mods] = global.get_pointer();
            let state = mods & this._modifierMask;

            if (state === 0)
                this._finish(event.get_time());
        } else {
            this._resetNoModsTimeout();
        }

        this.keyReleaseCallbacks.forEach(callback => callback());
        return Clutter.EVENT_STOP;
    }

    _doAction(mutterActionId) {
        let action = Keybindings.byId(mutterActionId);
        let space = Tiling.spaces.selectedSpace;
        let metaWindow = space.selectedWindow;
        const nav = getNavigator();

        if (mutterActionId === Meta.KeyBindingAction.MINIMIZE) {
            metaWindow.minimize();
        } else if (action && action.options.activeInNavigator) {
            // action is performed while navigator is open (e.g. switch-left)
            if (!metaWindow && (action.options.mutterFlags & Meta.KeyBindingFlags.PER_WINDOW)) {
                return;
            }

            if (!Tiling.inGrab && action.options.opensMinimap) {
                nav.showMinimap(space);
            }
            action.handler(metaWindow, space, { navigator: this.navigator });
            if (space !== Tiling.spaces.selectedSpace) {
                this.navigator.minimaps.forEach(m => typeof m === 'number'
                    ? Utils.timeout_remove(m) : m.hide());
            }
            if (Tiling.inGrab && !Tiling.inGrab.dnd && Tiling.inGrab.window) {
                Tiling.inGrab.beginDnD();
            }
        } else if (action) {
            // closes navigator and action is performed afterwards
            // (e.g. switch-monitor-left)
            this._resetNoModsTimeout();
            this._doActionTimeout = GLib.timeout_add(GLib.PRIORITY_DEFAULT, 0, () => {
                action.handler(metaWindow, space);
                this._doActionTimeout = null;
                return false; // on return false destroys timeout
            });
        }
    }

    _finish(_timestamp) {
        let nav = getNavigator();
        nav.accept();
        !this._destroy && nav.destroy();
        dismissDispatcher(Clutter.GrabState.KEYBOARD);
        let space = Tiling.spaces.selectedSpace;
        let metaWindow = space.selectedWindow;
        if (metaWindow) {
            if (!metaWindow.appears_focused) {
                space.setSelectionInactive();
            }
        }
    }

    destroy() {
        Utils.timeout_remove(this._noModsTimeoutId);
        this._noModsTimeoutId = null;
        Utils.timeout_remove(this._doActionTimeout);
        this._doActionTimeout = null;

        try {
            if (grab) {
                Main.popModal(grab);
                grab = null;
            }
        } catch (e) {
            console.debug("Failed to release grab: ", e);
        }

        this.actor.reactive = false;
        this.signals.destroy();
        this.signals = null;
        // We have already destroyed the navigator
        getNavigator().destroy();
        dispatcher = null;
    }
}

let index = 0;
export let navigator;
class NavigatorClass {
    constructor() {
        console.debug("#navigator", "nav created");

        /**
         * Hint for using take window mode (used in `takeWindow`).
         */
        this.takeHint = new St.Label({ style_class: 'take-window-hint' });
        this.takeHint.clutter_text.set_markup(
            `<i>â€¢ press <span foreground="#6be67b">spacebar</span> to return the last taken window</i>
<i>â€¢ press <span foreground="#6be67b">tab</span> to cycle forward through taken windows</i>
<i>â€¢ press <span foreground="#6be67b">shift+tab</span> to cycle backward through taken windows</i>
<i>â€¢ press <span foreground="#6be67b">q</span> to close all taken windows</i>`
        );

        navigating = true;

        this.was_accepted = false;
        this.index = index++;

        this._block = Main.wm._blockAnimations;
        Main.wm._blockAnimations = true;
        // Meta.disable_unredirect_for_screen(screen);
        this.space = Tiling.spaces.activeSpace;

