            mode = mode ?? Settings.OpenWindowPositions.RIGHT;
            this.positionMode = mode;
            this._icon.setMode(mode);
            return this;
        }

        _onClicked(_actor, _event) {
            switchToNextOpenPositionMode();
            return Clutter.EVENT_PROPAGATE;
        }
    }
);

export const WorkspaceMenu = GObject.registerClass(
    class WorkspaceMenu extends panelMenu.Button {
        _init() {
            super._init(0.5, 'Workspace', false);

            this.name = 'workspace-button';

            let scale = display.get_monitor_scale(Main.layoutManager.primaryIndex);
            this.label = new St.Label({
                y_align: Clutter.ActorAlign.CENTER,
                // Avoid moving the menu on short names
                // TODO: update on scale changes
                min_width: 60 * scale,
            });

            this.setName(Meta.prefs_get_workspace_name(workspaceManager.get_active_workspace_index()));

            this.add_child(this.label);

            this.signals = new Utils.Signals();
            // eslint-disable-next-line no-undef
            this.signals.connect(global.window_manager,
                'switch-workspace',
                this.workspaceSwitched.bind(this));

            this.menu.addMenuItem(new popupMenu.PopupSeparatorMenuItem('Workspace Settings'));

            this.entry = new PopupMenuEntry(this.label.text);
            this.menu.addMenuItem(this.entry);
            let changed = () => {
                let name = this.entry.label.text;
                let space = Tiling.spaces.activeSpace;
                space.settings.set_string('name', name);
                this.setName(name);
            };
            this.signals.connect(this.entry.label.clutter_text, 'text-changed',
                changed);

            // this._zenItem = new popupMenu.PopupSwitchMenuItem('Hide top bar', false);
            // this.menu.addMenuItem(this._zenItem);
            // this._zenItem.connect('toggled', item => {
            //     Tiling.spaces.selectedSpace.settings.set_boolean('show-top-bar', !item.state);
            // });

            this.menu.addMenuItem(new popupMenu.PopupSeparatorMenuItem());

            this._prefItem = new popupMenu.PopupImageMenuItem('Workspace preference', 'preferences-system-symbolic');
            this.menu.addMenuItem(this._prefItem);

            // this.prefsIcon = createButton('preferences-system-symbolic', 'workspace preference');
            // this.prevIcon = createButton('go-previous-symbolic', 'previous workspace setting');
            // this.nextIcon = createButton('go-next-symbolic', 'next workspace setting');

            this._prefItem.connect('activate', () => {
                this.menu.close(true);
                let wi = workspaceManager.get_active_workspace_index();
                let temp_file = Gio.File.new_for_path(GLib.get_tmp_dir()).get_child('paperwm.workspace');
                temp_file.replace_contents(wi.toString(), null, false, Gio.FileCreateFlags.REPLACE_DESTINATION, null);
                openPrefs();
            });

            // this.iconBox = new St.BoxLayout();
            // this.menu.box.add(this.iconBox);

            // this.iconBox.add(this.prefsIcon, { expand: true, x_fill: false });

            // this.entry.actor.width = this.colors.actor.width;
            // this.colors.entry.actor.width = this.colors.actor.width;
            this.state = "NORMAL";
        }

        _finishWorkspaceSelect() {
            this.state = "NORMAL";
            this._enterbox.destroy();
            delete this.selected;
            delete this._enterbox;
            delete this._navigator;
        }

        _onEvent(actor, event) {
            if (!this.menu) {
                console.warn("?? no menu ??");
                Utils.print_stacktrace();
                return Clutter.EVENT_PROPAGATE;
            }

            if (this.state === "MENU" && !this.menu.isOpen) {
                this.state = "NORMAL";
            }

            let type = event.type();

            if (type === Clutter.EventType.TOUCH_END ||
                type === Clutter.EventType.BUTTON_RELEASE) {
                if (Navigator.navigating) {
                    Navigator.getNavigator().finish();
                } else {
                    if (this.menu.isOpen) {
                        this.menu.toggle();
                    } else if (event.get_button() === Clutter.BUTTON_SECONDARY) {
                        this.menu.toggle();
                    } else {
                        Main.overview.toggle();
                    }
                    this.state = this.menu.isOpen ? "MENU" : "NORMAL";
                }
                return Clutter.EVENT_PROPAGATE;
            }

            if (Main.overview.visible) {
                return Clutter.EVENT_PROPAGATE;
            }

            if (["NORMAL", "SCROLL"].includes(this.state) &&
                type === Clutter.EventType.SCROLL) {
                const direction = event.get_scroll_direction();
                switch (direction) {
                case Clutter.ScrollDirection.DOWN:
                    Tiling.spaces.selectSequenceSpace(Meta.MotionDirection.DOWN);
                    Navigator.getNavigator().finish();
                    break;
                case Clutter.ScrollDirection.UP:
                    Tiling.spaces.selectSequenceSpace(Meta.MotionDirection.UP);
                    Navigator.getNavigator().finish();
                    break;
                }
            }

            return Clutter.EVENT_PROPAGATE;
        }

        vfunc_event(event) {
            this._onEvent(null, event);
        }

        // WorkspaceMenu.prototype._onOpenStateChanged = function
        _onOpenStateChanged(menu, open) {
            if (!open)
                return;

            let space = Tiling.spaces.activeSpace;
            this.entry.label.text = space.name;
            GLib.idle_add(GLib.PRIORITY_DEFAULT, this.entry.activate.bind(this.entry));

            // this._zenItem._switch.setToggleState(!space.showTopBar);
        }

        workspaceSwitched(wm, fromIndex, toIndex) {
            updateWorkspaceIndicator(toIndex);
        }

        destroy() {
            this.signals.destroy();
            this.signals = null;
            super.destroy();
        }

        setName(name) {
            this.label.text = name;
        }
    });

/**
 * Returns monitor where panel is currently on.
 * @returns Monitor
 */
export function panelMonitor() {
    return Main.layoutManager.primaryMonitor;
}

/**
 * Returns space where panel is currently on.
 * @returns Tiling.Space
 */
export function panelSpace() {
    return Tiling?.spaces?.monitors?.get(panelMonitor());
}

export function setNoBackgroundStyle() {
    if (Settings.prefs.disable_topbar_styling) {
        return;
    }

    removeStyles();
    Main.panel.add_style_class_name('background-clear');
}

export function setTransparentStyle() {
    if (Settings.prefs.disable_topbar_styling) {
        return;
    }

    removeStyles();
    Main.panel.add_style_class_name('topbar-transparent-background');
}

export function removeStyles() {
    ['background-clear', 'topbar-transparent-background'].forEach(s => {
        Main.panel.remove_style_class_name(s);
    });
}

/**
 * Applies correct style based on whether we use the windowPositionBar or not.
 */
export function fixStyle() {
    const space = panelSpace();
    if (
        Settings.prefs.show_window_position_bar &&
        (space?.showPositionBar ?? true)
    ) {
        setNoBackgroundStyle();
    }
    else {
        setTransparentStyle();
    }
}

export function fixTopBar() {
    const space = panelSpace();
    if (!space)
        return;

    const normal = !Main.overview.visible && !Tiling.inPreview;
    // selected is current (tiled) selected window (can be different to focused window)
    const selected = space.selectedWindow;
    const focused = display.focus_window;
    const focusIsFloatOrScratch = focused && (space.isFloating(focused) || Scratch.isScratchWindow(focused));
    // check if is currently fullscreened (check focused-floating, focused-scratch, and selected/tiled window)
    const fullscreen = focusIsFloatOrScratch ? focused.fullscreen : selected && selected.fullscreen;

    if (normal && !space.showTopBar) {
        hideTopBar();
    }
    else if (normal && fullscreen) {
        hideTopBar();
    }
    else {
        showTopBar();
    }
}

export function showTopBar() {
    panelBox.show();
}

export function hideTopBar() {
    panelBox.hide();
}

export function fixWorkspaceIndicator() {
    const show = Settings.prefs.show_workspace_indicator;
    if (show) {
        Main.panel.statusArea.activities.hide();
        menu.show();
    }
    else {
        menu.hide();
        Main.panel.statusArea.activities.show();
    }
}

export function fixFocusModeIcon() {
    Settings.prefs.show_focus_mode_icon ? focusButton.show() : focusButton.hide();
    Tiling.spaces.forEach(s => s.showFocusModeIcon());
}

export function fixOpenPositionIcon() {
    Settings.prefs.show_open_position_icon ? openPositionButton.show() : openPositionButton.hide();
}

/**
   Override the activities label with the workspace name.
   let workspaceIndex = 0
*/
export function updateWorkspaceIndicator(index) {
    let spaces = Tiling.spaces;
    let space = spaces?.spaceOf(workspaceManager.get_workspace_by_index(index));
    if (space && space.monitor === panelMonitor()) {
        setWorkspaceName(space.name);

        // also update focus mode
        focusButton.setFocusMode(space.focusMode);
    }
}

/**
 * Refreshes topbar workspace indicator.
 */
export function refreshWorkspaceIndicator() {
    const space = panelSpace();
    if (space) {
        updateWorkspaceIndicator(space.index);
    }
}

export function setWorkspaceName (name) {
    menu && menu.setName(name);
}

import Clutter from 'gi://Clutter';
import Cogl from 'gi://Cogl';
import GdkPixbuf from 'gi://GdkPixbuf';
import Gio from 'gi://Gio';
import GLib from 'gi://GLib';
import Meta from 'gi://Meta';
import St from 'gi://St';

import * as Main from 'resource:///org/gnome/shell/ui/main.js';
import * as Ripples from 'resource:///org/gnome/shell/ui/ripples.js';
import * as Config from 'resource:///org/gnome/shell/misc/config.js';

import { Lib } from './imports.js';

const Display = global.display;
export let version = Config.PACKAGE_VERSION.split('.').map(Number);

let warpRipple;

let signals, touchCoords;
let inTouch = false;

export function enable() {
    warpRipple = new Ripples.Ripples(0.5, 0.5, 'ripple-pointer-location');
    warpRipple.addTo(Main.uiGroup);

    signals = new Signals();
    signals.connect(global.stage, "touch-event", (_actor, event) => {
        switch (event.type()) {
        case Clutter.EventType.TOUCH_BEGIN:
        case Clutter.EventType.TOUCH_UPDATE:
            inTouch = true;
            break;
        case Clutter.EventType.TOUCH_END:
        case Clutter.EventType.TOUCH_CANCEL:
            inTouch = false;
            break;
        default:
            return Clutter.EVENT_PROPAGATE;
        }

        // was one of our touch events
        touchCoords = event.get_coords();
        return Clutter.EVENT_PROPAGATE;
    });
}

export function disable() {
    warpRipple?.destroy();
    warpRipple = null;
    markNewClonesSignalId = null;

    signals.destroy();
    signals = null;
}

export function assert(condition, message, ...dump) {
    if (!condition) {
        throw new Error(`${message}\n`, dump);
    }
}

export function print_stacktrace(error) {
    let trace;
    if (!error) {
        trace = new Error().stack.split("\n");
        // Remove _this_ frame
        trace.splice(0, 1);
    } else {
        trace = error.stack.split("\n");
    }
    console.error(`JS ERROR: ${error}\n ${trace.join('\n')}`);
}

/**
 * Pretty prints args using JSON.stringify.
 * @param  {...any} arugs
 */
export function prettyPrintToLog(...args) {
    console.log(args.map(v => JSON.stringify(v, null), 2));
}

export function framestr(rect) {
    return `[ x:${rect.x}, y:${rect.y} w:${rect.width} h:${rect.height} ]`;
}

export function isPointInsideActor(actor, x, y) {
    return (actor.x <= x && x <= actor.x + actor.width) &&
        (actor.y <= y && y <= actor.y + actor.height);
}

export function setBackgroundImage(actor, resource_path) {
    // resource://{resource_path}
    let image = new Clutter.Image();

    let pixbuf = GdkPixbuf.Pixbuf.new_from_resource(resource_path);

    image.set_data(pixbuf.get_pixels(),
        pixbuf.get_has_alpha() ? Cogl.PixelFormat.RGBA_8888
            : Cogl.PixelFormat.RGB_888,
        pixbuf.get_width(),
        pixbuf.get_height(),
        pixbuf.get_rowstride());
    actor.set_content(image);
    actor.content_repeat = Clutter.ContentRepeat.BOTH;
}

/**
 * Backwards compatible function.  Attempts to use Cogl.Color with a fallback
 * to Clutter.Color.
 * @param {String} colorString
 */
export function color_from_string(colorString) {
    try {
        // Gnome 47+ merged Clutter.Color into Cogl.Color
        return Cogl.Color.from_string(colorString);
    } catch (error) {
        // fallback for Gnome 45, 46
        return Clutter.Color.from_string(colorString);
    }
}


// // Debug and development utils

/**
 * Visualize the frame and buffer bounding boxes of a meta window
 */
export function toggleWindowBoxes(metaWindow) {
    metaWindow = metaWindow || Display.focus_window;

    if (metaWindow._paperDebugBoxes) {
        metaWindow._paperDebugBoxes.forEach(box => {
            box.destroy();
        });
        delete metaWindow._paperDebugBoxes;
        return [];
    }

    const frame = metaWindow.get_frame_rect();
    const inputFrame = metaWindow.get_buffer_rect();
    const actor = metaWindow.get_compositor_private();

    const makeFrameBox = ({ x, y, width, height }, color)  => {
        let frameBox = new St.Widget();
        frameBox.set_position(x, y);
        frameBox.set_size(width, height);
        frameBox.set_style(`border: 2px${color} solid`);
        return frameBox;
    };

    const boxes = [];
    boxes.push(makeFrameBox(frame, "red"));
    boxes.push(makeFrameBox(inputFrame, "blue"));

    if (inputFrame.x !== actor.x || inputFrame.y !== actor.y ||
       inputFrame.width !== actor.width || inputFrame.height !== actor.height) {
        boxes.push(makeFrameBox(actor, "yellow"));
    }

    boxes.forEach(box => global.stage.add_child(box));
    metaWindow._paperDebugBoxes = boxes;
    return boxes;
}

let markNewClonesSignalId = null;
export function toggleCloneMarks() {
    // NB: doesn't clean up signal on disable

    function markCloneOf(metaWindow) {
        if (metaWindow.clone) {
            metaWindow.clone.opacity = 190;
            metaWindow.clone.__oldOpacity = 190;
            metaWindow.clone.background_color = color_from_string("red")[1];
        }
    }
    function unmarkCloneOf(metaWindow) {
        if (metaWindow.clone) {
            metaWindow.clone.opacity = 255;
            metaWindow.clone.__oldOpacity = 255;
            metaWindow.clone.background_color = null;
        }
    }

    let windows = Display.get_tab_list(Meta.TabList.NORMAL_ALL, null);

    if (markNewClonesSignalId) {
        Display.disconnect(markNewClonesSignalId);
        markNewClonesSignalId = null;
        windows.forEach(unmarkCloneOf);
    } else {
        markNewClonesSignalId = Display.connect_after(
            "window-created", (_, mw) => markCloneOf(mw));

        windows.forEach(markCloneOf);
    }
}

export function isInRect(x, y, r) {
    return r.x <= x && x < r.x + r.width &&
        r.y <= y && y < r.y + r.height;
}

/**
 * Retrieves global pointer coordinates taking into account touch screen events.
 * May not work for continuous tracking, see #766.
 */
export function getPointerCoords() {
    if (inTouch) {
        return touchCoords;
    } else {
        return global.get_pointer();
    }
}

/**
 * Returns monitor a pointer co-ordinates.
 */
export function monitorAtPoint(gx, gy) {
    for (let monitor of Main.layoutManager.monitors) {
        if (isInRect(gx, gy, monitor))
            return monitor;
    }
    return null;
}

/**
 * Returns the monitor current pointer coordinates.
 */
export function monitorAtCurrentPoint() {
    const [gx, gy] = getPointerCoords();
    return monitorAtPoint(gx, gy);
}

/**
 * Warps pointer to the center of a monitor.
 */
export function warpPointerToMonitor(monitor, params = { center: false, ripple: true }) {
    const center = params?.center ?? false;
    const ripple = params?.ripple ?? true;

    // no need to warp if already on this monitor
    let currMonitor = monitorAtCurrentPoint();
    if (currMonitor === monitor) {
        return;
    }

    let [x, y] = global.get_pointer();
    if (center) {
        x -= monitor.x;
        y -= monitor.y;
        warpPointer(
            monitor.x + Math.floor(monitor.width / 2),
            monitor.y + Math.floor(monitor.height / 2),
            ripple);
        return;
    }

    let proportionalX = (x - currMonitor.x) / currMonitor.width;
    let proportionalY = (y - currMonitor.y) / currMonitor.height;
    warpPointer(
        monitor.x + Math.floor(proportionalX * monitor.width),
        monitor.y + Math.floor(proportionalY * monitor.height),
        ripple
    );
}

/**
 * Warps pointer to x, y coordinates.
 * Optionally shows a ripple effect after warp.
 */
export function warpPointer(x, y, ripple = true) {
    const seat = Clutter.get_default_backend().get_default_seat();
    seat.warp_pointer(x, y);
    if (ripple) {
        warpRipple.playAnimation(x, y);
    }
}

/**
 * Return current modifiers state (or'ed Clutter.ModifierType.*)
 */
export function getModiferState() {
    let [, , mods] = global.get_pointer();
    return mods;
}

export function monitorOfPoint(x, y) {
    // get_monitor_index_for_rect "helpfully" returns the primary monitor index for out of bounds rects..
    for (let monitor of Main.layoutManager.monitors) {
        if ((monitor.x <= x && x <= monitor.x + monitor.width) &&
            (monitor.y <= y && y <= monitor.y + monitor.height))
        {
            return monitor;
        }
    }

    return null;
}

export function mkFmt({ nameOnly } = { nameOnly: false }) {
    function defaultFmt(actor, prefix = "") {
        const fmtNum = num => num.toFixed(0);
        let extra = [
            `${actor.get_position().map(fmtNum)}`,
            `${actor.get_size().map(fmtNum)}`,
        ];
        let metaWindow = actor.meta_window || actor.metaWindow;
        if (metaWindow) {
            metaWindow = `(mw: ${metaWindow.title})`;
            extra.push(metaWindow);
        }
        const extraStr = extra.join(" | ");
        let actorId = "";
        if (nameOnly) {
            // eslint-disable-next-line no-nested-ternary, eqeqeq
            actorId = actor.name ? actor.name : prefix.length == 0 ? "" : "#";
        } else {
            actorId = actor.toString();
        }
        actorId = prefix + actorId;
        let spacing = actorId.length > 0 ? " " : "";
        return `*${spacing}${actorId} ${extraStr}`;
    }
    return defaultFmt;
}

export function printActorTree(node, fmt = mkFmt(), options = {}, state = null) {
    state = Object.assign({}, state || { level: 0, actorPrefix: "" });
    const defaultOptions = {
        limit: 9999,
        collapseChains: true,
    };
    options = Object.assign(defaultOptions, options);

    if (state.level > options.limit) {
        return;
    }
    let collapse = false;
    if (options.collapseChains) {
        /*
          a
            b
              s
              t
            c 30,10
              u
          ->
          a.b.s
          a.b.t
          a.b.c ...
            u
        */
        if (node.get_children().length > 0) {
            if (node.x === 0 && node.y === 0) {
                state.actorPrefix += `${node.name ? node.name : "#"}.`;
                collapse = true;
            } else {
                collapse = false;
            }
        } else {
            collapse = false;
        }
    }
    if (!collapse) {
        console.debug(Lib.indent(state.level, fmt(node, state.actorPrefix)));
        state.actorPrefix = "";
        state.level += 1;
    }

    for (let child of node.get_children()) {
        printActorTree(child, fmt, options, state);
    }
}

export function isMetaWindow(obj) {
    return obj && obj.window_type && obj.get_compositor_private;
}

export function actor_raise(actor, above) {
    const parent = actor.get_parent();
    if (!parent) {
        return;
    }
    // needs to be null (not undefined) for valid second argument
    above = above ?? null;
    parent.set_child_above_sibling(actor, above);
}

export function actor_reparent(actor, newParent) {
    actor_remove_parent(actor);
    newParent.add_child(actor);
}

/**
 * Removes a child from a parent actor.  Checks child
 * exists in parent first.
 * @param {Clutter.Actor} parent
 * @param {Clutter.Actor} child
 */
export function actor_remove_child(parent, child) {
    if (parent.get_children().includes(child)) {
        parent.remove_child(child);
    }
}

/**
 * Removes the parent from this actor (if it has one).
 * @param {Clutter.Actor} actor
 */
export function actor_remove_parent(actor) {
    const parent = actor.get_parent();
    if (parent) {
        parent.remove_child(actor);
    }
}

/**
 * Adds a child from a parent actor.  Checks child if is already
 * attached.
 * @param {Clutter.Actor} parent
 * @param {Clutter.Actor} child
 */
export function actor_add_child(parent, child) {
    // check if already a child of this parent
    if (parent.get_children().includes(child)) {
        return;
    }

    actor_remove_parent(child);
    parent.add_child(child);
}

/**
 * Backwards compatible later_add function.
 */
export function later_add(...args) {
    global.compositor.get_laters().add(...args);
}

/**
 * Backwards compatible Display.grab_accelerator function.
 */
export function grab_accelerator(keystr, keyBindingFlags = Meta.KeyBindingFlags.NONE) {
    if (Display.grab_accelerator.length > 1) {
        return Display.grab_accelerator(keystr, keyBindingFlags);
    } else  {
        return Display.grab_accelerator(keystr);
    }
}

/**
 * Convenience method for removing timeout source(s) from Mainloop.
 */
export function timeout_remove(...timeouts) {
    timeouts.forEach(t => {
        if (t) {
            GLib.source_remove(t);
        }
    });
}

/**
 * Calls a period timeout (GLib.timeout_add) that calls a callback function.
 * Also contains parameters for init (call for initialisation), onContinue (callback when continuing),
 * onComplete (callback when completed last callback).
 * @param {Object} options
 * @param {Number} options.period_ms
 * @param {Number} options.count
 * @param {Function} options.init
 * @param {Function} options.callback
 * @param {Function} options.onContinue
 * @param {Function} options.onComplete
 * @returns GLib timeout id
 */
export function periodic_timeout(options = { }) {
    const operiod = options?.period_ms ?? 1000;
    const ocount = options?.count ?? 1;
    const oinit = options?.init ?? function() {};
    const ocallback = options?.callback ?? function() {};
    const ocontinue = options?.onContinue ?? function() {};
    const ocomplete = options?.onComplete ?? function() {};

    oinit();
    let called = 0;
    return GLib.timeout_add(
        GLib.PRIORITY_DEFAULT,
        operiod,
        () => {
            // check for early exit (if callback returns false)
            if (ocallback() === false) {
                ocomplete();
                return false;
            }

            if (called < ocount) {
                called++;
                ocontinue(called);
                return true;
            }

            ocomplete();
            return false; // on return false destroys timeout
        });
}

export class Signals extends Map {
    static get [Symbol.species]() { return Map; }

    _getOrCreateSignals(object) {
        let signals = this.get(object);
        if (!signals) {
            signals = [];
            this.set(object, signals);
        }
        return signals;
    }

    connectOneShot(object, signal, handler) {
        let id = this.connect(object, signal, (...args) => {
            this.disconnect(object, id);
            return handler(...args);
        });
    }

    connect(object, signal, handler) {
        let id = object.connect(signal, handler);
        let signals = this._getOrCreateSignals(object);
        signals.push(id);
        return id;
    }

    disconnect(object, id = null) {
        let ids = this.get(object);
        if (ids) {
            if (id === null) {
                ids.forEach(id => object.disconnect(id));
                ids = [];
            } else {
                object.disconnect(id);
                let i = ids.indexOf(id);
                if (i > -1) {
                    ids.splice(i, 1);
                }
            }
            if (ids.length === 0)
                this.delete(object);
        }
    }

    destroy() {
        for (let [object, signals] of this) {
            signals.forEach(id => object.disconnect(id));
            this.delete(object);
        }
    }
}

/**
 * Note the name 'Tweener' used previously was just a legacy name, we're actually using
 * Widget.ease here.  This was renamed to avoid confusion with the deprecated `Tweener`
 * module.
 */
export let Easer = {
    /**
     * Safer time setting to essentiall disable easer animation.
     * Setting to values lower than this can have some side-effects
     * like "jumpy" three-finger left/right swiping etc.
     */
    ANIMATION_SAFE_TIME: 0.03,

    /**
     * Can set animation to instant time.  Used for to override animation
     * time to effectively "disable" an animation.  Setting to 0 can have
     * some side-effects and cause race aconditions
     */
    ANIMATION_INSTANT_TIME: 0.0001,

    addEase(actor, params) {
        if (params.time) {
            params.duration = this._safeDuration(params.time, params.instant);
            delete params.time;
        }

        if (!params.mode) {
            params.mode = Clutter.AnimationMode.EASE_IN_OUT_QUAD;
        }

        actor.ease(params);
    },

    /**
     * Returns a safe animation time to avoid timing
     * race conditions etc.
     */
    _safeDuration(time, instant) {
        let duration = Math.max(time, this.ANIMATION_SAFE_TIME);
        if (instant === true) {
            duration = this.ANIMATION_INSTANT_TIME;
        }

        return duration * 1000;
    },

    removeEase(actor) {
        actor.remove_all_transitions();
    },

    isEasing(actor) {
        return actor.get_transition('x') ||
        actor.get_transition('y') ||
        actor.get_transition('scale-x') ||
        actor.get_transition('scale-x');
    },
};

export class DisplayConfig {
    static get proxyWrapper() {
        return Gio.DBusProxy.makeProxyWrapper('<node>\
        <interface name="org.gnome.Mutter.DisplayConfig">\
            <method name="GetCurrentState">\
            <arg name="serial" direction="out" type="u" />\
            <arg name="monitors" direction="out" type="a((ssss)a(siiddada{sv})a{sv})" />\
            <arg name="logical_monitors" direction="out" type="a(iiduba(ssss)a{sv})" />\
            <arg name="properties" direction="out" type="a{sv}" />\
            </method>\
            <signal name="MonitorsChanged" />\
        </interface>\
    </node>');
    }

    constructor() {
        this.proxy = new DisplayConfig.proxyWrapper(
            Gio.DBus.session,
            'org.gnome.Mutter.DisplayConfig',
            '/org/gnome/Mutter/DisplayConfig',
            (proxy, error) => {
                if (error) {
                    console.error(error);
                    return;
                }
                this.upgradeGnomeMonitors();
            }
        );
    }

    /**
     * Upgrades Main.layoutManager.monitors by adding a dbus monitor connector
     * (e.g. "eDP-1" or "DP-1", etc.).  Used for stable restoring for monitor
     * layouts.
     */
    upgradeGnomeMonitors(callback = () => {}) {
        this.proxy.GetCurrentStateRemote((state, error) => {
            if (error) {
                console.error(error);
                return;
            }

            const [, monitors] = state;
            for (const monitor of monitors) {
                const [specs] = monitor;
                const [connector] = specs;

                // upgrade gnome monitor object to add connector
                let gnomeIndex = this.monitorManager.get_monitor_for_connector(connector);
                let gnomeMonitor = this.gnomeMonitors.find(m => m.index === gnomeIndex);
                if (gnomeMonitor) {
                    gnomeMonitor.connector = connector;
                }
            }

            callback();
        });
    }

    /**
     * Downgrades Main.layoutManager.monitors to default gnome state (without "connector"
     * information).
     */
    downgradeGnomeMonitors() {
        this.gnomeMonitors.forEach(m => {
            delete m.connector;
        });
    }

    get monitorManager() {
        return global.backend.get_monitor_manager();
    }

    get gnomeMonitors() {
        return Main.layoutManager.monitors;
    }
}

import St from 'gi://St';

import { Utils, Tiling } from './imports.js';

let fitProportionally = Tiling.fitProportionally;
let prefs = {
    window_gap: 5,
    minimum_margin: 3,
};

let virtStage = null;

export function repl() {
    if (virtStage) {
        virtStage.destroy();
    }

    let realMonitor = space.monitor;
    let scale = 0.10;
    let padding = 10;
    const monitorWidth = realMonitor.width * scale;
    const monitorHeight = realMonitor.height * scale;
    let stageStyle = 'background-color: white;';
    virtStage = new St.Widget({
        name: 'stage',
        style: stageStyle,
        height: monitorHeight + padding * 2,
        width: monitorWidth * 3,
    });

    let monitorStyle = `background-color: blue;`;
    let monitor = new St.Widget({
        name: "monitor0",
        style: monitorStyle,
        x: virtStage.width / 2 - monitorWidth / 2, y: padding,
        width: monitorWidth,
        height: virtStage.height - padding * 2,
    });

    let panel = new St.Widget({
        name: "panel",
        style: `background-color: gray`,
        x: 0, y: 0,
        width: monitor.width,
        height: 10,

    });
    let workArea = {
        x: monitor.x,
        y: panel.height,
        width: monitor.width,
        height: monitor.height - panel.height,
    };

    let tilingStyle = `background-color: rgba(190, 190, 0, 0.3);`;
    let tilingContainer = new St.Widget({ name: "tiling", style: tilingStyle });

    global.stage.add_child(virtStage);
    virtStage.x = 3000;
    virtStage.y = 300;

    virtStage.add_child(monitor);
    monitor.add_child(panel);
    monitor.add_child(tilingContainer);

    function sync(space_ = space) {
        let columns = layout(
            fromSpace(space_, scale),
            workArea,
            prefs
        );
        renderAndView(
            tilingContainer,
            columns
        );
        tilingContainer.x = space_.targetX * scale;
    }

    sync();

    Utils.printActorTree(virtStage, Utils.mkFmt({ nameOnly: true }));

    movecolumntoviewportposition(tilingContainer, monitor, columns[1][0], 30);

    virtStage.hide();
    virtStage.show();
    virtStage.y = 400;
}

/** tiling position given:
    m_s: monitor position
    w_m: window position (relative to monitor)
    w_t: window position (relative to tiling)
 */
export function t_s(m_s, w_m, w_t) {
    return w_m - w_t + m_s;
}

/**
   Calculates the tiling position such that column `k` is positioned at `x`
   relative to the viewport (or workArea?)
 */
export function movecolumntoviewportposition(tilingActor, viewport, window, x) {
    tilingActor.x = t_s(viewport.x, x, window.x);
}

export function renderAndView(container, columns) {
    for (let child of container.get_children()) {
        child.destroy();
    }

    render(columns, container);
}

export function fromSpace(space, scale = 1) {
    return space.map(
        col => col.map(
            metaWindow => {
                let f = metaWindow.get_frame_rect();
                return {
                    width: f.width * scale,
                    height: f.height * scale,
                };
            }
        )
    );
}

/** Render a dummy view of the windows */
export function render(columns, tiling) {
    const windowStyle = `border: black solid 1px; background-color: red`;

    function createWindowActor(window) {
        return new St.Widget({
            style: windowStyle,
            width: window.width,
            height: window.height,
            x: window.x,
            y: window.y,
        });
    }

    for (let col of columns) {
        for (let window of col) {
            let windowActor = createWindowActor(window);
            tiling.add_child(windowActor);
        }
    }
}

export function allocateDefault(column, availableHeight, preAllocatedWindow) {
    if (column.length === 1) {
        return [availableHeight];
    } else {
        // Distribute available height amongst non-selected windows in proportion to their existing height
        const gap = prefs.window_gap;
        const minHeight = 15;

        const heightOf = window => {
            return window.height;
        };

        const k = preAllocatedWindow && column.indexOf(preAllocatedWindow);
        const selectedHeight = preAllocatedWindow && heightOf(preAllocatedWindow);

        let nonSelected = column.slice();
        if (preAllocatedWindow) {
            nonSelected.splice(k, 1);
        }

        const nonSelectedHeights = nonSelected.map(heightOf);
        let availableForNonSelected = Math.max(
            0,
            availableHeight -
                (column.length - 1) * gap -
                (preAllocatedWindow ? selectedHeight : 0)
        );

        const deficit = Math.max(
            0, nonSelected.length * minHeight - availableForNonSelected);

        let heights = fitProportionally(
            nonSelectedHeights,
            availableForNonSelected + deficit
        );

        if (preAllocatedWindow) {
            heights.splice(k, 0, selectedHeight - deficit);
        }

        return heights;
    }
}

export function allocateEqualHeight(column, available) {
    available -= (column.length - 1) * prefs.window_gap;
    return column.map(_ => Math.floor(available / column.length));
}

export function layoutGrabColumn(column, x, y0, targetWidth, availableHeight, grabWindow) {
    function mosh(windows, height, y0) {
        let targetHeights = fitProportionally(
            windows.map(mw => mw.rect.height),
            height
        );
        let [w, y] = layoutColumnSimple(windows, x, y0, targetWidth, targetHeights);
        return y;
    }

    const k = column.indexOf(grabWindow);
    if (k < 0) {
        throw new Error(`Anchor doesn't exist in column ${grabWindow.title}`);
    }

    const gap = prefs.window_gap;
    const f = grabWindow.globalRect();
    let yGrabRel = f.y - this.monitor.y;
    targetWidth = f.width;

    const H1 = (yGrabRel - y0) - gap - (k - 1) * gap;
    const H2 = availableHeight - (yGrabRel + f.height - y0) - gap - (column.length - k - 2) * gap;
    k > 0 && mosh(column.slice(0, k), H1, y0);
    let y = mosh(column.slice(k, k + 1), f.height, yGrabRel);
    k + 1 < column.length && mosh(column.slice(k + 1), H2, y);

    return targetWidth;
}


export function layoutColumnSimple(windows, x, y0, targetWidth, targetHeights, time) {
    let y = y0;

    for (let i = 0; i < windows.length; i++) {
        let virtWindow = windows[i];
        let targetHeight = targetHeights[i];

        virtWindow.x = x;
        virtWindow.y = y;
        virtWindow.width = targetWidth;
        virtWindow.height = targetHeight;

        y += targetHeight + prefs.window_gap;
    }
    return targetWidth, y;
}


/**
   Mutates columns
 */
export function layout(columns, workArea, prefs, options = {}) {
    let gap = prefs.window_gap;
    let availableHeight = workArea.height;

    let { inGrab, selectedWindow } = options;
    let selectedIndex = -1;

    if (selectedWindow) {
        selectedIndex = columns.findIndex(col => col.includes(selectedWindow));
    }

    let y0 = workArea.y;
    let x = 0;

    for (let i = 0; i < columns.length; i++) {
        let column = columns[i];

        let selectedInColumn = i === selectedIndex ? selectedWindow : null;

        let targetWidth;
        if (i === selectedIndex) {
            targetWidth = selectedInColumn.width;
        } else {
            targetWidth = Math.max(...column.map(w => w.width));
        }
        targetWidth = Math.min(targetWidth, workArea.width - 2 * prefs.minimum_margin);

        if (inGrab && i === selectedIndex) {
            layoutGrabColumn(column, x, y0, targetWidth, availableHeight, selectedInColumn);
        } else {
            let allocator = options.customAllocators && options.customAllocators[i];
            allocator = allocator || allocateDefault;

            let targetHeights = allocator(column, availableHeight, selectedInColumn);
            layoutColumnSimple(column, x, y0, targetWidth, targetHeights);
        }

        x += targetWidth + gap;
    }

    return columns;
}

{ pkgs, config, lib, ... }:

{
  ### Make PaperWM available in system environment
  environment.systemPackages = with pkgs;
  [ paperwm
  ];

  ### Set graphical session to auto-login GNOME
  services.xserver =
  { enable = true;
    displayManager.autoLogin =
    { enable = true;
      user = "user";
    };
    displayManager.gdm.enable = true;
    desktopManager.gnome.enable = true;
  };

  ### Set dconf to enable PaperWM out of the box
  programs.dconf =
  { enable = true;
    profiles."user".databases = [
      { settings =
        { "org/gnome/shell" =
          { enabled-extensions = [ "paperwm@paperwm.github.com" ];
          };
        };
      }
    ];
  };

  ### Set default user
  users.users."user" =
  { isNormalUser = true;
    createHome = true;
    home = "/home";
    description = "PaperWM test user";
    extraGroups = [ "wheel" ];
    password = "paperwm";
  };

  ### No-password sudo
  security.sudo =
  { enable = true;
    extraConfig = "%wheel ALL=(ALL) NOPASSWD: ALL";
  };
}

import GLib from 'gi://GLib';
import GObject from 'gi://GObject';
import Gtk from 'gi://Gtk';

export const WinpropsPane = GObject.registerClass({
    GTypeName: 'WinpropsPane',
    Template: GLib.uri_resolve_relative(import.meta.url, './WinpropsPane.ui', GLib.UriFlags.NONE),
    InternalChildren: [
        'search',
        'listbox',
        'addButton',
        'scrolledWindow',
    ],
    Signals: {
        'changed': {},
    },
}, class WinpropsPane extends Gtk.Box {
    _init(params = {}) {
        super._init(params);

        // define search box filter function (searches wm_class, title, and accelLabel)
        this._listbox.set_filter_func(row => {
            let search = this._search.get_text().toLowerCase();
            let wmclass = row.winprop.wm_class?.toLowerCase() ?? '';
            let title = row.winprop.title?.toLowerCase() ?? '';
            let accelLabel = row._accelLabel.label?.toLowerCase() ?? '';
            return wmclass.includes(search) || title.includes(search) || accelLabel.includes(search);
        });
        this._search.connect('changed', () => {
            this._listbox.invalidate_filter();
        });

        this._expandedRow = null;
        this.rows = [];
        this.workspaces = [];
    }

    addWinprops(winprops) {
        winprops.forEach(winprop => {
            this._listbox.insert(this._createRow(winprop), -1);
        });
    }

    setWorkspaces(workspaces) {
        this.workspaces = workspaces;
    }

    _removeRow(row) {
        this._listbox.remove(row);
        let remove = this.rows.findIndex(r => r === row);
        if (remove >= 0) {
            this.rows.splice(remove, 1);
        }
        this.emit('changed');
    }

    _onAddButtonClicked() {
        // first clear search text, otherwise won't be able to see new row
        this._search.set_text('');

        let row = this._createRow();
        row.expanded = true;
        this._listbox.insert(row, 0);
        this._scrolledWindow.get_vadjustment().set_value(0);
    }

    _createRow(winprop) {
        let wp = winprop ?? { wm_class: '' };
        const row = new WinpropsRow({ winprop: wp, workspaces: this.workspaces });
        this.rows.push(row);
        row.connect('notify::expanded', row => this._onRowExpanded(row));
        row.connect('row-deleted', row => this._removeRow(row));
        row.connect('changed', () => this.emit('changed'));
        return row;
    }

    _onRowActivated(list, row) {
        if (!row.is_focus()) {
            return;
        }
        row.expanded = !row.expanded;
    }

    _onRowExpanded(row) {
        if (row.expanded) {
            if (this._expandedRow) {
                this._expandedRow.expanded = false;
            }
            this._expandedRow = row;
        } else if (this._expandedRow === row) {
            this._expandedRow = null;
        }
    }
});

export const WinpropsRow = GObject.registerClass({
    GTypeName: 'WinpropsRow',
    Template: GLib.uri_resolve_relative(import.meta.url, './WinpropsRow.ui', GLib.UriFlags.NONE),
    InternalChildren: [
        'header',
        'descLabel',
        'accelLabel',
        'revealer',
        'optionList',
        'wmClass',
        'title',
        'scratchLayer',
        'preferredWidth',
        'space',
        'focus',
        'deleteButton',
    ],
    Properties: {
        winprop: GObject.ParamSpec.jsobject(
            'winprop',
            'winprop',
            'Winprop',
            GObject.ParamFlags.READWRITE | GObject.ParamFlags.CONSTRUCT_ONLY
        ),
        workspaces: GObject.ParamSpec.jsobject(
            'workspaces',
            'workspaces',
            'Workspaces',
            GObject.ParamFlags.READWRITE
        ),
        expanded: GObject.ParamSpec.boolean(
            'expanded',
            'Expanded',
            'Expanded',
            GObject.ParamFlags.READWRITE,
            false
        ),
    },
    Signals: {
        'changed': {},
        'row-deleted': {},
    },
}, class WinpropsRow extends Gtk.ListBoxRow {
    _init(params = {}) {
        super._init(params);

        // description label
        this._setDescLabel();

        // set the values to current state and connect to 'changed' signal
        this._wmClass.set_text(this.winprop.wm_class ?? '');
        this._wmClass.connect('changed', () => {
            // check if null or empty (we still emit changed if wm_class is wiped)
            this.checkHasWmClassOrTitle();
            this.winprop.wm_class = this._wmClass.get_text();
            this._setDescLabel();
            this.emit('changed');
        });

        this._title.set_text(this.winprop.title ?? '');
        this._title.connect('changed', () => {
            this.checkHasWmClassOrTitle();
            this.winprop.title = this._title.get_text();
            this._setDescLabel();
            this.emit('changed');
        });

        this._scratchLayer.set_active(this.winprop.scratch_layer ?? false);
        this._scratchLayer.connect('state-set', () => {
            let isActive = this._scratchLayer.get_active();
            this.winprop.scratch_layer = isActive;

            // if is active then disable the preferredWidth input
            this._preferredWidth.set_sensitive(!isActive);

            this.emit('changed');
        });

        this._preferredWidth.set_text(this.winprop.preferredWidth ?? '');
        // if scratchLayer is active then users can't edit preferredWidth
        this._preferredWidth.set_sensitive(!this.winprop.scratch_layer ?? true);

        this._preferredWidth.connect('changed', () => {
            // if has value, needs to be valid (have a value or unit)
            if (this._preferredWidth.get_text()) {
                let value = this._preferredWidth.get_text();
                let digits = (value.match(/\d+/) ?? [null])[0];
                let isPercent = /^.*%$/.test(value);
                let isPixel = /^.*px$/.test(value);

                // check had valid number
                if (!digits) {
                    this._setError(this._preferredWidth);
                }
                // if no unit defined
                else if (!isPercent && !isPixel) {
                    this._setError(this._preferredWidth);
                }
                else {
                    this._setError(this._preferredWidth, false);
                    this.winprop.preferredWidth = this._preferredWidth.get_text();
                    this.emit('changed');
                }
            } else {
                // having no preferredWidth is valid
                this._setError(this._preferredWidth, false);
                delete this.winprop.preferredWidth;
                this.emit('changed');
            }
        });

        this._space.append_text("CURRENT");
        for (const [i, name] of this.workspaces.entries()) {
            // Combo box entries in normal workspace index order
            this._space.append_text(`${i}: ${name}`);
        }
        // index 0 is CURRENT, so add 1
        this._space.set_active((this.winprop.spaceIndex ?? -1) + 1);
        this._space.connect('changed', () => {
            let value = this._space.get_active() - 1;
            if (value < 0) {
                value = undefined;
            }
            this.winprop.spaceIndex = value;
            this.emit('changed');
        });

        this._focus.set_active(this.winprop.focus ?? true);
        this._focus.connect('state-set', () => {
            let isActive = this._focus.get_active();
            this.winprop.focus = isActive;
            this.emit('changed');
        });

        this._updateState();
    }

    /**
     * Checks has an input for either wmClass or title.
     * Sets 'error' cssClass is neither.
     */
    checkHasWmClassOrTitle() {
        if (!this._wmClass.get_text() && !this._title.get_text()) {
            this._setError(this._wmClass);
            this._setError(this._title);
            return false;
        } else {
            this._setError(this._wmClass, false);
            this._setError(this._title, false);
            return true;
        }
    }

    /**
     * Get the wmClass if it exists, otherwise returns the title.
     * @returns String
     */
    getWmClassOrTitle() {
        if (this.winprop.wm_class) {
            return this.winprop.wm_class;
        }
        else if (this.winprop.title) {
            return this.winprop.title;
        }
        else {
            return '';
        }
    }

    _setError(child, option = true) {
        if (child) {
            if (option) {
                child.add_css_class('error');
            } else {
                child.remove_css_class('error');
            }
        }
    }

    get expanded() {
        if (this._expanded === undefined)
            this._expanded = false;
        return this._expanded;
    }

    set expanded(value) {
        if (this._expanded === value)
            return;

        this._expanded = value;
        this.notify('expanded');
        this._updateState();
    }

    _onDeleteButtonClicked() {
        this.emit('row-deleted');
    }

    _onRowActivated(list, row) {
        if (row.is_focus()) {
            row.editing = !row.editing;
        }
    }

    _setAccelLabel() {
        if (this.winprop.scratch_layer ?? false) {
            return 'scratch layer';
        }
        else if (this.winprop.preferredWidth ?? false) {
            return 'preferred width';
        }
        else if (this.winprop.spaceIndex !== undefined) {
            return 'workspace';
        }
        else {
            return 'no setting';
        }
    }

    /**
     * Sets the description label for this row.
     * @returns boolean
     */
    _setDescLabel() {
        // if wmClass, use that, otherwise use title (fallback)
        if (this.winprop.wm_class) {
            this._descLabel.label = this.winprop.wm_class;
        }
        else if (this.winprop.title) {
            this._descLabel.label = this.winprop.title;
        }
    }

    _updateState() {
        GLib.idle_add(0, () => {
            this._accelLabel.label = this._setAccelLabel();
            if (this.expanded) {
                this._accelLabel.hide();
                this._revealer.reveal_child = true;
                this.add_css_class('expanded');
            } else {
                this._accelLabel.show();
                this._revealer.reveal_child = false;
                this.remove_css_class('expanded');
            }
        });
    }
});

import Gio from 'gi://Gio';
import GLib from 'gi://GLib';

import * as Lib from './lib.js';

/**
 * Workspace related utility functions used by other modules.
 */
const WORKSPACE_LIST_KEY = 'org.gnome.shell.extensions.paperwm.workspacelist';
const WORKSPACE_KEY = 'org.gnome.shell.extensions.paperwm.workspace';

export class WorkspaceSettings {
    constructor(extension) {
        this.workspaceSettingsCache = {};
        this.schemaSource = Gio.SettingsSchemaSource.new_from_directory(
            GLib.build_filenamev([extension.path, "schemas"]),
            Gio.SettingsSchemaSource.get_default(),
            false
        );

        this.workspaceList = new Gio.Settings({
            settings_schema: this.getSchemaSource().lookup(WORKSPACE_LIST_KEY, true),
        });
    }

    getSchemaSource() {
        return this.schemaSource;
    }

    getWorkspaceName(settings, index) {
        let name = settings.get_string('name') ?? `Workspace ${index + 1}`;
        if (!name || name === '') {
            name = `Workspace ${index + 1}`;
        }
        return name;
    }

    getWorkspaceList() {
        return this.workspaceList;
    }

    /**
     * Returns list of ordered workspace UUIDs.
     */
    getListUUID() {
        return this.getWorkspaceList().get_strv('list');
    }

    getWorkspaceSettings(index) {
        let list = this.getListUUID();
        for (let uuid of list) {
            let settings = this.getWorkspaceSettingsByUUID(uuid);
            if (settings.get_int('index') === index) {
                return [uuid, settings];
            }
        }
        return this.getNewWorkspaceSettings(index);
    }

    getNewWorkspaceSettings(index) {
        let uuid = GLib.uuid_string_random();
        let settings = this.getWorkspaceSettingsByUUID(uuid);
        let list = this.getListUUID();
        list.push(uuid);
        this.getWorkspaceList().set_strv('list', list);
        settings.set_int('index', index);
        return [uuid, settings];
    }

    getWorkspaceSettingsByUUID(uuid) {
        if (!this.workspaceSettingsCache[uuid]) {
            let settings = new Gio.Settings({
                settings_schema: this.getSchemaSource().lookup(WORKSPACE_KEY, true),
                path: `/org/gnome/shell/extensions/paperwm/workspaces/${uuid}/`,
            });
            this.workspaceSettingsCache[uuid] = settings;
        }
        return this.workspaceSettingsCache[uuid];
    }

    /** Returns [[uuid, settings, name], ...] (Only used for debugging/development atm.) */
    findWorkspaceSettingsByName(regex) {
        let list = this.getListUUID();
        let settings = list.map(this.getWorkspaceSettingsByUUID);
        return Lib.zip(list, settings, settings.map(s => s.get_string('name')))
            .filter(([uuid, s, name]) => name.match(regex));
    }

    /** Only used for debugging/development atm. */
    deleteWorkspaceSettingsByName(regex, dryrun = true) {
        let out = "";
        function rprint(...args) { console.debug(...args); out += `${args.join(" ")}\n`; }
        let n = global.workspace_manager.get_n_workspaces();
        for (let [uuid, s, name] of this.findWorkspaceSettingsByName(regex)) {
            let index = s.get_int('index');
            if (index < n) {
                rprint("Skipping in-use settings", name, index);
                continue;
            }
            rprint(dryrun ? "[dry]" : "", `Delete settings for '${name}' (${uuid})`);
            if (!dryrun) {
                this.deleteWorkspaceSettings(uuid);
            }
        }
        return out;
    }

    /** Only used for debugging/development atm. */
    deleteWorkspaceSettings(uuid) {
        // NB! Does not check if the settings is currently in use. Does not reindex subsequent settings.
        let list = this.getListUUID();
        let i = list.indexOf(uuid);
        let settings = this.getWorkspaceSettingsByUUID(list[i]);
        for (let key of settings.list_keys()) {
            // Hopefully resetting all keys will delete the relocatable settings from dconf?
            settings.reset(key);
        }

        list.splice(i, 1);
        this.getWorkspaceList().set_strv('list', list);
    }

    // Useful for debugging
    printWorkspaceSettings() {
        let list = this.getListUUID();
        let settings = list.map(this.getWorkspaceSettingsByUUID);
        let zipped = Lib.zip(list, settings);
        const key = s => s[1].get_int('index');
        zipped.sort((a, b) => key(a) - key(b));
        for (let [uuid, s] of zipped) {
            console.log('index:', s.get_int('index'), s.get_string('name'), s.get_string('color'), uuid);
        }
    }
}
