{
  "adjoining-classes": false,
  "box-model": false,
  "box-sizing": false,
  "order-alphabetical": false
}

module.exports = {
  extends: [require.resolve("vis-dev-utils/eslint-shareable-config")],
};

// Markdown Lint Rules
// https://github.com/mivok/markdownlint/blob/master/docs/RULES.md

rules
  "~MD012", // alert on multiple consecutive blank lines
  "~MD013", // line length should be no more than 80 characters
  "~MD014", // Dollar signs used before commands without showing output

{
  "extension": ["ts", "js"],
  "require": ["vis-dev-utils/babel-register"],
  "spec": ["./test/**/*.test.js", "./test/**/*.test.ts"]
}

module.exports = {
  exclude: require("vis-dev-utils").BABEL_IGNORE_RE,
  presets: [["vis-dev-utils/babel-preset", { css: true, ts: true }]],
};

import { defineConfig } from "cypress";

export default defineConfig({
  env: {
    SNAPSHOT_BASE_DIRECTORY: "./cypress/snapshots/base",
    SNAPSHOT_DIFF_DIRECTORY: "./cypress/snapshots/diff",
  },
  screenshotsFolder: "./cypress/snapshots/actual",
  trashAssetsBeforeRuns: true,
  viewportHeight: 1600,
  viewportWidth: 1200,
  e2e: {
    // We've imported your old cypress plugins here.
    // You may want to clean this up later by importing these.
    async setupNodeEvents(on, config) {
      return (await import("./cypress/plugins/index.ts")).default(on, config);
    },
    specPattern: [
      "cypress/e2e/visual/**/*.spec.ts",
      "cypress/e2e/functional/**/*.spec.ts",
    ],
    supportFile: "cypress/support/e2e.ts",
  },
});

{
  "plugins": ["plugins/markdown"]
}

{
  "version": "0.2.0",
  "configurations": [
    {
      "type": "node",
      "request": "launch",
      "name": "Mocha All",
      "program": "${workspaceFolder}/node_modules/mocha/bin/_mocha",
      "args": ["--timeout", "999999", "--colors", "${workspaceFolder}/test"],
      "console": "integratedTerminal",
      "internalConsoleOptions": "neverOpen"
    },
    {
      "type": "node",
      "request": "launch",
      "name": "Mocha Current File",
      "program": "${workspaceFolder}/node_modules/mocha/bin/_mocha",
      "args": ["--timeout", "999999", "--colors", "${file}"],
      "console": "integratedTerminal",
      "internalConsoleOptions": "neverOpen"
    }
  ]
}

