var cp = require('child_process')
var chokidar = require('chokidar')

cp.exec('mkdir -p dist')

watch('css/**/*.styl', 'npm run build-css')
watch('views/**/*.jade', 'npm run build-html')
watch('lib/**/*.js', 'npm run build-js')

function watch (glob, cmd) {
  chokidar.watch(glob)
    .on('change', function () {
      exec(cmd)
    })
}

function exec (cmd) {
  cp.exec(cmd, function (err, stdout, stderr) {
    err && console.error(err)
    stdout && console.log(stdout)
    stderr && console.log(stderr)
  })
}

Package.describe({
  name: 'pakastin:deck-of-cards',
  version: '0.1.4',
  // Brief, one-line summary of the package.
  summary: 'HTML5 Deck of Cards',
  // URL to the Git repository containing the source code for this package.
  git: 'https://github.com/pakastin/deck-of-cards.git',
  // By default, Meteor will default to using README.md for documentation.
  // To avoid submitting documentation, set this field to null.
  documentation: 'README.md'
});

Package.onUse(function(api) {
  api.versionsFrom('1.2.1');
  api.use('ecmascript');
  api.use('stylus');

  api.addFiles('dist/deck.js', 'client', { bare: true });
  api.addFiles('css/card.styl', 'client');

  // Add card faces and backs
  api.addAssets('css/faces/back.png', 'client');

  // 52 deck cards
  for( var i = 0; i < 4; i++ ) {
    for( var j = 1; j <= 13; j++ ) {
      api.addAssets('css/faces/' + i + '_' + j + '.svg', 'client');
    }
  }

  // 3 jokers
  for( var k = 1; k <= 3; k++ ) {
    api.addAssets('css/faces/4_' + k + '.svg', 'client');
  }

  api.export(['Deck'], ['client']);
});

Package.onTest(function(api) {
  api.use('ecmascript');
  api.use('tinytest');

  api.use('pakastin:deck-of-cards');
});

{
  "name": "deck-of-cards",
  "version": "0.1.9",
  "description": "HTML5 Deck of Cards",
  "main": "dist/deck.min.js",
  "module": "lib/deck.js",
  "jsnext:main": "lib/deck.js",
  "scripts": {
    "start": "node index",
    "build-js": "rollup --name Deck -f iife lib/deck.js | babel -o dist/deck.js && uglifyjs dist/deck.js -cmo dist/deck.min.js",
    "build-css": "stylus -u nib css/index.styl -o example/example.css",
    "build-html": "jade -P views/index.jade -o example/",
    "postversion": "git push --tags",
    "postinstall": "opencollective-postinstall || true"
  },
  "repository": {
    "type": "git",
    "url": "git+https://github.com/pakastin/deck-of-cards.git"
  },
  "keywords": [
    "html5",
    "deck",
    "of",
    "cards"
  ],
