  "license": "MIT",
  "bugs": {
    "url": "https://github.com/pakastin/deck-of-cards/issues"
  },
  "homepage": "https://github.com/pakastin/deck-of-cards#readme",
  "devDependencies": {
    "babel": "^5.8.23",
    "chokidar": "^1.0.5",
    "jade": "^1.11.0",
    "nib": "^1.1.0",
    "rollup": "^0.16.4",
    "stylus": "^0.52.0",
    "uglify-js": "^2.4.24"
  },
  "dependencies": {
    "opencollective-postinstall": "^2.0.2"
  },
  "collective": {
    "type": "opencollective",
    "url": "https://opencollective.com/deck-of-cards"
  }
}
