{
  "name": "juice",
  "version": "11.0.0",
  "description": "Inlines css into html source",
  "bin": "bin/juice",
  "main": "index.js",
  "browser": "client.js",
  "types": "juice.d.ts",
  "files": [
    "bin",
    "lib",
    "client.js",
    "index.js",
    "juice.d.ts"
  ],
  "scripts": {
    "test": "mocha --reporter spec && npm run test-typescript",
    "testcover": "istanbul cover node_modules/mocha/bin/_mocha -- -R spec",
    "test-typescript": "tsc ./test/typescript/juice-tests.ts && rm ./test/typescript/juice-tests.js",
    "browserify": "browserify client.js -o tmp/bundle.js --standalone juice"
  },
  "license": "MIT",
  "contributors": [
    {
      "name": "Guillermo Rauch"
    },
    {
      "name": "Andrew Kelley"
    },
    {
      "name": "Jarrett Widman"
    }
  ],
  "engines": {
    "node": ">=18.17"
  },
  "dependencies": {
    "cheerio": "^1.0.0",
    "commander": "^12.1.0",
    "mensch": "^0.3.4",
    "slick": "^1.12.2",
    "web-resource-inliner": "^7.0.0"
  },
  "devDependencies": {
    "@types/node": "^22.4.2",
    "batch": "0.6.1",
    "browserify": "^17.0.0",
    "cross-spawn": "^7.0.3",
    "mocha": "^10.7.3",
    "should": "^13.2.3",
    "typescript": "^5.5.4"
  },
  "repository": {
    "type": "git",
    "url": "https://github.com/Automattic/juice.git"
  }
}
