{
    "presets": [
        ["@babel/preset-env", {
            "targets": {
                "browsers": [">1%", "not ie 11", "not op_mini all", "not dead"]
            }
        }]
    ],
    "plugins": []
}

{
    "name": "jcbrand/converse.js",
    "description": "Browser based XMPP instant messaging client",
    "type": "library",
    "license": "MPL-2.0",
    "minimum-stability": "stable",
    "homepage": "https://conversejs.org/",
    "keywords": ["xmpp", "messaging", "chat", "presence"],
    "require": {}
}

{
  "short_name": "Converse",
  "name": "Converse Chat",
  "description": "Messaging Freedom",
  "version": "11.0.0",
  "categories": ["social"],
  "icons": [
    {
      "src": "dist/images/logo/conversejs-filled-512.png",
      "sizes": "512x512"
    },
    {
      "src": "dist/images/logo/conversejs-filled-192.png",
      "sizes": "192x192"
    },
    {
      "src": "dist/images/logo/conversejs-filled-192.svg",
      "sizes": "192x192"
    },
    {
      "src": "dist/images/logo/conversejs-filled-512.svg",
      "sizes": "512x512"
    }
  ],
  "start_url": "/fullscreen.html",
  "background_color": "#397491",
  "display": "standalone",
  "scope": "/",
  "theme_color": "#397491"
}

