module.exports = {
  extends: ['eslint:recommended', 'plugin:prettier/recommended'],
  env: {
    es6: true,
    node: true,
  },
  parserOptions: {
    sourceType: 'module',
  },
  overrides: [
    {
      files: ['**/*.ts'],
      extends: [
        'eslint:recommended',
        'plugin:@typescript-eslint/recommended',
        'plugin:@typescript-eslint/recommended-requiring-type-checking',
        'plugin:prettier/recommended',
      ],
      parser: '@typescript-eslint/parser',
      parserOptions: {
        ecmaVersion: 6,
        sourceType: 'module',
        project: './tsconfig.json',
        warnOnUnsupportedTypeScriptVersion: false,
      },
      plugins: ['@typescript-eslint'],
      rules: {
        'arrow-body-style': ['error'],
        'no-template-curly-in-string': 'error',
        'no-useless-concat': 'error',
        'no-useless-escape': 'off',
        'no-var': 'error',
        'one-var': ['error', 'never'],
        'prefer-arrow-callback': ['error', { allowNamedFunctions: true }],
        'prefer-const': 'error',
        'prefer-template': 'error',
        'template-curly-spacing': 'error',
        'template-tag-spacing': 'error',
        quotes: ['error', 'single', { avoidEscape: true }],

        '@typescript-eslint/ban-ts-comment': 'off',
        '@typescript-eslint/no-unsafe-argument': 'off',
        '@typescript-eslint/no-unsafe-assignment': 'off',
        '@typescript-eslint/no-unsafe-call': 'off',
        '@typescript-eslint/no-unsafe-member-access': 'off',
        '@typescript-eslint/no-var-requires': 'off',
        '@typescript-eslint/restrict-template-expressions': 'off',
      },
    },
    {
      files: ['**/*.spec.js'],
      env: {
        jest: true,
      },
    },
  ],
}

{
  "branches": [
    "master",
    { "name": "alpha", "prerelease": true },
    { "name": "beta", "prerelease": true }
  ],
  "repositoryUrl": "https://github.com/htmlhint/HTMLHint",
  "debug": "true",
  "plugins": [
    "@semantic-release/commit-analyzer",
    "@semantic-release/release-notes-generator",
    [
      "@semantic-release/changelog",
      {
        "changelogFile": "CHANGELOG.md"
      }
    ],
    "@semantic-release/github",
    "@semantic-release/npm",
    [
      "@semantic-release/git",
      {
        "assets": [
          "dist",
          "bin",
          "package.json",
          "package-lock.json",
          "CHANGELOG.md"
        ],
        "message": "chore(release): ${nextRelease.version} [skip ci]\n\n${nextRelease.notes}"
      }
    ]
  ]
}

/* eslint-env node */
const types = [
  {
    value: 'feat',
    name: 'feat: A new feature',
  },
  {
    value: 'fix',
    name: 'fix: A bug fix',
  },
  {
    value: 'docs',
    name: 'docs: Documentation only changes',
  },
  {
    value: 'style',
    name: 'style: Changes that do not affect the meaning of the code (white-space, formatting, missing semi-colons, etc)',
  },
  {
    value: 'refactor',
    name: 'refactor: A code change that neither fixes a bug nor adds a feature',
  },
  {
    value: 'perf',
    name: 'perf: A code change that improves performance',
  },
  {
    value: 'test',
    name: 'test: Adding missing tests',
  },
  {
    value: 'chore',
    name: 'chore: Changes to the build process or auxiliary tools and libraries such as documentation generation',
  },
  {
    value: 'revert',
    name: 'revert: Revert to a commit',
  },
]

const scopes = ['core', 'cli', 'htmlhint'].map((name) => ({ name }))

module.exports = {
  types,
  scopes,
  allowCustomScopes: true,
  allowBreakingChanges: ['feat', 'fix', 'perf', 'refactor'],
}

/* eslint-env node */
const { types, scopes, allowCustomScopes } = require('./commitizen.config')

const validTypes = types.map((type) => type.value)
const validScopes = scopes.map((scope) => scope.name)
const scopeValidationLevel = allowCustomScopes ? 1 : 2

module.exports = {
  extends: ['@commitlint/config-conventional'],
  parserPreset: './parser-preset',

  // Add your own rules. See https://marionebl.github.io/commitlint
  rules: {
    // Apply valid scopes and types
    'scope-enum': [scopeValidationLevel, 'always', validScopes],
    'type-enum': [2, 'always', validTypes],
  },
}

module.exports = {
  coverageDirectory: 'coverage',
  coverageProvider: 'v8',
  coverageReporters: ['json', 'text'],
  testTimeout: 30000,
}

