module.exports = {
  "extends": [
    "@commitlint/config-conventional"
  ]
}

module.exports = {
  '*.md,!test/**/*.md': [
    (filenames) => filenames.map((filename) => `remark ${filename} -qfo`)
  ],
  './package.json': 'fixpack',
  '*.js': 'xo --fix',
  '*.ts': 'xo --fix'
};

module.exports = {
  "singleQuote": true,
  "bracketSpacing": true,
  "trailingComma": "none"
}

module.exports = {
  plugins: ['preset-github']
};

module.exports = {
  prettier: true,
  space: true,
  extends: ['xo-lass'],
  ignore: ['config.js'],
  rules: {
    'capitalized-comments': 'off',
    'unicorn/catch-error-name': 'off',
    'unicorn/require-post-message-target-origin': 'off',
    'unicorn/prefer-node-protocol': 'warn',
    'unicorn/prefer-top-level-await': 'warn',
    'unicorn/prefer-event-target': 'off',
    'unicorn/no-empty-file': 'warn',
    'unicorn/no-process-exit': 'warn',
    'unicorn/prefer-logical-operator-over-ternary': 'warn'
  },
  overrides: [
    {
      files: '**/*.mjs',
      parserOptions: {
        sourceType: 'module'
      }
    },
    {
      files: 'test/jobs/*.js',
      rules: {
        'unicorn/no-process-exit': 'off'
      }
    },
    {
      files: ['**/*.d.ts'],
      rules: {
        'no-unused-vars': 'off',
        '@typescript-eslint/naming-convention': 'off',
        'no-redeclare': 'off',
        '@typescript-eslint/no-redeclare': 'off'
      }
    },
    {
      files: ['**/*.test-d.ts'],
      rules: {
        '@typescript-eslint/no-unsafe-call': 'off',
        '@typescript-eslint/no-confusing-void-expression': 'off', // Conflicts with `expectError` assertion.
        '@typescript-eslint/no-unsafe-assignment': 'off'
      }
    }
  ]
};

module.exports = {
  files: [
    'test/*.js',
    'test/**/*.js',
    '!test/jobs',
    '!test/noIndexJobs',
    '!test/issues/**/jobs',
    '!test/issues/**/jobs-no-default-export'
  ],
  verbose: true,
  require: ['events.once/polyfill']
};

// eslint-disable-next-line no-undef
docute.init({
  debug: true,
  title: 'Bree',
  repo: 'breejs/bree',
  'edit-link': 'https://github.com/breejs/bree/tree/master/',
  twitter: 'niftylettuce',
  nav: {
    default: [
      {
        title: 'The best job scheduler for Node.js and JavaScript',
        path: '/'
      },
      {
        title: 'Upgrading',
        path: '/UPGRADING'
      }
    ]
  },
  // eslint-disable-next-line no-undef
  plugins: [docuteEmojify()]
});

module.exports = {
  extension: ['.js'],
  'report-dir': './coverage',
  'temp-dir': './.nyc_output',
  reporter: ['lcov', 'html', 'text'],
  'check-coverage': true,
  lines: 100,
  functions: 100,
  branches: 100
};
