  // = private methods =
  // ===================

  const postProcess = (msg, namedValues, args, count) => {
    // test for parsable interval string
    if (/\|/.test(msg)) {
      msg = parsePluralInterval(msg, count)
    }

    // replace the counter
    if (typeof count === 'number') {
