{
  "parser": "babel-eslint",
  "extends": "airbnb",
  "rules": {
    "prefer-template": 0,
    "new-cap": 0,
    "no-use-before-define": 0,
    "arrow-body-style": 0,
    // triple equals is required except for when comparing with null
    "eqeqeq": [2, "allow-null"],
    // Allow console methods, which get removed during build process
    "no-console": 0,
    // Disable `arrow-parens` because it conflicts with Flow.
    "arrow-parens": 0,
    "global-require": 0,
    "react/jsx-filename-extension": 0,
    "no-underscore-dangle": 0,
    "import/no-extraneous-dependencies": 0,
    "react/no-unused-prop-types": 0,
    "react/require-default-props": 0
  }
}

module.exports = {
  // Control the interface on which the local server listens (defaults to 'localhost')
  // (default: 'localhost')
  // bind: '0.0.0.0',

  // Control the port used for the local server
  // (default: 4567)
  // port: 7777,

  // List javascript source files. These can be files or raw URLs.
  // (default: [])
  sourceFiles: [
    'example/web/dist/happo.bundle.js',
  ],

  // List css source files. These can also be files or raw URLs.
  // (default: [])
  stylesheets: [
    // 'application.css',
  ],

  // List directories where public files are accessible (useful for e.g. font files)
  // (default: [])
  publicDirectories: [
    'example',
  ],

  // Specify the folder where snapshots are saved
  // (default: 'snapshots')
  // snapshotsFolder: 'happo-snapshots',

  // Configure the window size when taking snapshots
  // (defaults shown below)
  viewports: {
    small: {
      width: 375,
      height: 667,
    },
  },
};

var webpack = require('webpack');
var path = require('path');

module.exports = [{
  entry: {
    example: './example/web/example.js',
    happo: './example/web/happo.js',
    pythagoras: './example/web/pythagoras.js',
  },
  mode: 'development',
  output: {
    path: path.resolve(__dirname, 'example', 'web', 'dist'),
    filename: '[name].bundle.js',
  },
  module: {
    rules: [
      {
        test: /\.(js|jsx)$/,
        exclude: /node_modules/,
        loader: 'babel-loader',
      },
    ],
  },
  plugins: [
    new webpack.DefinePlugin({
      'process.env': {
        NODE_ENV: JSON.stringify('development'),
      },
    }),
  ],
}, {
  entry: {
    perf: './example/web/perf.js',
  },
  mode: 'production',
  output: {
    path: path.resolve(__dirname, 'example', 'web', 'dist'),
    filename: '[name].bundle.js',
  },
  module: {
    rules: [
      {
        test: /\.(js|jsx)$/,
        exclude: /node_modules/,
        loader: 'babel-loader',
      },
    ],
  },
  plugins: [
    new webpack.DefinePlugin({
      'process.env': {
        NODE_ENV: JSON.stringify('production'),
      },
    }),
  ],
}];
