        "block-spacing": [
            "error"
        ],
        "brace-style": [
            "error"
        ],
        "camelcase": [
            "error"
        ],
        "comma-spacing": [
            "error"
        ],
        "comma-style": [
            "error"
        ],
        "computed-property-spacing": [
            "error"
        ],
        "consistent-this": [
            "error",
            "self"
