        "func-call-spacing": [
            "error"
        ],
        "func-name-matching": [
            "error"
        ],
        "key-spacing": [
            "error",
            {
                "mode": "minimum"
            }
        ],
        "keyword-spacing": [
            "error"
        ],
        "max-len": [
            "error",
            80
        ],
        "max-statements-per-line": [
            "error",
            {
                "max": 1
            }
        ],
        "new-cap": [
            "error"
        ],
        "new-parens": [
            "error"
        ],
