        ],
        "operator-assignment": [
            "error"
        ],
        "operator-linebreak": [
            "error",
            "after"
        ],
        "quote-props": [
            "error",
            "as-needed"
        ],
        "semi-spacing": [
            "error"
        ],
        "space-before-blocks": [
            "error"
        ],
        "space-before-function-paren": [
            "error",
            "never"
        ],
        "space-in-parens": [
            "error",
            "never"
        ],
        "space-infix-ops": [
            "error"
        ],
        "space-unary-ops": [
            "error",
            {
                "words": false,
                "nonwords": false
            }
        ],
        "unicode-bom": [
            "error"
        ],
        "no-caller": [
            "error"
        ],
        "no-loop-func": [
            "error"
        ]
    },
    "globals": {
        "OpenSeadragon": "writable",
        "define": "readonly",
        "module": "readonly",
        "Map": "readonly"
    }
}

