import { defineConfig } from 'cypress'

export default defineConfig({
  fixturesFolder: false,
  env: {
    backend_url: 'http://localhost:8080',
  },
  e2e: {
    setupNodeEvents(on, config) {},
    baseUrl: 'http://localhost:3000',
    supportFile: false,
  },
})

'use strict'

// Have you identified a contributing guideline that should be included here?
// Please open a pull request!
//
// To test changes to this file, pick a PR to test against, then run
// `./node_modules/.bin/danger pr pr-url`
// Note that the line numbers in the runtime errors are incorrect.

// To test changes locally:
// DANGER_GITHUB_API_TOKEN=your-github-api-token npm run danger -- pr https://github.com/badges/shields/pull/2665

const { danger, fail, message, warn } = require('danger')
const { fileMatch } = danger.git

const documentation = fileMatch(
  '**/*.md',
  'frontend/docs/**',
  'frontend/src/**',
)
const server = fileMatch('core/server/**.js', '!*.spec.js')
const serverTests = fileMatch('core/server/**.spec.js')
const legacyHelpers = fileMatch('lib/**/*.js', '!*.spec.js')
const legacyHelperTests = fileMatch('lib/**/*.spec.js')
const packageJson = fileMatch('package.json')
const packageLock = fileMatch('package-lock.json')
const secretsDocs = fileMatch('doc/server-secrets.md')
const capitals = fileMatch('**/*[A-Z]*.js')
const underscores = fileMatch('**/*_*.js')

message(
  [
    ':sparkles: Thanks for your contribution to Shields, ',
    `@${danger.github.pr.user.login}!`,
  ].join(''),
)

const targetBranch = danger.github.pr.base.ref
if (targetBranch !== 'master') {
  const message = `This PR targets \`${targetBranch}\``
  const idea = 'It is likely that the target branch should be `master`'
  warn(`${message} - <i>${idea}</i>`)
}

if (documentation.edited) {
  message(
    [
      'Thanks for contributing to our documentation. ',
      'We :heart: our [documentarians](http://www.writethedocs.org/)!',
    ].join(''),
  )
}

if (packageJson.modified && !packageLock.modified) {
  const message = 'This PR modified `package.json`, but not `package-lock.json`'
  const idea = 'Perhaps you need to run `npm install`?'
  warn(`${message} - <i>${idea}</i>`)
}

if (server.modified && !serverTests.modified) {
  warn(
    [
      'This PR modified the server but none of its tests. <br>',
      "That's okay so long as it's refactoring existing code.",
    ].join(''),
  )
}

if (legacyHelpers.created) {
  warn(['This PR added helper modules in `lib/` which is deprecated.'].join(''))
} else if (legacyHelpers.edited && !legacyHelperTests.edited) {
  warn(
    [
      'This PR modified helper functions in `lib/` but not accompanying tests. <br>',
      "That's okay so long as it's refactoring existing code.",
    ].join(''),
  )
}

if (capitals.created || underscores.created) {
  fail(
    [
      'JavaScript source files should be named with `kebab-case` ',
      '(dash-separated lowercase).',
    ].join(''),
  )
}

const allFiles = danger.git.created_files.concat(danger.git.modified_files)

if (allFiles.length > 100) {
  warn("Lots 'o changes. Skipping diff-based checks.")
} else {
  allFiles.forEach(file => {
    if (file === 'dangerfile.js') {
      return
    }

    // eslint-disable-next-line promise/prefer-await-to-then
    danger.git.diffForFile(file).then(({ diff }) => {
      if (diff.includes('authHelper') && !secretsDocs.modified) {
        warn(
          [
            ':books: Remember to ensure any changes to `config.private` ',
            `in \`${file}\` are reflected in the [server secrets documentation]`,
            '(https://github.com/badges/shields/blob/master/doc/server-secrets.md)',
          ].join(''),
        )
      }

      if (diff.includes('.assert(')) {
        warn(
          [
            `Found 'assert' statement added in \`${file}\`. <br>`,
            'Please ensure tests are written using Chai ',
            '[expect syntax](http://chaijs.com/guide/styles/#expect)',
          ].join(''),
        )
      }

      if (diff.includes("from '@hapi/joi'")) {
        fail(
          [
            `Found import of '@hapi/joi' in \`${file}\`. <br>`,
            "Joi must be imported as 'joi'.",
          ].join(''),
        )
      }
    })
  })
}

function onlyUnique(value, index, self) {
  return self.indexOf(value) === index
}

const affectedServices = allFiles
  .map(file => {
    const match = file.match(/^services\/(.+)\/.+\.service.js$/)
    return match ? match[1] : undefined
  })
  .filter(Boolean)
  .filter(onlyUnique)

const testedServices = allFiles
  .map(file => {
    const match = file.match(/^services\/(.+)\/.+\.tester.js$/)
    return match ? match[1] : undefined
  })
  .filter(Boolean)
  .filter(onlyUnique)

affectedServices.forEach(service => {
  if (testedServices.indexOf(service) === -1) {
    warn(
      [
        `This PR modified service code for <kbd>${service}</kbd> but not its test code. <br>`,
        "That's okay so long as it's refactoring existing code.",
      ].join(''),
    )
  }
})

import { expect } from 'chai'
import isSvg from 'is-svg'
import got from './core/got-test-client.js'

let serverModule
before(async function () {
  this.timeout('30s')
  // remove args coming from mocha
  // https://github.com/badges/shields/issues/3365
  process.argv = []
  serverModule = await import('./server.js')
})

after('shut down the server', async function () {
  await serverModule.server.stop()
})

it('should render a badge', async function () {
  this.timeout('30s')
  const { statusCode, body } = await got(
    'http://localhost:1111/badge/fruit-apple-green.svg',
  )
  expect(statusCode).to.equal(200)
  expect(body).to.satisfy(isSvg).and.to.include('fruit').and.to.include('apple')
})

import chaiFriendlyPlugin from 'eslint-plugin-chai-friendly'
import cypressPlugin from 'eslint-plugin-cypress/flat'
import jsdocPlugin from 'eslint-plugin-jsdoc'
import mochaPlugin from 'eslint-plugin-mocha'
import icedfrisbyPlugin from 'eslint-plugin-icedfrisby'
import sortClassMembersPlugin from 'eslint-plugin-sort-class-members'
import importPlugin from 'eslint-plugin-import'
import reactHooksPlugin from 'eslint-plugin-react-hooks'
import prettierConfig from 'eslint-plugin-prettier/recommended'
import promisePlugin from 'eslint-plugin-promise'
import globals from 'globals'
import neostandard from 'neostandard'
import tsParser from '@typescript-eslint/parser'
import js from '@eslint/js'

// Config that is used across the whole codebase
// and customisations to built-in ESLint rules
const globalConfig = {
  plugins: {
    import: importPlugin,
    promise: promisePlugin,
  },

  rules: {
    'import/order': ['error', { 'newlines-between': 'never' }],
    'promise/prefer-await-to-then': 'error',

    // ESLint built-in rules config
    'no-empty': ['error', { allowEmptyCatch: true }],
    'no-var': 'error',
    'prefer-const': 'error',
    'arrow-body-style': ['error', 'as-needed'],
    'object-shorthand': ['error', 'properties'],
    'prefer-template': 'error',
    'func-style': ['error', 'declaration', { allowArrowFunctions: true }],
    'new-cap': ['error', { capIsNew: true }],
    quotes: [
      'error',
      'single',
      { avoidEscape: true, allowTemplateLiterals: false },
    ],
    camelcase: [
      'error',
      {
        ignoreDestructuring: true,
        properties: 'never',
        ignoreGlobals: true,
        allow: ['^UNSAFE_'],
      },
    ],
  },
}

// config specific to linting Node (CommonJS) files
const commonJsConfig = {
  files: ['badge-maker/**/*.js', '**/*.cjs'],

  languageOptions: {
    globals: {
      ...globals.node,
    },
  },
}

// config specific to linting Node (ESModules) files
const nodeEsmConfig = {
  files: ['**/*.@(js|mjs)', '!frontend/**/*.js', '!badge-maker/**/*.js'],

  languageOptions: {
    globals: {
      ...globals.node,
    },
    parser: tsParser,
    sourceType: 'module',
  },

  rules: {
    'no-console': 'off',
  },
}

// config specific to linting Frontend (ESModules) files
const frontendConfig = {
  files: ['frontend/**/*.js'],

  plugins: {
    'react-hooks': reactHooksPlugin,
  },

  languageOptions: {
    globals: {
      ...globals.browser,
    },
    sourceType: 'module',
  },

  rules: {
    'react-hooks/rules-of-hooks': 'error',
    'react-hooks/exhaustive-deps': 'error',
  },
}

// config specific to linting Services
const servicesConfig = {
  files: ['core/base-service/**/*.js', 'services/**/*.js'],

  plugins: {
    'sort-class-members': sortClassMembersPlugin,
  },

  rules: {
    'sort-class-members/sort-class-members': [
      'error',
      {
        order: [
          'name',
          'category',
          'isDeprecated',
          'route',
          'auth',
          'openApi',
          '_cacheLength',
          'defaultBadgeData',
          'render',
          'constructor',
          'fetch',
          'transform',
          'handle',
        ],
      },
    ],
  },
}

// config specific to linting Mocha tests
const mochaConfig = {
  files: [
    '**/*.spec.@(js|mjs|ts)',
    '**/*.integration.js',
    '**/test-helpers.js',
    'core/service-test-runner/**/*.js',
  ],

  plugins: {
    mocha: mochaPlugin,
  },

  languageOptions: {
    globals: {
      ...globals.mocha,
    },
  },

  rules: {
    'mocha/no-exclusive-tests': 'error',
    'mocha/no-skipped-tests': 'error',
    'mocha/no-mocha-arrows': 'error',
    'mocha/prefer-arrow-callback': 'error',
    'no-unused-expressions': 'off',
  },
}

// config specific to linting Cypress tests
const cypressConfig = {
  files: ['**/*.cy.@(js|ts)'],
  ...cypressPlugin.configs.recommended,
}
// append these to cypress.configs.recommended, without overwriting
cypressConfig.plugins.mocha = mochaPlugin
cypressConfig.rules['mocha/no-exclusive-tests'] = 'error'
cypressConfig.rules['mocha/no-skipped-tests'] = 'error'
cypressConfig.rules['mocha/no-mocha-arrows'] = 'off'

// config specific to linting Service tests (IcedFrisby)
const serviceTestsConfig = {
  files: ['services/**/*.tester.js'],

  plugins: {
    icedfrisby: icedfrisbyPlugin,
  },

  rules: {
    'icedfrisby/no-exclusive-tests': 'error',
    'icedfrisby/no-skipped-tests': 'error',
    'no-unused-expressions': 'off',
  },
}

// config specific to linting JSDoc comments
const jsDocConfig = {
  plugins: {
    jsdoc: jsdocPlugin,
  },

  rules: {
    'jsdoc/require-jsdoc': 'off',
    'jsdoc/no-undefined-types': ['error', { definedTypes: ['Joi'] }],
    'jsdoc/check-alignment': 'error',
    'jsdoc/check-param-names': 'error',
    'jsdoc/check-tag-names': 'error',
    'jsdoc/check-types': 'error',
    'jsdoc/implements-on-classes': 'error',
    'jsdoc/tag-lines': ['error', 'any', { startLines: 1 }],
    'jsdoc/require-param': 'error',
    'jsdoc/require-param-description': 'error',
    'jsdoc/require-param-name': 'error',
    'jsdoc/require-param-type': 'error',
    'jsdoc/require-returns': 'error',
    'jsdoc/require-returns-check': 'error',
    'jsdoc/require-returns-description': 'error',
    'jsdoc/require-returns-type': 'error',
    'jsdoc/valid-types': 'error',
  },
}

const config = [
  {
    ignores: [
      'api-docs/',
      'build',
      'coverage',
      '__snapshots__',
      'public',
      'badge-maker/node_modules/',
      '!.github/',
      'frontend/.docusaurus/**',
    ],
  },

  js.configs.recommended,
  chaiFriendlyPlugin.configs.recommendedFlat,
  ...neostandard({ noStyle: true }),

  globalConfig,
  commonJsConfig,
  nodeEsmConfig,
  frontendConfig,
  servicesConfig,
  mochaConfig,
  cypressConfig,
  serviceTestsConfig,
  jsDocConfig,

  // register prettierConfig last, as per
  // https://github.com/prettier/eslint-plugin-prettier?tab=readme-ov-file#configuration-new-eslintconfigjs
  prettierConfig,
]

export default config

