const babelPresetConfig = () => ({
  targets: {
    chrome: '110',
    firefox: '110',
    safari: '14.1',
    node: '11', // support for Webpack 4 and similar oldish bundlers
  },
  modules: false,
  debug: false,
  useBuiltIns: 'usage',
  corejs: {
    version: '3.37'
  }
});

module.exports = {
  presets: [
    ['@babel/preset-env', babelPresetConfig()]
  ],
  plugins: [
    ['transform-inline-environment-variables'],
    '@babel/plugin-syntax-jsx'
  ]
};

const moment = require('moment');

module.exports = {
  HOT_FILENAME: 'handsontable',
  HOT_VERSION: '15.0.0',
  HOT_PACKAGE_NAME: 'handsontable',
  HOT_BUILD_DATE: moment().format('DD/MM/YYYY HH:mm:ss'),
  HOT_RELEASE_DATE: '16/12/2024',
};

