import vm from "node:vm";
import url from "node:url";
import fs from "node:fs";
import path from "node:path";
import crypto from "node:crypto";
import perf_hooks from "node:perf_hooks";

const __dirname = url.fileURLToPath(new URL(".", import.meta.url));

let files = [
    "src/const.js",
    "src/config.js",
    "src/log.js",
    "src/cpu.js",
    "src/debug.js",
    "src/io.js",
    "src/main.js",
    "src/lib.js",
    "src/buffer.js",
    "src/ide.js",
    "src/pci.js",
    "src/floppy.js",
    "src/memory.js",
    "src/dma.js",
    "src/pit.js",
    "src/vga.js",
    "src/ps2.js",

    "src/rtc.js",
    "src/uart.js",

    "src/acpi.js",
    "src/apic.js",
    "src/ioapic.js",

    "src/state.js",
    "src/ne2k.js",
    "src/sb16.js",
    "src/virtio.js",
    "src/virtio_console.js",
    "src/virtio_net.js",
    //"src/virtio_balloon.js",
    "src/bus.js",

    "src/debug.js",
    "src/elf.js",
    "src/kernel.js",

    "lib/9p.js",
    "lib/filesystem.js",
    "lib/jor1k.js",
    "lib/marshall.js",

    "src/browser/screen.js",
    "src/browser/keyboard.js",
    "src/browser/mouse.js",
    "src/browser/speaker.js",
    "src/browser/serial.js",
    "src/browser/network.js",
    "src/browser/fake_network.js",
    "src/browser/fetch_network.js",
    "src/browser/starter.js",
    "src/browser/worker_bus.js",
    "src/browser/dummy_screen.js",
    "src/browser/print_stats.js",
    "src/browser/filestorage.js"
];


let globals = Object.create(globalThis);
let v86 = {};

let ctx = vm.createContext(globals);
globals.DEBUG = false;
globals.module = {exports:v86};
Object.defineProperty(globals, "crypto", {value: crypto});
globals.require = (what) => {
    return ({
        perf_hooks,
        fs
    })[what];
};

for( let f of files ) {
    vm.runInContext(fs.readFileSync(path.join(__dirname, f), "utf8"), ctx, {
        filename: f
    });
}

export let {
    V86,
    CPU,
    FetchNetworkAdapter,
    MemoryFileStorage,
    ServerFileStorageWrapper,
} = globals;

#log, #runtime_infos, #serial, #filesystem_panel, #debug_panel {
    font-family: DejaVu Sans Mono, monospace;
    font-size: 13px;
    border: 1px solid #333;
    background-color: #000;
    padding: 4px;
    color: #fff;
}
#screen_container:fullscreen {
    display: flex !important;
    align-items: center;
    justify-content: center;
}
#screen_container:fullscreen #vga {
    width: auto !important;
    height: 100% !important;
}
#runtime_infos, #filesystem_panel {
    float: left;
    width: 250px;
    margin-bottom: 7px;
    margin-right: 7px;
    padding-bottom: 7px;
}
#log {
    height: 100px;
    width: 550px;
}
#debug_panel {
    white-space: pre;
    float: left;
}
#serial {
    margin: 0;
}
#serial:focus {
    outline: 1px solid rgb(229, 151, 0);
}
#screen {
    white-space: pre;
    position: relative;
    font-family: Liberation Mono, DejaVu Sans Mono, Courier New, monospace;
    font-weight: bold;
    font-size: 15px;
    line-height: normal;
}
#screen, #vga {
    -webkit-transform-origin: top left;
    -moz-transform-origin: top left;
    transform-origin: top left;
}
#vga {
    background-color: #000;
    touch-action: none;
}
body {
    background-color: #111;
    color: #fff;
    line-height: 1.5;
    padding: 10px;
    font-family: sans-serif;
}
a {
    color: wheat;
    text-decoration: none;
    cursor: pointer;
}
.phone_keyboard {
    width: 0;
    height: 0;
    resize: none;
    position: absolute;
    opacity: 0;
    left: -9999em;
    top: 0;
    z-index: -10;
    white-space: nowrap;
    overflow: hidden;
}
h4 {
    margin: 0 0 9px 0;
}
#start_emulation {
    padding: 6px 18px;
    font-size: 16px;
    font-weight: bold;
}
#setup_error {
    color: red;
    font-weight: bold;
    background-color: #222;
    padding: 8px 18px;
}
#config_link {
    font-weight: bold;
    background-color: #222;
    padding: 8px 18px;
}
#screen_container {
    float: left;
    margin-right: 10px;
    margin-bottom: 10px;
    outline: 1px solid #555;
}
#boot_options a {
    text-decoration: none;
    font-weight: bold;
    font-size: 16px;
}
#boot_options td, #boot_options th {
    padding: 1px 7px;
}

#oses tbody tr {
    cursor: pointer;
}
#oses {
    border-spacing: 0;
}
#oses tbody tr:hover {
    background-color: #311;
}
#oses td:nth-child(2) {
    text-align: right;
}
#oses thead {
    text-align: left;
}

/* This is the best I managed to do with my little css experience.
   If you can do better, please send a PR. */
@media (max-width: 1250px) {
    #oses td:nth-child(9), #oses th:nth-child(9) {
        display: none;
    }
}
@media (max-width: 1150px) {
    #oses td:nth-child(8), #oses td:nth-child(7), #oses th:nth-child(8), #oses th:nth-child(7) {
        display: none;
    }
}
@media (max-width: 1050px) {
    #oses td:nth-child(5), #oses td:nth-child(6), #oses th:nth-child(5), #oses th:nth-child(6) {
        display: none;
    }
}
@media (max-width: 850px) {
    #oses td:nth-child(4), #oses th:nth-child(4) {
        display: none;
    }
}
@media (max-width: 750px) {
    #oses th:nth-child(2), #oses th:nth-child(3), #oses th:nth-child(10) {
        display: none;
    }
    #oses td:nth-child(1), #oses td:nth-child(2), #oses td:nth-child(3) {
        display: inline;
    }
    #oses td:nth-child(2), td:nth-child(3) {
        font-size: smaller;
    }
    #oses td:nth-child(10) {
        display: block;
        padding-bottom: 10px;
    }
}

label {
    user-select: none;
    white-space: nowrap;
}
input[type=checkbox] {
    vertical-align: middle;
    position: relative;
    bottom: 1px;
    margin: 0;
}
#filter label {
    background-color: #444;
    padding: 2px 4px;
    border-radius: 4px;
}

#terminal {
    max-width: 1024px;
}
.blink {
    animation: blink 0.6s step-start infinite;
}
@keyframes blink {
    50% {
        color: transparent;
    }
}
.blinking-cursor {
    animation: blinking-cursor 0.6s step-start infinite;
}
@keyframes blinking-cursor {
    50% {
        background-color: transparent;
    }
}

